import { useState, useEffect } from "react";
import "./AdminSettings.css";

export default function Settings() {
  const [systemName, setSystemName] = useState("PDF-OCR System");
  const [maintenance, setMaintenance] = useState(false);
  const [fadeIn, setFadeIn] = useState(false);
  const [undoVisible, setUndoVisible] = useState(false);
  const [lastChange, setLastChange] = useState({});
  const [modalVisible, setModalVisible] = useState(false);
  const [collapsedGeneral, setCollapsedGeneral] = useState(false);
  const [collapsedSecurity, setCollapsedSecurity] = useState(false);

  useEffect(() => setFadeIn(true), []);

  const handleSave = (e) => {
    e.preventDefault();
    setLastChange({ systemName, maintenance });
    setUndoVisible(true);
    setTimeout(() => setUndoVisible(false), 5000);
  };

  const handleResetField = (field) => {
    if (field === "systemName") setSystemName("PDF-OCR System");
    if (field === "maintenance") setMaintenance(false);
  };

  const handleResetAll = () => {
    setSystemName("PDF-OCR System");
    setMaintenance(false);
    setModalVisible(false);
    setUndoVisible(true);
    setLastChange({ systemName, maintenance });
    setTimeout(() => setUndoVisible(false), 5000);
  };

  const handleUndo = () => {
    setSystemName(lastChange.systemName);
    setMaintenance(lastChange.maintenance);
    setUndoVisible(false);
  };

  return (
    <div className={`settings-page ${fadeIn ? "fade-in" : ""}`}>
      <h2 className="settings-title">Admin Settings</h2>

      {/* Undo Toast */}
      {undoVisible && (
        <div className="toast">
          <span>Changes applied!</span>
          <button className="toast-btn" onClick={handleUndo}>Undo</button>
        </div>
      )}

      <form className="settings-form" onSubmit={handleSave}>
        {/* Collapsible General Section */}
        <div className="collapsible-header" onClick={() => setCollapsedGeneral(!collapsedGeneral)}>
          <h3>General Settings</h3>
          <span>{collapsedGeneral ? "+" : "-"}</span>
        </div>
        {!collapsedGeneral && (
          <div className="collapsible-content">
            <div className="form-group">
              <label className="form-label">
                System Name <span className="info-tooltip" title="Name displayed across the system interface.">ℹ️</span>
              </label>
              <input
                type="text"
                className="form-input"
                value={systemName}
                onChange={(e) => setSystemName(e.target.value)}
                placeholder="Enter system name"
              />
              <div className="form-actions">
                <button type="button" className="btn-reset" onClick={() => handleResetField("systemName")}>Reset</button>
              </div>
            </div>
          </div>
        )}

        {/* Collapsible Security Section */}
        <div className="collapsible-header" onClick={() => setCollapsedSecurity(!collapsedSecurity)}>
          <h3>Security Settings</h3>
          <span>{collapsedSecurity ? "+" : "-"}</span>
        </div>
        {!collapsedSecurity && (
          <div className="collapsible-content">
            <div className="form-group">
              <label className="form-label">
                Maintenance Mode <span className="info-tooltip" title="Enable to temporarily restrict user access.">ℹ️</span>
              </label>
              <div className="toggle-switch">
                <input
                  type="checkbox"
                  id="maintenance"
                  checked={maintenance}
                  onChange={() => setMaintenance(!maintenance)}
                />
                <label htmlFor="maintenance" className="switch-label">
                  {maintenance ? "On" : "Off"}
                </label>
              </div>
              <div className="form-actions">
                <button type="button" className="btn-reset" onClick={() => handleResetField("maintenance")}>Reset</button>
              </div>
            </div>
          </div>
        )}

        {/* Buttons */}
        <div className="form-actions">
          <button type="submit" className="btn-save">Save Changes</button>
          <button type="button" className="btn-reset" onClick={() => setModalVisible(true)}>Reset All</button>
        </div>
      </form>

      {/* Modal */}
      {modalVisible && (
        <div className="modal-overlay">
          <div className="modal">
            <h3>Confirm Reset</h3>
            <p>Are you sure you want to reset all settings?</p>
            <div className="form-actions">
              <button className="btn-save" onClick={handleResetAll}>Yes, Reset</button>
              <button className="btn-reset" onClick={() => setModalVisible(false)}>Cancel</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
