import React, { useState } from 'react';
import axios from 'axios';

const OCRUpload = () => {
  const [file, setFile] = useState(null);
  const [metadataOnly, setMetadataOnly] = useState([]);
  const [loading, setLoading] = useState(false);
  const [downloadReady, setDownloadReady] = useState(false);
  const [structuredData, setStructuredData] = useState(null);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
    setMetadataOnly([]);
    setDownloadReady(false);
    setStructuredData(null);
  };

  const handleUpload = async () => {
    if (!file) return;

    const formData = new FormData();
    formData.append('file', file);

    try {
      setLoading(true);
      const res = await axios.post('http://localhost:5000/api/ocr', formData);
      const data = res.data.structured_data;
      setStructuredData(data);
      setMetadataOnly(data["Metadata"] || []);
      setDownloadReady(true);
    } catch (err) {
      console.error('Error uploading file:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleDownloadPDF = async () => {
    try {
      const res = await axios.post(
        'http://localhost:5000/api/export-pdf',
        { structured_data: structuredData },
        { responseType: 'blob' }
      );

      const url = window.URL.createObjectURL(new Blob([res.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', 'ocr_output.pdf');
      document.body.appendChild(link);
      link.click();
    } catch (err) {
      console.error('Error downloading PDF:', err);
    }
  };

  return (
    <div style={{ padding: '2rem', fontFamily: 'sans-serif' }}>
      <h2>üìÑ PDF OCR Metadata Extractor</h2>
      <input type="file" onChange={handleFileChange} />
      <button onClick={handleUpload} style={{ marginLeft: '1rem' }}>
        Upload
      </button>

      {loading && (
        <p style={{ color: 'orange', marginTop: '1rem' }}>‚è≥ Processing...</p>
      )}

      {metadataOnly.length > 0 && (
        <div style={{ marginTop: '2rem' }}>
          <h3>üìã Extracted Metadata</h3>
          <table border="1" cellPadding="10" style={{ width: '100%', borderCollapse: 'collapse' }}>
            <thead>
              <tr style={{ backgroundColor: '#f0f0f0' }}>
                <th>Field</th>
                <th>Value</th>
              </tr>
            </thead>
            <tbody>
              {metadataOnly.map((line, i) => {
                const [key, ...rest] = line.split(":");
                const value = rest.join(":").trim();
                return (
                  <tr key={i}>
                    <td><strong>{key.trim()}</strong></td>
                    <td>{value || '[Not provided]'}</td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </div>
      )}

      {!loading && structuredData && metadataOnly.length === 0 && (
        <p style={{ marginTop: '1rem', color: 'gray' }}>No metadata found in this file.</p>
      )}

      {downloadReady && (
        <button onClick={handleDownloadPDF} style={{ marginTop: '2rem' }}>
          ‚¨áÔ∏è Download PDF
        </button>
      )}
    </div>
  );
};

export default OCRUpload;
