import React from 'react';

const MetadataTable = ({ metadataList }) => {
  if (!metadataList || metadataList.length === 0) return null;

  const parsedRows = metadataList.map((line, index) => {
    const [key, ...rest] = line.split(":");
    const value = rest.join(":").trim();
    return { key: key.trim(), value };
  });

  return (
    <div className="my-6 bg-white shadow-lg rounded-md overflow-hidden border border-gray-200">
      <h2 className="text-lg font-semibold p-4 bg-gray-100 border-b">Patient / Procedure Details</h2>
      <table className="w-full text-sm text-left">
        <thead className="bg-gray-200 text-gray-700">
          <tr>
            <th className="px-4 py-2 border-r w-1/3">Field</th>
            <th className="px-4 py-2">Value</th>
          </tr>
        </thead>
        <tbody>
          {parsedRows.map((row, index) => (
            <tr key={index} className="border-t hover:bg-gray-50">
              <td className="px-4 py-2 font-medium text-gray-800">{row.key}</td>
              <td className="px-4 py-2 text-gray-700">{row.value || '[Not provided]'}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default MetadataTable;
