// src/components/Signup.jsx
import React, { useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const API_BASE = process.env.REACT_APP_API_BASE || "http://localhost:5000";

export default function Signup({ onSignupSuccess }) {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("user"); // default role
  const [message, setMessage] = useState("");

  const handleSignup = async (e) => {
    e.preventDefault();
    setMessage("");
    try {
      const res = await axios.post(`${API_BASE}/auth/signup`, {
        username,
        password,
        role,
      });
      setMessage(res.data.message || "Signup successful");
      if (onSignupSuccess) onSignupSuccess();
    } catch (err) {
      console.error("Signup error:", err);
      setMessage(err.response?.data?.error || "Signup failed");
    }
  };

  return (
    <div className="glass-container" style={{marginTop: "50px"}}>
      <h2>Sign up</h2>
      {message && (
        <div className={`message ${message.toLowerCase().includes('failed') ? 'error' : 'success'}`}>
          {message}
        </div>
      )}
      <form onSubmit={handleSignup}>
        <input
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          placeholder="Username"
          required
          className="glass-input glass-username"
        />
        <input
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          type="password"
          placeholder="Password"
          required
          className="glass-input"
        />
        <select
          value={role}
          onChange={(e) => setRole(e.target.value)}
          className="glass-select"
        >
          <option value="user">User</option>
          <option value="admin">Admin</option>
          <option value="viewer">Viewer</option>
        </select>
        <button type="submit" className="glass-btn">Sign up</button>
      </form>
      <p>
        Already have an account? <Link to="/login">Login</Link>
      </p>
    </div>
  );
}
