import React, { useState } from 'react';
import axios from 'axios';

const OCRUpload = () => {
  const [file, setFile] = useState(null);
  const [metadata, setMetadata] = useState({});
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [downloadReady, setDownloadReady] = useState(false);

  const handleFileChange = (e) => {
    const selected = e.target.files[0];
    if (selected && selected.type === "application/pdf") {
      setFile(selected);
      setMetadata({});
      setDownloadReady(false);
      setError("");
    } else {
      setError("Please select a valid PDF file.");
      setFile(null);
    }
  };

  const handleUpload = async () => {
    if (!file) return;

    const formData = new FormData();
    formData.append('file', file);

    setLoading(true);
    setError("");
    try {
      const res = await axios.post("http://localhost:5000/api/ocr", formData);
      const extracted = res.data.metadata || {};
      setMetadata(extracted);
      setDownloadReady(Object.keys(extracted).length > 0);
    } catch (err) {
      setError("OCR failed. Please check the backend logs.");
      console.error("OCR failed", err);
    } finally {
      setLoading(false);
    }
  };

  const handleDownloadPDF = async () => {
    try {
      const res = await axios.post(
        "http://localhost:5000/api/export-pdf",
        { metadata },
        { responseType: "blob" }
      );
      const url = URL.createObjectURL(new Blob([res.data]));
      const link = document.createElement("a");
      link.href = url;
      link.setAttribute("download", "metadata_output.pdf");
      document.body.appendChild(link);
      link.click();
    } catch (err) {
      setError("Failed to download PDF.");
    }
  };

  return (
    <div style={{ padding: "2rem", fontFamily: "Arial, sans-serif" }}>
      <h2>📄 PDF to Extract Patient Metadata</h2>
      <input type="file" accept=".pdf" onChange={handleFileChange} />
      <button onClick={handleUpload} disabled={!file || loading} style={{ marginLeft: "1rem" }}>
        {loading ? "Processing..." : "Upload"}
      </button>

      {error && <p style={{ color: "red", marginTop: "1rem" }}>{error}</p>}

      {!loading && Object.keys(metadata).length > 0 && (
        <div style={{ marginTop: "2rem" }}>
          <h3>📋 Extracted Metadata</h3>
          <table border="1" cellPadding="10" style={{ borderCollapse: "collapse", width: "100%" }}>
            <thead>
              <tr style={{ backgroundColor: "#f0f0f0" }}>
                <th>Field</th>
                <th>Value</th>
              </tr>
            </thead>
            <tbody>
              {Object.entries(metadata).map(([key, value], i) => (
                <tr key={i}>
                  <td><strong>{key}</strong></td>
                  <td>{value}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {!loading && file && Object.keys(metadata).length === 0 && downloadReady === false && (
        <p style={{ marginTop: "1rem", color: "gray" }}>No metadata found in this file.</p>
      )}

      {downloadReady && (
        <button onClick={handleDownloadPDF} style={{ marginTop: "1.5rem" }}>
          ⬇️ Download PDF
        </button>
      )}
    </div>
  );
};

export default OCRUpload;
