import React from 'react';

const MetadataTable = ({ metadata }) => {
  if (!metadata || Object.keys(metadata).length === 0) return null;

  return (
    <div className="my-6 bg-white shadow-lg rounded-md overflow-hidden border border-gray-200">
      <h2 className="text-lg font-semibold p-4 bg-gray-100 border-b">
        ðŸ§¾ Extracted Patient / Procedure Details
      </h2>
      <table className="w-full text-sm text-left">
        <thead className="bg-gray-200 text-gray-700">
          <tr>
            <th className="px-4 py-2 border-r w-1/3">Field</th>
            <th className="px-4 py-2">Value</th>
          </tr>
        </thead>
        <tbody>
          {Object.entries(metadata).map(([section, fields], sectionIndex) => (
            <React.Fragment key={sectionIndex}>
              <tr className="bg-gray-100 border-t">
                <td colSpan={2} className="px-4 py-2 font-bold text-blue-700">{section}</td>
              </tr>

              {Array.isArray(fields) ? (
                fields.map((item, idx) => (
                  <tr key={`${sectionIndex}-${idx}`} className="border-t hover:bg-gray-50">
                    <td className="px-4 py-2 text-gray-800">
                      {item["Full Name"] || `Contact ${idx + 1}`}
                    </td>
                    <td className="px-4 py-2 text-gray-700">
                      {Object.entries(item)
                        .filter(([k]) => k !== "Full Name")
                        .map(([k, v]) => `${k}: ${v}`)
                        .join(", ")}
                    </td>
                  </tr>
                ))
              ) : (
                Object.entries(fields).map(([key, value], fieldIndex) => (
                  <tr key={`${sectionIndex}-${fieldIndex}`} className="border-t hover:bg-gray-50">
                    <td className="px-4 py-2 font-medium text-gray-800">{key}</td>
                    <td className="px-4 py-2 text-gray-700">{value || "-"}</td>
                  </tr>
                ))
              )}
            </React.Fragment>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default MetadataTable;
