import { useState, useEffect } from "react";
import "./AdminSettings.css";

export default function Settings() {
  const [systemName, setSystemName] = useState("PDF-OCR System");
  const [maintenance, setMaintenance] = useState(false);
  const [fadeIn, setFadeIn] = useState(false);
  const [toast, setToast] = useState("");
  const [unsaved, setUnsaved] = useState(false);

  useEffect(() => {
    setFadeIn(true);
  }, []);

  const handleSave = (e) => {
    e.preventDefault();
    if (!systemName.trim()) {
      setToast("System name cannot be empty!");
      return;
    }
    setToast("Settings saved successfully!");
    setUnsaved(false);
    setTimeout(() => setToast(""), 3000);
  };

  const handleReset = () => {
    setSystemName("PDF-OCR System");
    setMaintenance(false);
    setUnsaved(false);
    setToast("Settings reset to defaults");
    setTimeout(() => setToast(""), 3000);
  };

  const handleSystemNameChange = (e) => {
    setSystemName(e.target.value);
    setUnsaved(true);
  };

  const toggleMaintenance = () => {
    setMaintenance(!maintenance);
    setUnsaved(true);
  };

  return (
    <div className={`settings-page ${fadeIn ? "fade-in" : ""}`}>
      <h2 className="settings-title">Admin Settings</h2>

      {toast && <div className="toast">{toast}</div>}
      <p className={`save-status ${unsaved ? "unsaved" : "saved"}`}>
        {unsaved ? "Unsaved changes" : "All changes saved"}
      </p>

      <form className="settings-form" onSubmit={handleSave}>
        {/* System Name */}
        <div className="form-group fade-section">
          <label className="form-label">System Name</label>
          <input
            type="text"
            className="form-input"
            value={systemName}
            onChange={handleSystemNameChange}
            placeholder="Enter system name"
          />
          <p className="form-helper">Name displayed across the system interface.</p>
          <p className="form-preview">Current system display: <strong>{systemName}</strong></p>
        </div>

        {/* Maintenance Mode */}
        <div className="form-group fade-section">
          <label className="form-label">Maintenance Mode</label>
          <div className="toggle-switch">
            <input
              type="checkbox"
              id="maintenance"
              checked={maintenance}
              onChange={toggleMaintenance}
            />
            <label htmlFor="maintenance" className="switch-label"></label>
          </div>
          <p className="form-helper">Enable to temporarily restrict user access.</p>
        </div>

        {/* Theme Selection */}
        <div className="form-group fade-section">
          <label className="form-label">Theme</label>
          <select
            className="form-input"
            onChange={() => setUnsaved(true)}
            defaultValue="Light"
          >
            <option>Light</option>
            <option>Dark</option>
          </select>
        </div>

        {/* Buttons */}
        <div className="form-actions fade-section">
          <button type="submit" className="btn-save">
            Save Changes
          </button>
          <button type="button" className="btn-reset" onClick={handleReset}>
            Reset to Defaults
          </button>
        </div>
      </form>
    </div>
  );
}
