import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../App.css';

const OCRUpload = ({ auth, setAuth, isAdmin = false }) => {
  const navigate = useNavigate();
  const [files, setFiles] = useState([]);
  const [tables, setTables] = useState([]);
  const [rawTexts, setRawTexts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [downloadReady, setDownloadReady] = useState(false);

  const [isSignup, setIsSignup] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');

  const API_BASE = process.env.REACT_APP_API_BASE || 'http://localhost:5000';
  const api = axios.create({ baseURL: API_BASE });

  // Attach token
  api.interceptors.request.use((config) => {
    if (auth?.token) config.headers.Authorization = `Bearer ${auth.token}`;
    return config;
  });

  // Handle 401/403
  api.interceptors.response.use(
    (response) => response,
    (error) => {
      if ([401, 403].includes(error.response?.status)) {
        localStorage.clear();
        setAuth(null);
        alert('⚠️ Session expired or access denied. Please log in again.');
      }
      return Promise.reject(error);
    }
  );

  useEffect(() => {
    if (auth?.token && !isAdmin) {
      navigate('/', { replace: true });
    }
  }, [auth, isAdmin, navigate]);

  // -------- Auth Flows --------
  const handleSignup = async () => {
    try {
      const res = await api.post('/auth/signup', { username, password });
      setMessage(res.data.message || 'Signup successful!');
      const loginRes = await api.post('/auth/login', { username, password });
      setAuth({
        token: loginRes.data.token,
        username: loginRes.data.username,
        role: loginRes.data.role,
      });
      localStorage.setItem('auth', JSON.stringify({
        token: loginRes.data.token,
        username: loginRes.data.username,
        role: loginRes.data.role,
      }));
    } catch (err) {
      setMessage('Signup failed: ' + (err.response?.data?.error || err.message));
    }
  };

  const handleLogin = async () => {
    try {
      const res = await api.post('/auth/login', { username, password });
      if (res.data.token) {
        setAuth({
          token: res.data.token,
          username: res.data.username,
          role: res.data.role,
        });
        localStorage.setItem('auth', JSON.stringify({
          token: res.data.token,
          username: res.data.username,
          role: res.data.role,
        }));
        setMessage('Login successful!');
      } else {
        setMessage('Invalid credentials');
      }
    } catch (err) {
      setMessage('Login failed: ' + (err.response?.data?.error || err.message));
    }
  };

  // -------- File Selection --------
  const handleFileChange = (e) => {
    const selected = Array.from(e.target.files || []);
    if (selected.length > 10) {
      alert('⚠️ You can upload up to 10 files only.');
      return;
    }
    setFiles(selected);
    setTables([]);
    setRawTexts([]);
    setDownloadReady(false);
  };

  // -------- Upload + Parse --------
  const handleUploadAll = async () => {
    if (!files.length) return;
    setLoading(true);

    try {
      if (auth?.role?.toLowerCase() === 'admin') {
        try { await api.post('/api/reset'); } catch {}
      }

      const outTables = [];
      const texts = [];

      for (const [idx, file] of files.entries()) {
        const formData = new FormData();
        formData.append('file', file);

        const res = await api.post('/api/ocr', formData, {
          headers: { 'Content-Type': 'multipart/form-data' },
        });

        console.log('OCR response for', file.name, res.data);

        const report = res.data.report || null;
        const fallbackText =
          res.data.report_text ||
          res.data.report_text_head ||
          res.data.text ||
          '';

        outTables[idx] = (report && Array.isArray(report.columns) && Array.isArray(report.rows))
          ? report
          : { columns: [], rows: [] };

        texts[idx] = typeof fallbackText === 'string' ? fallbackText : '';
      }

      setTables(outTables);
      setRawTexts(texts);
      setDownloadReady(true);
    } catch (err) {
      console.error('Upload failed:', err);
      alert('⚠️ Upload failed. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  // -------- Fixed Headers --------
  const fixedHeaders = [
    "Patient Name","Pat Ocr","Responsible Party Name","Rep #","Doc",
    "B-Type","Seen","Service","Type","Office","Code","Description",
    "Amount","Balance","Claim","Loc","Entered","Who"
  ];

  // -------- Table Renderer --------
  const ExcelGrid = ({ title, rows }) => {
    const totalAmount = rows.reduce((sum, r) => sum + (parseFloat(r?.Amount) || 0), 0);
    const totalBalance = rows.reduce((sum, r) => sum + (parseFloat(r?.Balance) || 0), 0);
    const totalReceipts = rows.reduce((sum, r) => sum + (parseFloat(r?.Receipts) || 0), 0);

    return (
      <div className="excel-grid-wrapper">
        <div className="excel-grid-header">
          <h4 className="file-title">{title}</h4>
        </div>
        <div className="excel-grid compact">
          <div className="excel-grid-scroll">
            <table className="excel-table">
              <thead>
                <tr>
                  {fixedHeaders.map((col) => (
                    <th key={col} className="excel-th">{col}</th>
                  ))}
                </tr>
              </thead>
              <tbody>
                {rows && rows.length > 0 ? (
                  rows.map((r, i) => (
                    <tr key={i}>
                      {fixedHeaders.map((col) => (
                        <td key={col} className="excel-td">{r?.[col] ?? ''}</td>
                      ))}
                    </tr>
                  ))
                ) : (
                  <tr>
                    <td colSpan={fixedHeaders.length} style={{ textAlign: 'center' }}>
                      No rows
                    </td>
                  </tr>
                )}
                {/* Summary Row */}
                <tr className="summary-row">
                  <td colSpan={fixedHeaders.length - 3} style={{ textAlign: 'right', fontWeight: 'bold' }}>
                    Charges: {totalAmount.toFixed(2)} | Receipts: {totalReceipts.toFixed(2)} | Totals: {(totalAmount - totalReceipts).toFixed(2)}
                  </td>
                  <td className="excel-td" colSpan={3} style={{ fontWeight: 'bold' }}>
                    Balance: {totalBalance.toFixed(2)}
                  </td>
                </tr>
              </tbody>
            </table>
          </div>
        </div>
      </div>
    );
  };

  // -------- Auth Gate --------
  if (!auth?.token && !isAdmin) {
    return (
      <div className="container">
        <h2>{isSignup ? 'Signup' : 'Login'}</h2>
        <input type="text" placeholder="Username" value={username} onChange={(e) => setUsername(e.target.value)} className="auth-input" />
        <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} className="auth-input" />
        {isSignup ? (
          <button className="auth-btn" onClick={handleSignup}>Signup</button>
        ) : (
          <button className="auth-btn" onClick={handleLogin}>Login</button>
        )}
        <button className="toggle-btn" onClick={() => { setIsSignup(!isSignup); setMessage(''); }}>
          {isSignup ? 'Already have an account? Login' : 'No account? Signup'}
        </button>
        {message && <p className="auth-message">{message}</p>}
      </div>
    );
  }

  // -------- UI --------
  return (
    <div className="container">
      <header>
        <h2 className="main-heading">
          {isAdmin ? '📂 Admin OCR Upload Tool' : '📄 File OCR Extraction Tool'}
        </h2>
      </header>

      <section className="upload-section">
        <label htmlFor="file-upload" className="upload-label">Upload up to 10 files:</label>
        <input type="file" id="file-upload" accept=".pdf,.jpg,.jpeg,.png,.doc,.docx" multiple onChange={handleFileChange} />
        <div className="button-row">
          <button className="upload-btn" onClick={handleUploadAll}>Upload All</button>
          {downloadReady && (
            <button className="download-btn" onClick={() => alert('Excel export handled on server.')}>
              ⬇️ Download Excel
            </button>
          )}
        </div>
      </section>

      {files.length > 0 && (
        <section className="file-preview">
          <h3 className="section-heading">📁 Selected Files</h3>
          <ul className="file-list">
            {files.map((file, i) => (<li key={i}>📎 {file.name}</li>))}
          </ul>
        </section>
      )}

      {loading && <p className="loading-text">⏳ Extracting table...</p>}

      {tables.length > 0 && (
        <section className="results-section">
          <h3 className="section-heading">📊 Procedure Report</h3>
          {files.map((file, idx) => (
            <ExcelGrid
              key={`grid-${idx}`}
              title={file.name}
              rows={tables[idx]?.rows || []}
            />
          ))}
        </section>
      )}
    </div>
  );
};

export default OCRUpload;
