// src/components/OCRUpload.jsx
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../App.css';

function HorizontalMetaTable({ meta }) {
  if (!meta) return null;
  const keys = Object.keys(meta || {});
  if (!keys.length) return null;
  return (
    <div className="metadata-horizontal">
      <table className="metadata-table">
        <thead>
          <tr>{keys.map(k => <th key={k}>{k}</th>)}</tr>
        </thead>
        <tbody>
          <tr>{keys.map(k => <td key={k}>{String(meta[k] ?? '')}</td>)}</tr>
        </tbody>
      </table>
    </div>
  );
}

function PatientBlock({ patient }) {
  const keys = Object.keys(patient || {});
  return (
    <div className="patient-block">
      <div className="patient-header">
        <h4>Patient: {patient["Patient Name"] ?? "Unknown"}</h4>
      </div>
      <table className="metadata-vertical">
        <tbody>
          {keys.map(k => (
            <tr key={k}>
              <th className="meta-key">{k}</th>
              <td className="meta-val">{String(patient[k] ?? '')}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

function ProcedureTables({ tables }) {
  if (!tables || Object.keys(tables).length === 0) {
    return <div className="empty">No procedure tables found.</div>;
  }
  return (
    <div>
      <h3>Procedure / Billing Tables</h3>
      {Object.entries(tables).map(([section, rows]) => (
        <div key={section} className="table-block">
          <h4>{section}</h4>
          <table className="excel-table">
            <thead>
              <tr><th>Code</th><th>Description</th><th>Fee</th></tr>
            </thead>
            <tbody>
              {rows && rows.length > 0 ? rows.map((r, i) => (
                <tr key={i}>
                  <td>{r[0]}</td>
                  <td>{r[1]}</td>
                  <td>{r[2]}</td>
                </tr>
              )) : (
                <tr><td colSpan={3} style={{ textAlign: 'center' }}>No rows</td></tr>
              )}
            </tbody>
          </table>
        </div>
      ))}
    </div>
  );
}

function DiagnosisTables({ tables }) {
  if (!tables || Object.keys(tables).length === 0) {
    return <div className="empty">No diagnosis tables found.</div>;
  }
  return (
    <div>
      <h3>Diagnosis Tables</h3>
      {Object.entries(tables).map(([section, rows]) => (
        <div key={section} className="table-block">
          <h4>{section}</h4>
          <table className="excel-table">
            <thead>
              <tr><th>Type</th><th>Code</th><th>Diagnosis</th></tr>
            </thead>
            <tbody>
              {rows && rows.length > 0 ? rows.map((r, i) => (
                <tr key={i}>
                  <td>{r[0]}</td>
                  <td>{r[1]}</td>
                  <td>{r[2]}</td>
                </tr>
              )) : (
                <tr><td colSpan={3} style={{ textAlign: 'center' }}>No rows</td></tr>
              )}
            </tbody>
          </table>
        </div>
      ))}
    </div>
  );
}

function ResultBlock({ item }) {
  // item expected: { file_name, patients, procedure_tables, diagnosis_tables, ... }
  const patients = item.patients || [];
  const procedures = item.procedure_tables || {};
  const diagnosis = item.diagnosis_tables || {};

  return (
    <div className="result-block">
      <h2 className="file-title">{item.file_name || item.filename || "uploaded file"}</h2>
      <div className="summary">{patients.length} patient(s) ‚Ä¢ {Object.values(procedures).reduce((s, r) => s + (r?.length || 0), 0)} procedure rows ‚Ä¢ {Object.values(diagnosis).reduce((s, r) => s + (r?.length || 0), 0)} diagnosis rows</div>

      {/* Patients */}
      <section>
        <h3>Patients</h3>
        {patients.length === 0 ? <div className="empty">No patients found</div> : patients.map((p, i) => <PatientBlock key={i} patient={p} />)}
      </section>

      {/* Procedure / Billing tables (global) */}
      <section style={{ marginTop: 18 }}>
        <ProcedureTables tables={procedures} />
      </section>

      {/* Diagnosis tables */}
      <section style={{ marginTop: 18 }}>
        <DiagnosisTables tables={diagnosis} />
      </section>

      <details style={{ marginTop: 12 }}>
        <summary>Raw response (debug)</summary>
        <pre style={{ maxHeight: 240, overflow: 'auto', background: '#0b1220', color: '#cbd5e1', padding: 8 }}>
          {JSON.stringify(item, null, 2)}
        </pre>
      </details>
    </div>
  );
}

const OCRUpload = ({ auth, setAuth, isAdmin = false }) => {
  const navigate = useNavigate();
  const [files, setFiles] = useState([]);
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);

  const [isSignup, setIsSignup] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');

  const API_BASE = process.env.REACT_APP_API_BASE || 'http://localhost:5000';
  const api = axios.create({ baseURL: API_BASE });

  api.interceptors.request.use((config) => {
    if (auth?.token) config.headers.Authorization = `Bearer ${auth.token}`;
    return config;
  });
  api.interceptors.response.use(
    (response) => response,
    (error) => {
      if ([401, 403].includes(error.response?.status)) {
        localStorage.clear();
        setAuth(null);
        alert('‚ö†Ô∏è Session expired or access denied. Please log in again.');
      }
      return Promise.reject(error);
    }
  );

  useEffect(() => {
    if (auth?.token && !isAdmin) {
      navigate('/', { replace: true });
    }
  }, [auth, isAdmin, navigate]);

  const handleSignup = async () => {
    try {
      const res = await api.post('/auth/signup', { username, password });
      setMessage(res.data.message || 'Signup successful!');
      const loginRes = await api.post('/auth/login', { username, password });
      const { token, username: un, role } = loginRes.data;
      setAuth({ token, username: un, role });
      localStorage.setItem('auth', JSON.stringify({ token, username: un, role }));
    } catch (err) {
      setMessage('Signup failed: ' + (err.response?.data?.error || err.message));
    }
  };

  const handleLogin = async () => {
    try {
      const res = await api.post('/auth/login', { username, password });
      if (res.data.token) {
        const { token, username: un, role } = res.data;
        setAuth({ token, username: un, role });
        localStorage.setItem('auth', JSON.stringify({ token, username: un, role }));
        setMessage('Login successful!');
      } else {
        setMessage('Invalid credentials');
      }
    } catch (err) {
      setMessage('Login failed: ' + (err.response?.data?.error || err.message));
    }
  };

  const handleFileChange = (e) => {
    const selected = Array.from(e.target.files || []);
    if (selected.length > 10) {
      alert('‚ö†Ô∏è You can upload up to 10 files only.');
      return;
    }
    setFiles(selected);
    setResults([]);
  };

  const buildItemFromResponse = (resData, file) => {
    // Normalize shapes so front-end always sees consistent keys
    if (!resData) {
      return { file_name: file.name, patients: [], procedure_tables: {}, diagnosis_tables: {}, metadata: {} };
    }
    // New canonical shape returned by backend (preferred)
    if (resData.patients || resData.procedure_tables || resData.diagnosis_tables) {
      return {
        file_name: resData.file_name || resData.filename || file.name,
        patients: Array.isArray(resData.patients) ? resData.patients : [],
        procedure_tables: (resData.procedure_tables && typeof resData.procedure_tables === 'object') ? resData.procedure_tables : {},
        diagnosis_tables: (resData.diagnosis_tables && typeof resData.diagnosis_tables === 'object') ? resData.diagnosis_tables : {},
        metadata: resData.metadata || {}
      };
    }

    // older shapes (medical_billing_codes, diagnosis_codes)
    if (resData.medical_billing_codes || resData.diagnosis_codes) {
      // put medical_billing_codes into a single Procedures section
      const procedures = { "Procedures": Array.isArray(resData.medical_billing_codes) ? resData.medical_billing_codes.map(r => [r.Code || r.code || "", r.Description || r.description || "", r.Fee || r.fee || ""]) : [] };
      const diags = { "Diagnosis": Array.isArray(resData.diagnosis_codes) ? resData.diagnosis_codes.map(d => [d.Type || d.type || "ICD-10", d.Code || d.code || "", d.Diagnosis || d.diagnosis || d.Description || d.description || ""]) : [] };
      return {
        file_name: resData.file_name || resData.filename || file.name,
        patients: Array.isArray(resData.patients) ? resData.patients : [],
        procedure_tables: procedures,
        diagnosis_tables: diags,
        metadata: resData.metadata || {}
      };
    }

    // fallback: wrap metadata
    const meta = resData.metadata || resData;
    if (!meta['File Name']) meta['File Name'] = file.name;
    return {
      file_name: meta['File Name'],
      patients: [],
      procedure_tables: {},
      diagnosis_tables: {},
      metadata: meta
    };
  };

  const handleUploadAll = async () => {
    if (!files.length) return;
    setLoading(true);
    try {
      const out = [];
      for (const file of files) {
        const formData = new FormData();
        formData.append('file', file);
        const res = await api.post('/api/ocr', formData, {
          headers: { 'Content-Type': 'multipart/form-data' },
        });
        const item = buildItemFromResponse(res.data || {}, file);
        out.push(item);
      }
      setResults(out);
    } catch (err) {
      console.error('Upload failed:', err);
      alert('‚ö†Ô∏è Upload failed. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  if (!auth?.token && !isAdmin) {
    return (
      <div className="container">
        <h2>{isSignup ? 'Signup' : 'Login'}</h2>
        <input type="text" placeholder="Username" value={username} onChange={(e) => setUsername(e.target.value)} className="auth-input" />
        <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} className="auth-input" />
        {isSignup ? (
          <button className="auth-btn" onClick={handleSignup}>Signup</button>
        ) : (
          <button className="auth-btn" onClick={handleLogin}>Login</button>
        )}
        <button className="toggle-btn" onClick={() => { setIsSignup(!isSignup); setMessage(''); }}>
          {isSignup ? 'Already have an account? Login' : 'No account? Signup'}
        </button>
        {message && <p className="auth-message">{message}</p>}
      </div>
    );
  }

  return (
    <div className="container">
      <header><h2 className="main-heading">{isAdmin ? 'üìÇ Admin OCR Upload Tool' : 'üìÑ File OCR Extraction Tool'}</h2></header>

      <section className="upload-section">
        <label htmlFor="file-upload" className="upload-label">Upload up to 10 files:</label>
        <input type="file" id="file-upload" accept=".pdf,.jpg,.jpeg,.png,.doc,.docx" multiple onChange={handleFileChange} />
        <div className="button-row">
          <button className="upload-btn" onClick={handleUploadAll} disabled={loading}>{loading ? 'Processing...' : 'Upload All'}</button>
        </div>
      </section>

      {files.length > 0 && (<section className="file-preview"><h3 className="section-heading">üìÅ Selected Files</h3><ul className="file-list">{files.map((file, i) => <li key={i}>üìé {file.name}</li>)}</ul></section>)}

      {loading && <p className="loading-text">‚è≥ Extracting tables...</p>}

      {results.length > 0 && (
        <section className="results-section">
          <h3 className="section-heading">üìä Parsed Tables</h3>
          {results.map((item, idx) => <ResultBlock key={idx} item={item} />)}
        </section>
      )}

    </div>
  );
};

export default OCRUpload;
