import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../App.css';

const OCRUpload = ({ auth, setAuth }) => {
  const [files, setFiles] = useState([]);
  const [allMetadata, setAllMetadata] = useState([]);
  const [loading, setLoading] = useState(false);
  const [downloadReady, setDownloadReady] = useState(false);

  // Auth states
  const [isSignup, setIsSignup] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');

  // Redirect if not logged in (disabled because we show login form here)
  useEffect(() => {
    // If auth is null, stay here and show login form
  }, [auth]);

  // Handle signup
  const handleSignup = async () => {
    try {
      const res = await axios.post('http://localhost:5000/signup', {
        username,
        password
      });
      setMessage(res.data.message);
      setIsSignup(false);
    } catch (err) {
      setMessage('Signup failed: ' + (err.response?.data?.message || err.message));
    }
  };

  // Handle login
  const handleLogin = async () => {
    try {
      const res = await axios.post('http://localhost:5000/login', {
        username,
        password
      });
      if (res.data.success) {
        setAuth({ token: res.data.token, username: res.data.username });
        localStorage.setItem('auth', JSON.stringify({ token: res.data.token, username: res.data.username }));
        setMessage('Login successful!');
      } else {
        setMessage('Invalid credentials');
      }
    } catch (err) {
      setMessage('Login failed: ' + (err.response?.data?.message || err.message));
    }
  };

  // File selection
  const handleFileChange = (e) => {
    const selected = Array.from(e.target.files);
    if (selected.length > 10) {
      alert('‚ö†Ô∏è You can upload up to 10 files only.');
      return;
    }
    setFiles(selected);
    setAllMetadata([]);
    setDownloadReady(false);
  };

  // Upload all files for OCR
  const handleUploadAll = async () => {
    if (!files.length) return;
    setLoading(true);

    try {
      await axios.get('http://localhost:5000/api/reset', {
        headers: { Authorization: `Bearer ${auth.token}` }
      });

      setAllMetadata([]);
      const all = [];

      for (const file of files) {
        const formData = new FormData();
        formData.append('file', file);

        const res = await axios.post('http://localhost:5000/api/ocr', formData, {
          headers: { Authorization: `Bearer ${auth.token}` }
        });

        const meta = res.data.metadata || {};
        meta['File Name'] = file.name;
        meta['Patient Signature'] = res.data.patient_signature;
        meta['Physician Signature'] = res.data.physician_signature;

        all.push(meta);

        await axios.post('http://localhost:5000/api/export-excel', { metadata: meta }, {
          headers: { Authorization: `Bearer ${auth.token}` }
        });
      }

      setAllMetadata(all);
      setDownloadReady(true);
    } catch (err) {
      console.error('Upload failed:', err);
      if (err.response?.status === 401) {
        alert('Session expired. Please log in again.');
        localStorage.clear();
        setAuth(null);
      } else {
        alert('Upload failed. Please try again.');
      }
    } finally {
      setLoading(false);
    }
  };

  // Download Excel
  const handleDownloadExcel = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/export-excel', {
        headers: { Authorization: `Bearer ${auth.token}` },
        responseType: 'blob'
      });
      const url = URL.createObjectURL(new Blob([res.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', 'metadata_output.xlsx');
      document.body.appendChild(link);
      link.click();
      link.remove();
    } catch (err) {
      console.error('Excel download failed:', err);
      alert('Download failed. Please try again.');
    }
  };

  // If not logged in ‚Üí show login/signup UI
  if (!auth?.token) {
    return (
      <div className="container">
        <h2>{isSignup ? 'Signup' : 'Login'}</h2>
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          className="auth-input"
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          className="auth-input"
        />
        {isSignup ? (
          <button className="auth-btn" onClick={handleSignup}>Signup</button>
        ) : (
          <button className="auth-btn" onClick={handleLogin}>Login</button>
        )}
        <button
          className="toggle-btn"
          onClick={() => setIsSignup(!isSignup)}
        >
          {isSignup ? 'Already have an account? Login' : 'No account? Signup'}
        </button>
        {message && <p className="auth-message">{message}</p>}
      </div>
    );
  }

  // If logged in ‚Üí show OCR upload UI
  return (
    <div className="container">
      <header>
        <h2 className="main-heading">üìÑ File OCR Extraction Tool</h2>
        <button
          onClick={() => { localStorage.clear(); setAuth(null); }}
          className="logout-btn"
        >
          Logout
        </button>
      </header>

      <section className="upload-section">
        <label htmlFor="file-upload" className="upload-label">
          Upload up to 10 files:
        </label>
        <input
          type="file"
          id="file-upload"
          accept=".pdf,.jpg,.jpeg,.png,.doc,.docx"
          multiple
          onChange={handleFileChange}
        />
        <div className="button-row">
          <button className="upload-btn" onClick={handleUploadAll}>Upload All</button>
          {downloadReady && (
            <button className="download-btn" onClick={handleDownloadExcel}>
              ‚¨áÔ∏è Download Excel
            </button>
          )}
        </div>
      </section>

      {files.length > 0 && (
        <section className="file-preview">
          <h3 className="section-heading">üìÅ Selected Files</h3>
          <ul className="file-list">
            {files.map((file, index) => (
              <li key={index}>üìé {file.name}</li>
            ))}
          </ul>
        </section>
      )}

      {loading && <p className="loading-text">‚è≥ Extracting metadata...</p>}

      {allMetadata.length > 0 && (
        <section className="results-section">
          <h3 className="section-heading">üìã Extracted Metadata</h3>
          {allMetadata.map((meta, fileIndex) => (
            <div key={fileIndex} className="table-container">
              <h4 className="file-title">{meta['File Name']}</h4>
              <table className="metadata-table">
                <thead>
                  <tr>
                    <th>Field</th>
                    <th>Value</th>
                  </tr>
                </thead>
                <tbody>
                  {Object.entries(meta).map(([key, value], i) => (
                    <tr key={i}>
                      <td className="field-cell">{key}</td>
                      <td className="value-cell">
                        {(key === 'Patient Signature' || key === 'Physician Signature') && value?.includes('/signatures/')
                          ? <img src={`http://localhost:5000${value}`} alt={key} className="signature-img" />
                          : value
                        }
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ))}
        </section>
      )}
    </div>
  );
};

export default OCRUpload;
