import React, { useState } from 'react';
import axios from 'axios';

const OCRUpload = () => {
  const [files, setFiles] = useState([]);
  const [allMetadata, setAllMetadata] = useState([]);
  const [loading, setLoading] = useState(false);
  const [downloadReady, setDownloadReady] = useState(false);

  const handleFileChange = (e) => {
    const selected = Array.from(e.target.files);
    if (selected.length > 10) {
      alert('⚠️ You can upload up to 10 PDF files only.');
      return;
    }
    setFiles(selected);
    setAllMetadata([]);
    setDownloadReady(false);
  };

  const handleUploadAll = async () => {
    if (!files || files.length === 0) return;

    setLoading(true);
    try {
      const all = [];

      for (const file of files) {
        const formData = new FormData();
        formData.append('file', file);

        const res = await axios.post('http://localhost:5000/api/ocr', formData);
        const meta = res.data.metadata || {};
        meta['File Name'] = file.name;
        meta['Patient Signature'] = res.data.patient_signature;
        meta['Physician Signature'] = res.data.physician_signature;

        all.push(meta);

        // Send to backend to prepare for Excel
        await axios.post('http://localhost:5000/api/export-excel', { metadata: meta });
      }

      setAllMetadata(all);
      setDownloadReady(true);
    } catch (err) {
      console.error('Upload failed:', err);
      alert('Upload failed. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleDownloadExcel = async () => {
    const res = await axios.post('http://localhost:5000/api/export-excel', {}, {
      responseType: 'blob'
    });
    const url = URL.createObjectURL(new Blob([res.data]));
    const link = document.createElement('a');
    link.href = url;
    link.setAttribute('download', 'metadata_output.xlsx');
    document.body.appendChild(link);
    link.click();
  };

  return (
    <div style={{ padding: '2rem', fontFamily: 'Arial, sans-serif' }}>
      <h2>📄 Upload Multiple PDFs to Extract Metadata</h2>
      <input type="file" accept=".pdf" multiple onChange={handleFileChange} />
      <button onClick={handleUploadAll} style={{ marginLeft: '1rem' }}>
        Upload All
      </button>

      {files.length > 0 && (
        <ul style={{ marginTop: '1rem' }}>
          {files.map((file, index) => (
            <li key={index}>{file.name}</li>
          ))}
        </ul>
      )}

      {loading && <p>⏳ Extracting...</p>}

      {allMetadata.length > 0 && (
        <div style={{ marginTop: '2rem' }}>
          <h3>📋 Extracted Metadata</h3>
          {allMetadata.map((meta, fileIndex) => (
            <div key={fileIndex} style={{ marginBottom: '2rem' }}>
              <h4>{meta['File Name']}</h4>

              <table border="1" cellPadding="10" style={{ borderCollapse: 'collapse', width: '100%' }}>
                <thead>
                  <tr>
                    <th><strong>Field</strong></th>
                    <th><strong>Value</strong></th>
                  </tr>
                </thead>
                <tbody>
                  {Object.entries(meta).map(([key, value], i) => (
                    <tr key={i}>
                      <td><strong>{key}</strong></td>
                      <td>
                        {(key === 'Patient Signature' || key === 'Physician Signature') && value && typeof value === 'string' && value.includes('/signatures/')
                          ? <img
                              src={`http://localhost:5000${value}`}
                              alt={key}
                              style={{ maxWidth: '300px', border: '1px solid #ccc', padding: '4px' }}
                            />
                          : value
                        }
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ))}
        </div>
      )}

      {downloadReady && (
        <button onClick={handleDownloadExcel} style={{ marginTop: '1.5rem' }}>
          ⬇️ Download Excel
        </button>
      )}
    </div>
  );
};

export default OCRUpload;
