import React, { useState } from 'react';
import axios from 'axios';

const OCRUpload = () => {
  const [file, setFile] = useState(null);
  const [structuredData, setStructuredData] = useState(null);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
    setStructuredData(null);
  };

  const handleUpload = async () => {
    if (!file) return;

    const formData = new FormData();
    formData.append('file', file);

    try {
      const res = await axios.post('http://localhost:5000/api/ocr', formData);
      setStructuredData(res.data.structured_data);
    } catch (err) {
      console.error('Error uploading file:', err);
    }
  };

  return (
    <div style={{ padding: '2rem', fontFamily: 'sans-serif' }}>
      <h2>ðŸ“„ PDF OCR Extractor</h2>
      <input type="file" onChange={handleFileChange} />
      <button onClick={handleUpload} style={{ marginLeft: '1rem' }}>Upload</button>

      {structuredData && Object.entries(structuredData).map(([section, rows]) => (
        <div key={section} style={{ marginTop: '2rem' }}>
          <h3>{section}</h3>
          <table border="1" cellPadding="10" style={{ width: '100%', borderCollapse: 'collapse' }}>
            <thead>
              <tr>
                <th>Code</th>
                <th>Description</th>
              </tr>
            </thead>
            <tbody>
              {rows.map((row, i) => (
                <tr key={i}>
                  <td>{row.code}</td>
                  <td>{row.description}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ))}
    </div>
  );
};

export default OCRUpload;
