import React, { useState } from 'react';
import axios from 'axios';

const OCRUpload = () => {
  const [file, setFile] = useState(null);
  const [structuredData, setStructuredData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [downloadReady, setDownloadReady] = useState(false);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
    setStructuredData(null);
    setDownloadReady(false);
  };

  const handleUpload = async () => {
    if (!file) return;

    const formData = new FormData();
    formData.append('file', file);

    try {
      setLoading(true);
      const res = await axios.post('http://localhost:5000/api/ocr', formData);
      setStructuredData(res.data.structured_data);
      setDownloadReady(true);
    } catch (err) {
      console.error('Error uploading file:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleDownloadPDF = async () => {
    try {
      const res = await axios.post(
        'http://localhost:5000/api/export-pdf',
        { structured_data: structuredData },
        { responseType: 'blob' }
      );

      const url = window.URL.createObjectURL(new Blob([res.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', 'ocr_output.pdf');
      document.body.appendChild(link);
      link.click();
    } catch (err) {
      console.error('Error downloading PDF:', err);
    }
  };

  const renderSection = (section, content) => {
    if (Array.isArray(content) && content.every((item) => typeof item === 'string')) {
      return (
        <div key={section}>
          <h3 style={{ marginTop: '1.5rem' }}>{section}</h3>
          <table border="1" cellPadding="10" style={{ width: '100%', borderCollapse: 'collapse' }}>
            <thead>
              <tr style={{ backgroundColor: '#f0f0f0' }}>
                <th>Field</th>
                <th>Value</th>
              </tr>
            </thead>
            <tbody>
              {content.map((line, i) => {
                const [key, ...rest] = line.split(":");
                const value = rest.join(":").trim();
                return (
                  <tr key={i}>
                    <td><strong>{key.trim()}</strong></td>
                    <td>{value || '[Not provided]'}</td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </div>
      );
    }

    if (Array.isArray(content) && content.every((item) => typeof item === 'object' && 'code' in item)) {
      return (
        <div key={section}>
          <h3 style={{ marginTop: '1.5rem' }}>{section}</h3>
          <table border="1" cellPadding="10" style={{ width: '100%', borderCollapse: 'collapse' }}>
            <thead>
              <tr style={{ backgroundColor: '#f0f0f0' }}>
                <th>Code</th>
                <th>Description</th>
              </tr>
            </thead>
            <tbody>
              {content.map((item, i) => (
                <tr key={i}>
                  <td>{item.code}</td>
                  <td>{item.description}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      );
    }

    if (Array.isArray(content)) {
      return (
        <div key={section}>
          <h3 style={{ marginTop: '1.5rem' }}>{section}</h3>
          <ul>
            {content.map((item, i) => (
              <li key={i} style={{ marginBottom: '5px' }}>{item}</li>
            ))}
          </ul>
        </div>
      );
    }

    return null;
  };

  return (
    <div style={{ padding: '2rem', fontFamily: 'Arial, sans-serif', maxWidth: '1000px', margin: '0 auto' }}>
      <h2>📄 PDF OCR Metadata + Table Extractor</h2>

      <div style={{ marginBottom: '1rem' }}>
        <input type="file" accept="application/pdf" onChange={handleFileChange} />
        <button onClick={handleUpload} style={{ marginLeft: '1rem' }}>Upload</button>
      </div>

      {loading && (
        <p style={{ color: 'orange' }}>⏳ Processing...</p>
      )}

      {!loading && structuredData && Object.keys(structuredData).length > 0 && (
        <div>
          {Object.entries(structuredData).map(([section, content]) => renderSection(section, content))}
        </div>
      )}

      {downloadReady && (
        <button onClick={handleDownloadPDF} style={{ marginTop: '2rem' }}>
          ⬇️ Download PDF
        </button>
      )}
    </div>
  );
};

export default OCRUpload;
