// src/components/Signup.jsx
import React, { useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const API_BASE = process.env.REACT_APP_API_BASE || "http://localhost:5000";

export default function Signup({ onSignupSuccess }) {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("user");
  const [message, setMessage] = useState("");

  const handleSignup = async (e) => {
    e.preventDefault();
    setMessage("");
    try {
      const res = await axios.post(`${API_BASE}/auth/signup`, { username, password, role });
      setMessage(res.data.message || "Signup successful");
      if (onSignupSuccess) onSignupSuccess();
    } catch (err) {
      console.error("Signup error:", err);
      setMessage(err.response?.data?.error || "Signup failed");
    }
  };

  return (
    <div
      style={{
        padding: 30,
        maxWidth: 420,
        margin: "40px auto",
        borderRadius: 20,
        background: "rgba(255,255,255,0.15)",
        backdropFilter: "blur(15px)",
        boxShadow: "0 8px 32px 0 rgba(31,38,135,0.37)",
        color: "#fff",
        textAlign: "center",
      }}
    >
      <h2 style={{ marginBottom: 20 }}>Sign up</h2>

      {message && (
        <div
          style={{
            color: message.toLowerCase().includes("failed") ? "red" : "#00ff88",
            marginBottom: 15,
          }}
        >
          {message}
        </div>
      )}

      <form onSubmit={handleSignup}>
        <input
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          placeholder="Username"
          required
          style={{
            width: "80%",        // slightly smaller than full width
            padding: 12,
            marginBottom: 15,
            borderRadius: 12,
            border: "none",
            outline: "none",
            background: "rgba(255,255,255,0.25)",
            color: "#fff",
            fontSize: 14,
            display: "block",
            marginLeft: "auto",
            marginRight: "auto",
          }}
        />

        <input
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          type="password"
          placeholder="Password"
          required
          style={{
            width: "100%",
            padding: 12,
            marginBottom: 15,
            borderRadius: 12,
            border: "none",
            outline: "none",
            background: "rgba(255,255,255,0.25)",
            color: "#fff",
            fontSize: 14,
          }}
        />

        <select
          value={role}
          onChange={(e) => setRole(e.target.value)}
          style={{
            width: "100%",
            padding: 12,
            marginBottom: 15,
            borderRadius: 12,
            border: "none",
            outline: "none",
            background: "rgba(255,255,255,0.25)",
            color: "#fff",
            fontSize: 14,
          }}
        >
          <option value="user">User</option>
          <option value="admin">Admin</option>
          <option value="manager">Viewer</option>
        </select>

        <button
          type="submit"
          style={{
            width: "100%",
            padding: 12,
            borderRadius: 12,
            border: "none",
            background: "rgba(0,255,136,0.7)",
            color: "#000",
            fontWeight: "bold",
            cursor: "pointer",
            fontSize: 15,
            transition: "0.3s",
          }}
          onMouseOver={(e) => (e.target.style.background = "rgba(0,255,136,0.9)")}
          onMouseOut={(e) => (e.target.style.background = "rgba(0,255,136,0.7)")}
        >
          Sign up
        </button>
      </form>

      <p style={{ marginTop: 20 }}>
        Already have an account?{" "}
        <Link to="/login" style={{ textDecoration: "underline", color: "#00ffcc" }}>
          Login
        </Link>
      </p>
    </div>
  );
}
