import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../App.css';

const OCRUpload = ({ auth, setAuth }) => {
  const navigate = useNavigate();
  const [files, setFiles] = useState([]);
  const [allMetadata, setAllMetadata] = useState([]);
  const [loading, setLoading] = useState(false);
  const [downloadReady, setDownloadReady] = useState(false);

  const [isSignup, setIsSignup] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');

  const API_BASE = process.env.REACT_APP_API_BASE || "http://localhost:5000";

  const api = axios.create({ baseURL: API_BASE });

  // Add Authorization header automatically
  api.interceptors.request.use(config => {
    if (auth?.token) config.headers.Authorization = `Bearer ${auth.token}`;
    return config;
  });

  // Handle session expiration globally
  api.interceptors.response.use(
    response => response,
    error => {
      if ([401, 403].includes(error.response?.status)) {
        localStorage.clear();
        setAuth(null);
        alert("‚ö†Ô∏è Session expired or access denied. Please log in again.");
      }
      return Promise.reject(error);
    }
  );

  // Redirect if already logged in
  useEffect(() => {
    if (auth?.token) navigate('/', { replace: true });
  }, [auth, navigate]);

  // Signup / Login
  const handleSignup = async () => {
    try {
      const res = await api.post('/auth/signup', { username, password });
      setMessage(res.data.message || 'Signup successful!');
      const loginRes = await api.post('/auth/login', { username, password });
      setAuth({
        token: loginRes.data.token,
        username: loginRes.data.username,
        role: loginRes.data.role,
      });
      localStorage.setItem('auth', JSON.stringify({
        token: loginRes.data.token,
        username: loginRes.data.username,
        role: loginRes.data.role,
      }));
    } catch (err) {
      setMessage('Signup failed: ' + (err.response?.data?.error || err.message));
    }
  };

  const handleLogin = async () => {
    try {
      const res = await api.post('/auth/login', { username, password });
      if (res.data.token) {
        setAuth({
          token: res.data.token,
          username: res.data.username,
          role: res.data.role,
        });
        localStorage.setItem('auth', JSON.stringify({
          token: res.data.token,
          username: res.data.username,
          role: res.data.role,
        }));
        setMessage('Login successful!');
      } else setMessage('Invalid credentials');
    } catch (err) {
      setMessage('Login failed: ' + (err.response?.data?.error || err.message));
    }
  };

  // File upload handlers
  const handleFileChange = (e) => {
    const selected = Array.from(e.target.files);
    if (selected.length > 10) {
      alert('‚ö†Ô∏è You can upload up to 10 files only.');
      return;
    }
    setFiles(selected);
    setAllMetadata([]);
    setDownloadReady(false);
  };
  const handleDownloadExcel = async () => {
    try {
      const res = await api.get('/api/export-excel', { responseType: 'blob' });
      const url = URL.createObjectURL(new Blob([res.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', 'metadata_output.xlsx');
      document.body.appendChild(link);
      link.click();
      link.remove();
    } catch (err) {
      console.error('Excel download failed:', err);
      alert('‚ö†Ô∏è Download failed. Please try again.');
    }
  };

  // --------------------------
  // Render
  // --------------------------
  if (!auth?.token) {
    return (
      <div className="container">
        <h2>{isSignup ? 'Signup' : 'Login'}</h2>
        <input type="text" placeholder="Username" value={username} onChange={(e) => setUsername(e.target.value)} className="auth-input" />
        <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} className="auth-input" />

        {isSignup ? (
          <button className="auth-btn" onClick={handleSignup}>Signup</button>
        ) : (
          <button className="auth-btn" onClick={handleLogin}>Login</button>
        )}

        <button className="toggle-btn" onClick={() => { setIsSignup(!isSignup); setMessage(''); }}>
          {isSignup ? 'Already have an account? Login' : 'No account? Signup'}
        </button>

        {message && <p className="auth-message">{message}</p>}
      </div>
    );
  }

  return (
    <div className="container">
      <header><h2 className="main-heading">üìÑ File OCR Extraction Tool</h2></header>

      <section className="upload-section">
        <label htmlFor="file-upload" className="upload-label">Upload up to 10 files:</label>
        <input type="file" id="file-upload" accept=".pdf,.jpg,.jpeg,.png,.doc,.docx" multiple onChange={handleFileChange} />
        <div className="button-row">
          <button className="upload-btn" onClick={handleUploadAll}>Upload All</button>
          {downloadReady && <button className="download-btn" onClick={handleDownloadExcel}>‚¨áÔ∏è Download Excel</button>}
        </div>
      </section>

      {files.length > 0 && (
        <section className="file-preview">
          <h3 className="section-heading">üìÅ Selected Files</h3>
          <ul className="file-list">{files.map((file, i) => <li key={i}>üìé {file.name}</li>)}</ul>
        </section>
      )}

      {loading && <p className="loading-text">‚è≥ Extracting metadata...</p>}

      {allMetadata.length > 0 && (
        <section className="results-section">
          <h3 className="section-heading">üìã Extracted Metadata</h3>
          {allMetadata.map((meta, fileIndex) => (
            <div key={fileIndex} className="table-container">
              <h4 className="file-title">{meta['File Name']}</h4>
              <table className="metadata-table">
                <thead><tr><th>Field</th><th>Value</th></tr></thead>
                <tbody>
                  {Object.entries(meta).map(([key, value], i) => (
                    <tr key={i}>
                      <td className="field-cell">{key}</td>
                      <td className="value-cell">
                        {(key === 'Patient Signature' || key === 'Physician Signature') && value?.includes('/signatures/')
                          ? <img src={`${API_BASE}${value}`} alt={key} className="signature-img" />
                          : value
                        }
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ))}
        </section>
      )}
    </div>
  );
};

export default OCRUpload;
