import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import * as XLSX from 'xlsx';
import { saveAs } from 'file-saver';
import '../App.css';

const OCRUpload = ({ auth, setAuth, isAdmin = false }) => {
  const navigate = useNavigate();
  const [files, setFiles] = useState([]);
  const [tables, setTables] = useState([]);       // [{ columns, rows, raw }]
  const [summaries, setSummaries] = useState([]); // [{ Charges, Receipts, ... , NOTE }]
  const [loading, setLoading] = useState(false);
  const [downloadReady, setDownloadReady] = useState(false);

  const [isSignup, setIsSignup] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');

  const API_BASE = process.env.REACT_APP_API_BASE || 'http://localhost:5000';
  const api = axios.create({ baseURL: API_BASE });

  api.interceptors.request.use((config) => {
    if (auth?.token) config.headers.Authorization = `Bearer ${auth.token}`;
    return config;
  });

  api.interceptors.response.use(
    (response) => response,
    (error) => {
      if ([401, 403].includes(error.response?.status)) {
        localStorage.clear();
        setAuth(null);
        alert('‚ö†Ô∏è Session expired or access denied. Please log in again.');
      }
      return Promise.reject(error);
    }
  );

  useEffect(() => {
    if (auth?.token && !isAdmin) navigate('/', { replace: true });
  }, [auth, isAdmin, navigate]);

  // ---------- Auth ----------
  const handleSignup = async () => {
    try {
      const res = await api.post('/auth/signup', { username, password });
      setMessage(res.data.message || 'Signup successful!');
      const loginRes = await api.post('/auth/login', { username, password });
      const { token, username: un, role } = loginRes.data;
      setAuth({ token, username: un, role });
      localStorage.setItem('auth', JSON.stringify({ token, username: un, role }));
    } catch (err) {
      setMessage('Signup failed: ' + (err.response?.data?.error || err.message));
    }
  };

  const handleLogin = async () => {
    try {
      const res = await api.post('/auth/login', { username, password });
      if (res.data.token) {
        const { token, username: un, role } = res.data;
        setAuth({ token, username: un, role });
        localStorage.setItem('auth', JSON.stringify({ token, username: un, role }));
        setMessage('Login successful!');
      } else setMessage('Invalid credentials');
    } catch (err) {
      setMessage('Login failed: ' + (err.response?.data?.error || err.message));
    }
  };

  // ---------- File Selection ----------
  const handleFileChange = (e) => {
    const selected = Array.from(e.target.files || []);
    if (selected.length > 10) {
      alert('‚ö†Ô∏è You can upload up to 10 files only.');
      return;
    }
    setFiles(selected);
    setTables([]);
    setSummaries([]);
    setDownloadReady(false);
  };

  // ---------- Upload & Parse ----------
  const handleUploadAll = async () => {
    if (!files.length) return;
    setLoading(true);

    try {
      const outTables = [];
      const outSummaries = [];

      for (const [idx, file] of files.entries()) {
        const formData = new FormData();
        formData.append('file', file);

        const res = await api.post('/api/ocr', formData, {
          headers: { 'Content-Type': 'multipart/form-data' },
        });

        console.log('OCR response for', file.name, res.data);

        // Your backend shape (based on your console.log)
        const md = res.data?.metadata || {};
        const report = md.report || {};
        const fieldArr = Array.isArray(md.fields) ? md.fields : [];
        const rawTables = Array.isArray(md.tables) ? md.tables : [];

        const fixedHeaders = report.columns || [
          "Patient Name","Pat Ocr","Responsible Party Name","Rep #","Doc",
          "B-Type","Seen","Service","Type","Office","Code","Description",
          "Amount","Balance","Claim","Loc","Entered","Who"
        ];

        // We *don‚Äôt* fabricate rows from raw text; keep exactly what backend parsed.
        const rows = Array.isArray(report.rows) ? report.rows : [];

        // Summary: take from fields; if blank, rebuild from rawTables
        const wanted = [
          "Charges","Receipts","Commissions","Total Due Doctor","Credits","Debits",
          "Bal Fwd","Fin Chg","Direct Paymnt","Assess","Acct. Rec.","Total Entries","NOTE"
        ];

        const summaryObj = {};
        // 1) direct (fields)
        fieldArr.forEach(({ Field, Value }) => {
          if (!Field) return;
          const key = wanted.find(w => w.toLowerCase() === String(Field).toLowerCase());
          if (key) summaryObj[key] = Value ?? "";
        });

        // 2) fallback (parse raw)
        if (rawTables.length) {
          const joined = rawTables
            .map(x => (Array.isArray(x) ? x.join(" ") : String(x ?? "")))
            .join(" ");
          const grab = (label) => {
            // matches "Label: 12,345.67" or "Label:12,345.67"
            const m = new RegExp(`${label.replace(/[.*+?^${}()|[\\]\\\\]/g, '\\$&')}\\s*:?\\s*([0-9,.-]+)`, "i").exec(joined);
            return m ? m[1] : "";
          };
          wanted.forEach(k => {
            if (!summaryObj[k] || summaryObj[k] === "") {
              if (k === "NOTE") {
                const m = /NOTE\s*:?(.+?)(?:\s{2,}|$)/i.exec(joined);
                if (m) summaryObj["NOTE"] = m[1].trim();
              } else {
                summaryObj[k] = grab(k) || "";
              }
            }
          });
        }

        outTables[idx] = { columns: fixedHeaders, rows, raw: rawTables };
        outSummaries[idx] = summaryObj;
      }

      setTables(outTables);
      setSummaries(outSummaries);
      setDownloadReady(true);
    } catch (err) {
      console.error('Upload failed:', err);
      alert('‚ö†Ô∏è Upload failed. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  // ---------- Excel Export ----------
  const handleDownloadExcel = () => {
    const wb = XLSX.utils.book_new();

    // Sheet per file: ReportRows_<n>
    tables.forEach((t, i) => {
      const data = (t.rows || []).map(r => {
        const o = {};
        (t.columns || []).forEach(c => { o[c] = r?.[c] ?? ""; });
        return o;
      });
      const ws = XLSX.utils.json_to_sheet(data.length ? data : [{ Note: "No rows" }]);
      XLSX.utils.book_append_sheet(wb, ws, `ReportRows_${i + 1}`);
    });

    // Fields sheet (one row per file)
    const fieldsRows = summaries.map((s, i) => ({ File: files[i]?.name || `File_${i + 1}`, ...s }));
    XLSX.utils.book_append_sheet(
      wb,
      XLSX.utils.json_to_sheet(fieldsRows.length ? fieldsRows : [{ Note: "No summaries" }]),
      "Fields"
    );

    // RawText_<n> per file
    tables.forEach((t, i) => {
      const raw = (t.raw || []).map((line, idx) => ({
        Line: idx + 1,
        Text: Array.isArray(line) ? line.join(" ") : String(line ?? "")
      }));
      const ws = XLSX.utils.json_to_sheet(raw.length ? raw : [{ Note: "No raw text" }]);
      XLSX.utils.book_append_sheet(wb, ws, `RawText_${i + 1}`);
    });

    const wbout = XLSX.write(wb, { type: 'array', bookType: 'xlsx' });
    saveAs(new Blob([wbout], { type: 'application/octet-stream' }), 'ocr_export.xlsx');
  };

  // ---------- Renderers ----------
  const ExcelGrid = ({ title, columns, rows, summary }) => (
    <div className="excel-grid-wrapper">
      <div className="excel-grid-header">
        <h4 className="file-title">{title}</h4>
      </div>
      <div className="excel-grid compact">
        <div className="excel-grid-scroll">
          <table className="excel-table">
            <thead>
              <tr>
                {columns.map((col) => (
                  <th key={col} className="excel-th">{col}</th>
                ))}
              </tr>
            </thead>
            <tbody>
              {rows && rows.length > 0 ? (
                rows.map((r, i) => (
                  <tr key={i}>
                    {columns.map((col) => (
                      <td key={col} className="excel-td">{r?.[col] ?? ''}</td>
                    ))}
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan={columns.length} style={{ textAlign: 'center' }}>
                    No rows (showing Summary and Raw OCR below)
                  </td>
                </tr>
              )}
              {summary && Object.keys(summary).length > 0 && (
                <tr className="summary-row">
                  <td colSpan={columns.length} style={{ fontWeight: 'bold' }}>
                    Charges: {summary["Charges"] || 0} | Receipts: {summary["Receipts"] || 0} | 
                    Credits: {summary["Credits"] || 0} | Debits: {summary["Debits"] || 0} | 
                    Balance Fwd: {summary["Bal Fwd"] || 0} | Acct. Rec.: {summary["Acct. Rec."] || 0} | 
                    Total Entries: {summary["Total Entries"] || 0}
                    {summary["NOTE"] ? ` | Note: ${summary["NOTE"]}` : ""}
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );

  const SummaryCards = ({ summary }) => {
    const keys = [
      "Charges","Receipts","Commissions","Total Due Doctor","Credits","Debits",
      "Bal Fwd","Fin Chg","Direct Paymnt","Assess","Acct. Rec.","Total Entries","NOTE"
    ];
    return (
      <div className="grid grid-cols-2 md:grid-cols-3 gap-2 my-3">
        {keys.map(k => (
          <div key={k} className="border border-white/15 rounded-lg p-2">
            <div className="text-xs opacity-70">{k}</div>
            <div className="font-semibold">{summary?.[k] ? String(summary[k]) : "‚Äî"}</div>
          </div>
        ))}
      </div>
    );
  };

  const RawPanel = ({ lines }) => (
    <div className="h-64 overflow-auto border border-white/15 rounded-xl p-3 text-xs leading-6 my-2">
      {(lines?.length ? lines : ["(no raw text)"]).map((t, i) => (
        <div key={i} className="whitespace-pre-wrap">
          {i + 1}. {Array.isArray(t) ? t.join(" ") : String(t ?? "")}
        </div>
      ))}
    </div>
  );

  // ---------- Auth Gate ----------
  if (!auth?.token && !isAdmin) {
    return (
      <div className="container">
        <h2>{isSignup ? 'Signup' : 'Login'}</h2>
        <input type="text" placeholder="Username" value={username} onChange={(e) => setUsername(e.target.value)} className="auth-input" />
        <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} className="auth-input" />
        {isSignup ? (
          <button className="auth-btn" onClick={handleSignup}>Signup</button>
        ) : (
          <button className="auth-btn" onClick={handleLogin}>Login</button>
        )}
        <button className="toggle-btn" onClick={() => { setIsSignup(!isSignup); setMessage(''); }}>
          {isSignup ? 'Already have an account? Login' : 'No account? Signup'}
        </button>
        {message && <p className="auth-message">{message}</p>}
      </div>
    );
  }

  // ---------- UI ----------
  return (
    <div className="container">
      <header>
        <h2 className="main-heading">
          {isAdmin ? 'üìÇ Admin OCR Upload Tool' : 'üìÑ File OCR Extraction Tool'}
        </h2>
      </header>

      <section className="upload-section">
        <label htmlFor="file-upload" className="upload-label">Upload up to 10 files:</label>
        <input type="file" id="file-upload" accept=".pdf,.jpg,.jpeg,.png,.doc,.docx" multiple onChange={handleFileChange} />
        <div className="button-row">
          <button className="upload-btn" onClick={handleUploadAll}>Upload All</button>
          {downloadReady && (
            <button className="download-btn" onClick={handleDownloadExcel}>
              ‚¨áÔ∏è Download Excel
            </button>
          )}
        </div>
      </section>

      {files.length > 0 && (
        <section className="file-preview">
          <h3 className="section-heading">üìÅ Selected Files</h3>
          <ul className="file-list">
            {files.map((file, i) => (<li key={i}>üìé {file.name}</li>))}
          </ul>
        </section>
      )}

      {loading && <p className="loading-text">‚è≥ Extracting table...</p>}

      {tables.length > 0 && (
        <section className="results-section">
          <h3 className="section-heading">üìä Procedure Report</h3>

          {files.map((file, idx) => (
            <div key={`wrap-${idx}`} className="mb-10">
              <ExcelGrid
                title={file.name}
                columns={tables[idx]?.columns || []}
                rows={tables[idx]?.rows || []}
                summary={summaries[idx] || {}}
              />

              <h4 className="section-heading" style={{ marginTop: '12px' }}>üìå Summary</h4>
              <SummaryCards summary={summaries[idx] || {}} />

              <h4 className="section-heading" style={{ marginTop: '12px' }}>üßæ Raw OCR Lines</h4>
              <RawPanel lines={tables[idx]?.raw || []} />
            </div>
          ))}
        </section>
      )}
    </div>
  );
};

export default OCRUpload;
