import React, { useState, useEffect, useMemo } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../App.css';

const OCRUpload = ({ auth, setAuth, isAdmin = false }) => {
  const navigate = useNavigate();
  const [files, setFiles] = useState([]);
  const [agingTables, setAgingTables] = useState([]); // parsed tables per file
  const [loading, setLoading] = useState(false);
  const [downloadReady, setDownloadReady] = useState(false);

  const [isSignup, setIsSignup] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');

  const API_BASE = process.env.REACT_APP_API_BASE || 'http://localhost:5000';

  const api = axios.create({ baseURL: API_BASE });

  api.interceptors.request.use((config) => {
    if (auth?.token) config.headers.Authorization = `Bearer ${auth.token}`;
    return config;
  });

  api.interceptors.response.use(
    (response) => response,
    (error) => {
      if ([401, 403].includes(error.response?.status)) {
        localStorage.clear();
        setAuth(null);
        alert('⚠️ Session expired or access denied. Please log in again.');
      }
      return Promise.reject(error);
    }
  );

  useEffect(() => {
    if (auth?.token && !isAdmin) {
      navigate('/', { replace: true });
    }
  }, [auth, isAdmin, navigate]);

  const handleSignup = async () => {
    try {
      const res = await api.post('/auth/signup', { username, password });
      setMessage(res.data.message || 'Signup successful!');
      const loginRes = await api.post('/auth/login', { username, password });
      setAuth({
        token: loginRes.data.token,
        username: loginRes.data.username,
        role: loginRes.data.role,
      });
      localStorage.setItem('auth', JSON.stringify({
        token: loginRes.data.token,
        username: loginRes.data.username,
        role: loginRes.data.role,
      }));
    } catch (err) {
      setMessage('Signup failed: ' + (err.response?.data?.error || err.message));
    }
  };

  const handleLogin = async () => {
    try {
      const res = await api.post('/auth/login', { username, password });
      if (res.data.token) {
        setAuth({
          token: res.data.token,
          username: res.data.username,
          role: res.data.role,
        });
        localStorage.setItem('auth', JSON.stringify({
          token: res.data.token,
          username: res.data.username,
          role: res.data.role,
        }));
        setMessage('Login successful!');
      } else {
        setMessage('Invalid credentials');
      }
    } catch (err) {
      setMessage('Login failed: ' + (err.response?.data?.error || err.message));
    }
  };

  const handleFileChange = (e) => {
    const selected = Array.from(e.target.files);
    if (selected.length > 10) {
      alert('⚠️ You can upload up to 10 files only.');
      return;
    }
    setFiles(selected);
    setAgingTables([]);
    setDownloadReady(false);
  };

  const handleUploadAll = async () => {
    if (!files.length) return;
    setLoading(true);

    try {
      if (auth?.role?.toLowerCase() === 'admin') {
        try { await api.post('/api/reset'); } catch {}
      }

      const tables = [];

      for (const [idx, file] of files.entries()) {
        const formData = new FormData();
        formData.append('file', file);

        const res = await api.post('/api/ocr', formData, {
          headers: {
            'Content-Type': 'multipart/form-data',
            Authorization: `Bearer ${auth?.token}`,
          },
        });

        // Expect the table here:
        const report = res.data.report || res.data.metadata?.report || null;
        tables[idx] = (report && report.columns && Array.isArray(report.rows))
          ? report
          : { columns: [], rows: [], sections: [] };

        // If server still needs a hit for Excel export, keep call but without storing any metadata for UI
        await api.post('/api/export-excel', { metadata: res.data.metadata || {} }, {
          headers: { Authorization: `Bearer ${auth?.token}` },
        });
      }

      setAgingTables(tables);
      setDownloadReady(true);
    } catch (err) {
      console.error('Upload failed:', err);
      alert('⚠️ Upload failed. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleDownloadExcel = async () => {
    try {
      const res = await api.get('/api/export-excel', {
        responseType: 'blob',
        headers: { Authorization: `Bearer ${auth?.token}` },
      });
      const url = URL.createObjectURL(new Blob([res.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', 'metadata_output.xlsx');
      document.body.appendChild(link);
      link.click();
      link.remove();
    } catch (err) {
      console.error('Excel download failed:', err);
      alert('⚠️ Download failed. Please try again.');
    }
  };

  const ExcelGrid = ({ title, columns, rows }) => {
    const hasData = Array.isArray(columns) && columns.length > 0 && Array.isArray(rows);
    return (
      <div className="excel-grid-wrapper">
        <div className="excel-grid-header">
          <h4 className="file-title">{title}</h4>
        </div>
        {!hasData ? (
          <div className="no-data">No table rows parsed</div>
        ) : (
          <div className="excel-grid compact">
            <div className="excel-grid-scroll">
              <table className="excel-table">
                <thead>
                  <tr>
                    {columns.map((col) => (
                      <th key={col} className="excel-th">{col}</th>
                    ))}
                  </tr>
                </thead>
                <tbody>
                  {rows.length === 0 ? (
                    <tr>
                      <td className="excel-td" colSpan={columns.length} style={{ textAlign: 'center' }}>
                        No rows
                      </td>
                    </tr>
                  ) : (
                    rows.map((r, i) => (
                      <tr key={r?.row_id ?? i}>
                        {columns.map((col) => (
                          <td key={col} className="excel-td">{r?.[col] ?? ''}</td>
                        ))}
                      </tr>
                    ))
                  )}
                </tbody>
              </table>
            </div>
          </div>
        )}
      </div>
    );
  };

  if (!auth?.token && !isAdmin) {
    return (
      <div className="container">
        <h2>{isSignup ? 'Signup' : 'Login'}</h2>
        <input type="text" placeholder="Username" value={username} onChange={(e) => setUsername(e.target.value)} className="auth-input" />
        <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} className="auth-input" />
        {isSignup ? (
          <button className="auth-btn" onClick={handleSignup}>Signup</button>
        ) : (
          <button className="auth-btn" onClick={handleLogin}>Login</button>
        )}
        <button className="toggle-btn" onClick={() => { setIsSignup(!isSignup); setMessage(''); }}>
          {isSignup ? 'Already have an account? Login' : 'No account? Signup'}
        </button>
        {message && <p className="auth-message">{message}</p>}
      </div>
    );
  }

  return (
    <div className="container">
      <header>
        <h2 className="main-heading">
          {isAdmin ? '📂 Admin OCR Upload Tool' : '📄 File OCR Extraction Tool'}
        </h2>
      </header>

      <section className="upload-section">
        <label htmlFor="file-upload" className="upload-label">
          Upload up to 10 files:
        </label>
        <input
          type="file"
          id="file-upload"
          accept=".pdf,.jpg,.jpeg,.png,.doc,.docx"
          multiple
          onChange={handleFileChange}
        />
        <div className="button-row">
          <button className="upload-btn" onClick={handleUploadAll}>
            Upload All
          </button>
          {downloadReady && (
            <button className="download-btn" onClick={handleDownloadExcel}>
              ⬇️ Download Excel
            </button>
          )}
        </div>
      </section>

      {files.length > 0 && (
        <section className="file-preview">
          <h3 className="section-heading">📁 Selected Files</h3>
          <ul className="file-list">
            {files.map((file, i) => (
              <li key={i}>📎 {file.name}</li>
            ))}
          </ul>
        </section>
      )}

      {loading && <p className="loading-text">⏳ Extracting table...</p>}

      {/* Only the table view, no raw metadata/FieldValue/columns/rows/sections */}
      {agingTables.length > 0 && (
        <section className="results-section">
          <h3 className="section-heading">📊 Aging Report</h3>
          {files.map((file, idx) => {
            const table = agingTables[idx] || { columns: [], rows: [], sections: [] };
            return (
              <ExcelGrid
                key={`grid-${idx}`}
                title={file.name}
                columns={table.columns || []}
                rows={table.rows || []}
              />
            );
          })}
        </section>
      )}
    </div>
  );
};

export default OCRUpload;
