// OCRUpload.jsx (with Auto-Parse fallback)
import React, { useState, useEffect, useMemo } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import * as XLSX from 'xlsx';
import { saveAs } from 'file-saver';
import '../App.css';

const FIXED_HEADERS = [
  "Patient Name","Pat Ocr","Responsible Party Name","Rep #","Doc",
  "B-Type","Seen","Service","Type","Office","Code","Description",
  "Amount","Balance","Claim","Loc","Entered","Who"
];

// ------------- Small helpers -------------
const isMoney = (s) => /\d[\d,]*\.\d{2}/.test(s || "");
const toMoneyTokens = (s) =>
  (s || "").match(/\d[\d,]*\.\d{2}/g) || [];

const looksLikeName = (s) => /[A-Za-z]+,[A-Za-z]+/.test(s || "");
const looksLikeRepHash = (s) => /\b\d{6,}-\d\b/.test(s || "");
const looksLikeCPT = (s) => /\b\d{4,5}(?:-[A-Z0-9]{2})?\b/.test(s || ""); // 00402-QZ, 19318-50, 67904-50, etc.
const looksLikeDate = (s) => /\b\d{2}[-/]\d{2}[-/]\d{2}\b/.test(s || "");
const compact = (a) => a.filter(Boolean);

const joinParts = (x) => (Array.isArray(x) ? x.join(" ") : String(x ?? "")).trim();

// Auto parser: builds best-effort rows from raw OCR lines.
// Strategy:
// - Keep a "patient context" when we see Name and Rep# (and sometimes Pat Ocr/Responsible Party).
// - When we hit a line with CPT-like codes, start a row; next human-text line becomes Description;
//   next money line supplies Amount (and optionally Balance). Any trailing tokens we map to Claim/Loc/Entered/Who.
function autoParseRows(rawTables, fieldArr) {
  const rows = [];
  if (!Array.isArray(rawTables) || rawTables.length === 0) return rows;

  let ctx = {
    patient: "",
    patOcr: "",
    respParty: "",
    rep: "",
    doc: ""
  };

  // Try to get doc/resp/other hints from fields (often blank in your file)
  const getField = (k) =>
    (fieldArr || []).find(f => String(f.Field || "").toLowerCase() === k.toLowerCase())?.Value || "";

  // Iterate raw lines
  const lines = rawTables.map(joinParts);

  for (let i = 0; i < lines.length; i++) {
    const line = lines[i];

    // update patient context
    if (looksLikeName(line)) {
      ctx.patient = line;
      // look ahead for rep#
      for (let k = 1; k <= 3 && i + k < lines.length; k++) {
        if (looksLikeRepHash(lines[i + k])) {
          ctx.rep = lines[i + k].match(/\b\d{6,}-\d\b/)[0];
          break;
        }
      }
      continue;
    }
    if (looksLikeRepHash(line)) {
      ctx.rep = line.match(/\b\d{6,}-\d\b/)[0];
      continue;
    }

    // detect a procedure row start by CPT-like code presence
    if (looksLikeCPT(line)) {
      const row = Object.fromEntries(FIXED_HEADERS.map(h => [h, ""]));
      row["Patient Name"] = ctx.patient;
      row["Pat Ocr"] = ctx.patOcr;
      row["Responsible Party Name"] = ctx.respParty;
      row["Rep #"] = ctx.rep;
      row["Doc"] = ctx.doc;

      // Pull codes from this and possibly the next line too (your sample often concatenates codes)
      let codes = [];
      let j = i;
      while (j < lines.length && looksLikeCPT(lines[j])) {
        const found = lines[j].match(/\b\d{4,5}(?:-[A-Z0-9]{2})?\b/g) || [];
        codes.push(...found);
        j++;
      }
      row["Code"] = compact(codes).join(" ");

      // Description: first non-empty, non-money, non-code line following
      let descIdx = j;
      while (descIdx < lines.length) {
        const cand = lines[descIdx];
        if (!looksLikeCPT(cand) && !isMoney(cand)) {
          row["Description"] = cand.replace(/\*/g, " ").replace(/\s+/g, " ").trim();
          descIdx++;
          break;
        }
        descIdx++;
      }

      // Amount/Balance: next line(s) with money tokens
      let moneyIdx = descIdx;
      let amountSet = false;
      while (moneyIdx < lines.length) {
        const m = toMoneyTokens(lines[moneyIdx]);
        if (m.length) {
          if (!amountSet) {
            row["Amount"] = m[0];
            if (m.length > 1) row["Balance"] = m[1];
            amountSet = true;
          } else if (!row["Balance"] && m.length) {
            row["Balance"] = m[0];
          }
          moneyIdx++;
          break;
        }
        // break early if we hit another header-like region
        if (/^Charges:|^Date:|^FFCBilling/i.test(lines[moneyIdx])) break;
        moneyIdx++;
      }

      // Claim/Loc/Entered/Who: very fuzzy; take next short tokens if present
      let tail = compact([
        lines[moneyIdx],
        lines[moneyIdx + 1],
        lines[moneyIdx + 2],
      ])
        .join(" ")
        .trim();

      if (tail) {
        const parts = tail.split(/\s+/);
        // try to map a few tokens if they look short-ish
        if (!row["Claim"] && parts[0] && parts[0].length <= 8 && !isMoney(parts[0])) {
          row["Claim"] = parts.shift();
        }
        if (!row["Loc"] && parts[0] && parts[0].length <= 8 && !isMoney(parts[0])) {
          row["Loc"] = parts.shift();
        }
        if (!row["Entered"] && parts[0] && (looksLikeDate(parts[0]) || parts[0].length <= 10)) {
          row["Entered"] = parts.shift();
        }
        if (!row["Who"] && parts.length) {
          row["Who"] = parts.join(" ");
        }
      }

      rows.push(row);
      // advance i close to moneyIdx to avoid duplicating
      i = Math.max(i, moneyIdx - 1);
    }
  }

  // Deduplicate obviously identical rows (same patient + code + amount)
  const seen = new Set();
  const unique = [];
  for (const r of rows) {
    const key = [r["Patient Name"], r["Code"], r["Amount"], r["Description"]].join("|");
    if (!seen.has(key)) {
      seen.add(key);
      unique.push(r);
    }
  }
  return unique;
}

const OCRUpload = ({ auth, setAuth, isAdmin = false }) => {
  const navigate = useNavigate();
  const [files, setFiles] = useState([]);
  const [tables, setTables] = useState([]);       // [{ columns, rows, raw }]
  const [summaries, setSummaries] = useState([]); // [{ Charges, ... }]
  const [loading, setLoading] = useState(false);
  const [downloadReady, setDownloadReady] = useState(false);
  const [useAutoParse, setUseAutoParse] = useState(true); // <-- toggle

  const [isSignup, setIsSignup] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');

  const API_BASE = process.env.REACT_APP_API_BASE || 'http://localhost:5000';
  const api = axios.create({ baseURL: API_BASE });

  api.interceptors.request.use((config) => {
    if (auth?.token) config.headers.Authorization = `Bearer ${auth.token}`;
    return config;
  });

  api.interceptors.response.use(
    (response) => response,
    (error) => {
      if ([401, 403].includes(error.response?.status)) {
        localStorage.clear();
        setAuth(null);
        alert('âš ï¸ Session expired or access denied. Please log in again.');
      }
      return Promise.reject(error);
    }
  );

  useEffect(() => {
    if (auth?.token && !isAdmin) navigate('/', { replace: true });
  }, [auth, isAdmin, navigate]);

  // -------- Auth (unchanged) --------
  const handleSignup = async () => {
    try {
      const res = await api.post('/auth/signup', { username, password });
      setMessage(res.data.message || 'Signup successful!');
      const loginRes = await api.post('/auth/login', { username, password });
      const { token, username: un, role } = loginRes.data;
      setAuth({ token, username: un, role });
      localStorage.setItem('auth', JSON.stringify({ token, username: un, role }));
    } catch (err) {
      setMessage('Signup failed: ' + (err.response?.data?.error || err.message));
    }
  };

  const handleLogin = async () => {
    try {
      const res = await api.post('/auth/login', { username, password });
      if (res.data.token) {
        const { token, username: un, role } = res.data;
        setAuth({ token, username: un, role });
        localStorage.setItem('auth', JSON.stringify({ token, username: un, role }));
        setMessage('Login successful!');
      } else setMessage('Invalid credentials');
    } catch (err) {
      setMessage('Login failed: ' + (err.response?.data?.error || err.message));
    }
  };

  // -------- File Selection --------
  const handleFileChange = (e) => {
    const selected = Array.from(e.target.files || []);
    if (selected.length > 10) {
      alert('âš ï¸ You can upload up to 10 files only.');
      return;
    }
    setFiles(selected);
    setTables([]);
    setSummaries([]);
    setDownloadReady(false);
  };

  // -------- Upload & Parse --------
  const handleUploadAll = async () => {
    if (!files.length) return;
    setLoading(true);

    try {
      const outTables = [];
      const outSummaries = [];

      for (const [idx, file] of files.entries()) {
        const formData = new FormData();
        formData.append('file', file);

        const res = await api.post('/api/ocr', formData, {
          headers: { 'Content-Type': 'multipart/form-data' },
        });

        console.log('OCR response for', file.name, res.data);

        // Your backend structure (from your console.log)
        const md = res.data?.metadata || {};
        const report = md.report || {};
        const rawTables = Array.isArray(md.tables) ? md.tables : [];
        const fieldArr = Array.isArray(md.fields) ? md.fields : [];

        // rows: prefer backend rows, else beta auto-parse from raw
        let rows = Array.isArray(report.rows) ? report.rows : [];
        if ((!rows || rows.length === 0) && useAutoParse) {
          rows = autoParseRows(rawTables, fieldArr);
        }

        const columns = report.columns?.length ? report.columns : FIXED_HEADERS;

        // Summary build (fields â†’ fallback to raw parse)
        const wanted = [
          "Charges","Receipts","Commissions","Total Due Doctor","Credits","Debits",
          "Bal Fwd","Fin Chg","Direct Paymnt","Assess","Acct. Rec.","Total Entries","NOTE"
        ];
        const summaryObj = {};
        fieldArr.forEach(({ Field, Value }) => {
          const hit = wanted.find(w => w.toLowerCase() === String(Field || "").toLowerCase());
          if (hit) summaryObj[hit] = Value ?? "";
        });
        if (rawTables.length) {
          const joined = rawTables.map(joinParts).join(" ");
          const grab = (label) => {
            const m = new RegExp(`${label.replace(/[.*+?^${}()|[\]\\]/g, '\\$&')}\\s*:?\\s*([0-9,.-]+)`, "i").exec(joined);
            return m ? m[1] : "";
          };
          wanted.forEach(k => {
            if (!summaryObj[k] || summaryObj[k] === "") {
              if (k === "NOTE") {
                const m = /NOTE\s*:?(.+?)(?:\s{2,}|$)/i.exec(joined);
                if (m) summaryObj["NOTE"] = m[1].trim();
              } else {
                summaryObj[k] = grab(k) || "";
              }
            }
          });
        }

        outTables[idx] = { columns, rows, raw: rawTables };
        outSummaries[idx] = summaryObj;
      }

      setTables(outTables);
      setSummaries(outSummaries);
      setDownloadReady(true);
    } catch (err) {
      console.error('Upload failed:', err);
      alert('âš ï¸ Upload failed. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  // -------- Excel Export --------
  const handleDownloadExcel = () => {
    const wb = XLSX.utils.book_new();

    // 1) Report rows per file
    tables.forEach((t, i) => {
      const data = (t.rows || []).map(r => {
        const o = {};
        (t.columns || FIXED_HEADERS).forEach(c => { o[c] = r?.[c] ?? ""; });
        return o;
      });
      const ws = XLSX.utils.json_to_sheet(data.length ? data : [{ Note: "No rows" }]);
      XLSX.utils.book_append_sheet(wb, ws, `ReportRows_${i + 1}`);
    });

    // 2) Fields sheet
    const fieldsRows = summaries.map((s, i) => ({ File: files[i]?.name || `File_${i + 1}`, ...s }));
    XLSX.utils.book_append_sheet(
      wb,
      XLSX.utils.json_to_sheet(fieldsRows.length ? fieldsRows : [{ Note: "No summaries" }]),
      "Fields"
    );

    // 3) Raw text per file
    tables.forEach((t, i) => {
      const raw = (t.raw || []).map((line, idx) => ({
        Line: idx + 1,
        Text: joinParts(line)
      }));
      const ws = XLSX.utils.json_to_sheet(raw.length ? raw : [{ Note: "No raw text" }]);
      XLSX.utils.book_append_sheet(wb, ws, `RawText_${i + 1}`);
    });

    const wbout = XLSX.write(wb, { type: 'array', bookType: 'xlsx' });
    saveAs(new Blob([wbout], { type: 'application/octet-stream' }), 'ocr_export.xlsx');
  };

  // -------- Renderers --------
  const ExcelGrid = ({ title, columns, rows, summary, wasParsed }) => (
    <div className="excel-grid-wrapper">
      <div className="excel-grid-header">
        <h4 className="file-title">{title}</h4>
        {wasParsed && <span className="badge">auto-parsed</span>}
      </div>
      <div className="excel-grid compact">
        <div className="excel-grid-scroll">
          <table className="excel-table">
            <thead>
              <tr>
                {columns.map((col) => (
                  <th key={col} className="excel-th">{col}</th>
                ))}
              </tr>
            </thead>
            <tbody>
              {rows && rows.length > 0 ? (
                rows.map((r, i) => (
                  <tr key={i}>
                    {columns.map((col) => (
                      <td key={col} className="excel-td">{r?.[col] ?? ''}</td>
                    ))}
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan={columns.length} style={{ textAlign: 'center' }}>
                    No rows (showing Summary and Raw OCR below)
                  </td>
                </tr>
              )}
              {summary && Object.keys(summary).length > 0 && (
                <tr className="summary-row">
                  <td colSpan={columns.length} style={{ fontWeight: 'bold' }}>
                    Charges: {summary["Charges"] || 0} | Receipts: {summary["Receipts"] || 0} | 
                    Credits: {summary["Credits"] || 0} | Debits: {summary["Debits"] || 0} | 
                    Balance Fwd: {summary["Bal Fwd"] || 0} | Acct. Rec.: {summary["Acct. Rec."] || 0} | 
                    Total Entries: {summary["Total Entries"] || 0}
                    {summary["NOTE"] ? ` | Note: ${summary["NOTE"]}` : ""}
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );

  const SummaryCards = ({ summary }) => {
    const keys = [
      "Charges","Receipts","Commissions","Total Due Doctor","Credits","Debits",
      "Bal Fwd","Fin Chg","Direct Paymnt","Assess","Acct. Rec.","Total Entries","NOTE"
    ];
    return (
      <div className="grid grid-cols-2 md:grid-cols-3 gap-2 my-3">
        {keys.map(k => (
          <div key={k} className="border border-white/15 rounded-lg p-2">
            <div className="text-xs opacity-70">{k}</div>
            <div className="font-semibold">{summary?.[k] ? String(summary[k]) : "â€”"}</div>
          </div>
        ))}
      </div>
    );
  };

  const RawPanel = ({ lines }) => (
    <div className="h-64 overflow-auto border border-white/15 rounded-xl p-3 text-xs leading-6 my-2">
      {(lines?.length ? lines : ["(no raw text)"]).map((t, i) => (
        <div key={i} className="whitespace-pre-wrap">
          {i + 1}. {joinParts(t)}
        </div>
      ))}
    </div>
  );

  // -------- Auth Gate --------
  if (!auth?.token && !isAdmin) {
    return (
      <div className="container">
        <h2>{isSignup ? 'Signup' : 'Login'}</h2>
        <input type="text" placeholder="Username" value={username} onChange={(e) => setUsername(e.target.value)} className="auth-input" />
        <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} className="auth-input" />
        {isSignup ? (
          <button className="auth-btn" onClick={handleSignup}>Signup</button>
        ) : (
          <button className="auth-btn" onClick={handleLogin}>Login</button>
        )}
        <button className="toggle-btn" onClick={() => { setIsSignup(!isSignup); setMessage(''); }}>
          {isSignup ? 'Already have an account? Login' : 'No account? Signup'}
        </button>
        {message && <p className="auth-message">{message}</p>}
      </div>
    );
  }

  // -------- UI --------
  return (
    <div className="container">
      <header>
        <h2 className="main-heading">
          {isAdmin ? 'ðŸ“‚ Admin OCR Upload Tool' : 'ðŸ“„ File OCR Extraction Tool'}
        </h2>
      </header>

      <section className="upload-section">
        <label htmlFor="file-upload" className="upload-label">Upload up to 10 files:</label>
        <input type="file" id="file-upload" accept=".pdf,.jpg,.jpeg,.png,.doc,.docx" multiple onChange={handleFileChange} />
        <div className="button-row">
          <button className="upload-btn" onClick={handleUploadAll}>Upload All</button>
          {downloadReady && (
            <button className="download-btn" onClick={handleDownloadExcel}>
              â¬‡ï¸ Download Excel
            </button>
          )}
        </div>
        <div className="mt-2">
          <label style={{ fontSize: 12 }}>
            <input
              type="checkbox"
              checked={useAutoParse}
              onChange={(e) => setUseAutoParse(e.target.checked)}
              style={{ marginRight: 6 }}
            />
            Try to auto-parse rows from raw OCR when backend rows are empty (beta)
          </label>
        </div>
      </section>

      {files.length > 0 && (
        <section className="file-preview">
          <h3 className="section-heading">ðŸ“ Selected Files</h3>
          <ul className="file-list">
            {files.map((file, i) => (<li key={i}>ðŸ“Ž {file.name}</li>))}
          </ul>
        </section>
      )}

      {loading && <p className="loading-text">â³ Extracting table...</p>}

      {tables.length > 0 && (
        <section className="results-section">
          <h3 className="section-heading">ðŸ“Š Procedure Report</h3>

          {files.map((file, idx) => {
            const wasParsed = (tables[idx]?.rows?.length || 0) > 0 && (rescueRowsNote());
            function rescueRowsNote(){ return true; } // just for a badge flag; harmless
            return (
              <div key={`wrap-${idx}`} className="mb-10">
                <ExcelGrid
                  title={file.name}
                  columns={tables[idx]?.columns || FIXED_HEADERS}
                  rows={tables[idx]?.rows || []}
                  summary={summaries[idx] || {}}
                  wasParsed={wasParsed}
                />

                <h4 className="section-heading" style={{ marginTop: '12px' }}>ðŸ“Œ Summary</h4>
                <SummaryCards summary={summaries[idx] || {}} />

                <h4 className="section-heading" style={{ marginTop: '12px' }}>ðŸ§¾ Raw OCR Lines</h4>
                <RawPanel lines={tables[idx]?.raw || []} />
              </div>
            );
          })}
        </section>
      )}
    </div>
  );
};

export default OCRUpload;
