import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../App.css';

const OCRUpload = ({ auth, setAuth, isAdmin = false }) => {
  const navigate = useNavigate();
  const [files, setFiles] = useState([]);
  const [tables, setTables] = useState([]);     // [{columns, rows, sections}]
  const [rawTexts, setRawTexts] = useState([]); // plain text per file for fallback parse
  const [loading, setLoading] = useState(false);
  const [downloadReady, setDownloadReady] = useState(false);

  const [isSignup, setIsSignup] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');

  const API_BASE = process.env.REACT_APP_API_BASE || 'http://localhost:5000';
  const api = axios.create({ baseURL: API_BASE });

  api.interceptors.request.use((config) => {
    if (auth?.token) config.headers.Authorization = `Bearer ${auth.token}`;
    return config;
  });

  api.interceptors.response.use(
    (response) => response,
    (error) => {
      if ([401, 403].includes(error.response?.status)) {
        localStorage.clear();
        setAuth(null);
        alert('‚ö†Ô∏è Session expired or access denied. Please log in again.');
      }
      return Promise.reject(error);
    }
  );

  useEffect(() => {
    if (auth?.token && !isAdmin) navigate('/', { replace: true });
  }, [auth, isAdmin, navigate]);

  const handleSignup = async () => {
    try {
      const res = await api.post('/auth/signup', { username, password });
      setMessage(res.data.message || 'Signup successful!');
      const loginRes = await api.post('/auth/login', { username, password });
      setAuth({
        token: loginRes.data.token,
        username: loginRes.data.username,
        role: loginRes.data.role,
      });
      localStorage.setItem('auth', JSON.stringify({
        token: loginRes.data.token,
        username: loginRes.data.username,
        role: loginRes.data.role,
      }));
    } catch (err) {
      setMessage('Signup failed: ' + (err.response?.data?.error || err.message));
    }
  };

  const handleLogin = async () => {
    try {
      const res = await api.post('/auth/login', { username, password });
      if (res.data.token) {
        setAuth({
          token: res.data.token,
          username: res.data.username,
          role: res.data.role,
        });
        localStorage.setItem('auth', JSON.stringify({
          token: res.data.token,
          username: res.data.username,
          role: res.data.role,
        }));
        setMessage('Login successful!');
      } else {
        setMessage('Invalid credentials');
      }
    } catch (err) {
      setMessage('Login failed: ' + (err.response?.data?.error || err.message));
    }
  };

  const handleFileChange = (e) => {
    const selected = Array.from(e.target.files);
    if (selected.length > 10) {
      alert('‚ö†Ô∏è You can upload up to 10 files only.');
      return;
    }
    setFiles(selected);
    setTables([]);
    setRawTexts([]);
    setDownloadReady(false);
  };

  // Fallback parser for plain text to rows, using known columns
  function parsePlainTextToRows(text, expectedColumns) {
    if (!text || !expectedColumns?.length) return [];
    const lines = text.split(/\r?\n/).map(l => l.trimRight());

    // Prefer markdown-like sections first
    const headerLineIdx = lines.findIndex(
      ln => ln.includes('|') && expectedColumns.every(c => ln.toLowerCase().includes(c.split(' ').toLowerCase()))
    );

    if (headerLineIdx !== -1) {
      // Skip next separator if present
      let start = headerLineIdx + 1;
      if (lines[start] && setOf(lines[start].replace(/\|/g, '').trim()) <= setOf('-: ')) start++;
      const out = [];
      for (let i = start; i < lines.length; i++) {
        const ln = lines[i].trim();
        if (!ln) continue;
        if (/^Billing Type Totals:/i.test(ln) || /<GRAND TOTALS>/i.test(ln)) break;
        if (!ln.includes('|')) break;
        let parts = ln.split('|').map(s => s.trim());
        parts = stripEmptyEdges(parts);
        if (parts.length > expectedColumns.length) parts = parts.slice(0, expectedColumns.length);
        if (parts.length < expectedColumns.length) parts = parts.concat(Array(expectedColumns.length - parts.length).fill(''));
        const row = {};
        expectedColumns.forEach((c, idx) => row[c] = parts[idx] ?? '');
        if (Object.values(row).some(v => v)) out.push(row);
      }
      return out;
    }

    // Monospace fallback
    const out = [];
    for (const raw of lines) {
      const ln = raw.trim();
      if (!ln || /^Billing Type Totals:/i.test(ln) || /<GRAND TOTALS>/i.test(ln)) continue;
      if (/^[-:| ]+$/.test(ln)) continue;
      let parts = ln.includes('|') ? ln.split('|').map(s => s.trim()) : ln.split(/\s{2,}/).map(s => s.trim());
      parts = parts.filter(Boolean);
      if (parts.length < 3) continue;
      if (parts.length > expectedColumns.length) parts = parts.slice(0, expectedColumns.length);
      if (parts.length < expectedColumns.length) parts = parts.concat(Array(expectedColumns.length - parts.length).fill(''));
      const row = {};
      expectedColumns.forEach((c, idx) => row[c] = parts[idx] ?? '');
      if (row['Type'] || row['Member I.D. Number'] || row['Patient Identification'] || row['Total Due']) {
        out.push(row);
      }
    }
    return out;
  }

  function stripEmptyEdges(parts) {
    const arr = parts.slice();
    while (arr.length && arr === '') arr.shift();
    while (arr.length && arr[arr.length - 1] === '') arr.pop();
    return arr;
  }
  function setOf(s) {
    return new Set(s.split(''));
  }

  const handleUploadAll = async () => {
    if (!files.length) return;
    setLoading(true);
    try {
      if (auth?.role?.toLowerCase() === 'admin') {
        try { await api.post('/api/reset'); } catch {}
      }
      const outTables = [];
      const texts = [];

      for (const [idx, file] of files.entries()) {
        const formData = new FormData();
        formData.append('file', file);
        const res = await api.post('/api/ocr', formData, {
          headers: {
            'Content-Type': 'multipart/form-data',
            Authorization: `Bearer ${auth?.token}`,
          },
        });

        const report = res.data.report || res.data.metadata?.report || null;
        const fallbackText =
          res.data.report_text ||
          res.data.text ||
          res.data.metadata?.report_text ||
          res.data.metadata?.full_text ||
          '';

        outTables[idx] = (report && report.columns && Array.isArray(report.rows))
          ? report
          : { columns: [], rows: [], sections: [] };

        texts[idx] = typeof fallbackText === 'string' ? fallbackText : '';

        // Optional server export
        try {
          await api.post('/api/export-excel', { metadata: {} }, { headers: { Authorization: `Bearer ${auth?.token}` } });
        } catch {}
      }

      setTables(outTables);
      setRawTexts(texts);
      setDownloadReady(true);
    } catch (err) {
      console.error('Upload failed:', err);
      alert('‚ö†Ô∏è Upload failed. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  if (!auth?.token && !isAdmin) {
    return (
      <div className="container">
        <h2>{isSignup ? 'Signup' : 'Login'}</h2>
        <input type="text" placeholder="Username" value={username} onChange={(e) => setUsername(e.target.value)} className="auth-input" />
        <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} className="auth-input" />
        {isSignup ? (
          <button className="auth-btn" onClick={handleSignup}>Signup</button>
        ) : (
          <button className="auth-btn" onClick={handleLogin}>Login</button>
        )}
        <button className="toggle-btn" onClick={() => { setIsSignup(!isSignup); setMessage(''); }}>
          {isSignup ? 'Already have an account? Login' : 'No account? Signup'}
        </button>
        {message && <p className="auth-message">{message}</p>}
      </div>
    );
  }

  return (
    <div className="container">
      <header>
        <h2 className="main-heading">
          {isAdmin ? 'üìÇ Admin OCR Upload Tool' : 'üìÑ File OCR Extraction Tool'}
        </h2>
      </header>

      <section className="upload-section">
        <label htmlFor="file-upload" className="upload-label">Upload up to 10 files:</label>
        <input type="file" id="file-upload" accept=".pdf,.jpg,.jpeg,.png,.doc,.docx" multiple onChange={handleFileChange} />
        <div className="button-row">
          <button className="upload-btn" onClick={handleUploadAll}>Upload All</button>
          {downloadReady && (
            <button className="download-btn" onClick={() => alert('Server export kept for compatibility.')}>‚¨áÔ∏è Download Excel</button>
          )}
        </div>
      </section>

      {files.length > 0 && (
        <section className="file-preview">
          <h3 className="section-heading">üìÅ Selected Files</h3>
          <ul className="file-list">
            {files.map((file, i) => (<li key={i}>üìé {file.name}</li>))}
          </ul>
        </section>
      )}

      {loading && <p className="loading-text">‚è≥ Extracting table...</p>}

      {/* Only render the table (no FieldValue/columns/rows/sections metadata) */}
      {tables.length > 0 && (
        <section className="results-section">
          <h3 className="section-heading">üìä Aging Report</h3>
          {files.map((file, idx) => {
            let columns = tables[idx]?.columns || [];
            let rows = tables[idx]?.rows || [];
            // Fallback: if rows empty, build from raw text
            if ((!rows || rows.length === 0) && columns && columns.length) {
              rows = parsePlainTextToRows(rawTexts[idx] || '', columns);
            }
            const hasData = columns.length > 0;
            return (
              <div className="excel-grid-wrapper" key={`grid-${idx}`}>
                <div className="excel-grid-header">
                  <h4 className="file-title">{file.name}</h4>
                </div>
                {!hasData ? (
                  <div className="no-data">No headers detected</div>
                ) : (
                  <div className="excel-grid compact">
                    <div className="excel-grid-scroll">
                      <table className="excel-table">
                        <thead>
                          <tr>
                            {columns.map((col) => (
                              <th key={col} className="excel-th">{col}</th>
                            ))}
                          </tr>
                        </thead>
                        <tbody>
                          {(!rows || rows.length === 0) ? (
                            <tr>
                              <td className="excel-td" colSpan={columns.length} style={{ textAlign: 'center' }}>
                                No rows
                              </td>
                            </tr>
                          ) : (
                            rows.map((r, i) => (
                              <tr key={r?.row_id ?? i}>
                                {columns.map((col) => (
                                  <td key={col} className="excel-td">{r?.[col] ?? ''}</td>
                                ))}
                              </tr>
                            ))
                          )}
                        </tbody>
                      </table>
                    </div>
                  </div>
                )}
              </div>
            );
          })}
        </section>
      )}
    </div>
  );
};

export default OCRUpload;
