import React, { useState, useEffect, useMemo } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../App.css';

const OCRUpload = ({ auth, setAuth, isAdmin = false }) => {
  const navigate = useNavigate();
  const [files, setFiles] = useState([]);
  const [allMetadata, setAllMetadata] = useState([]);
  const [agingTables, setAgingTables] = useState([]); // NEW: per-file {columns, rows, sections}
  const [loading, setLoading] = useState(false);
  const [downloadReady, setDownloadReady] = useState(false);

  const [isSignup, setIsSignup] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');

  const API_BASE = process.env.REACT_APP_API_BASE || 'http://localhost:5000';

  // Axios instance
  const api = axios.create({ baseURL: API_BASE });

  // Add Authorization header automatically
  api.interceptors.request.use((config) => {
    if (auth?.token) config.headers.Authorization = `Bearer ${auth.token}`;
    return config;
  });

  // Handle session expiration globally
  api.interceptors.response.use(
    (response) => response,
    (error) => {
      if ([401, 403].includes(error.response?.status)) {
        localStorage.clear();
        setAuth(null);
        alert('‚ö†Ô∏è Session expired or access denied. Please log in again.');
      }
      return Promise.reject(error);
    }
  );

  // Redirect if already logged in (only for non-admins)
  useEffect(() => {
    if (auth?.token && !isAdmin) {
      navigate('/', { replace: true });
    }
  }, [auth, isAdmin, navigate]);

  // --------------------------
  // Signup / Login
  // --------------------------
  const handleSignup = async () => {
    try {
      const res = await api.post('/auth/signup', { username, password });
      setMessage(res.data.message || 'Signup successful!');
      // auto-login after signup
      const loginRes = await api.post('/auth/login', { username, password });
      setAuth({
        token: loginRes.data.token,
        username: loginRes.data.username,
        role: loginRes.data.role,
      });
      localStorage.setItem(
        'auth',
        JSON.stringify({
          token: loginRes.data.token,
          username: loginRes.data.username,
          role: loginRes.data.role,
        })
      );
    } catch (err) {
      // FIX: use JS logical OR (||), not Python "or"
      setMessage('Signup failed: ' + (err.response?.data?.error || err.message));
    }
  };

  const handleLogin = async () => {
    try {
      const res = await api.post('/auth/login', { username, password });
      if (res.data.token) {
        setAuth({
          token: res.data.token,
          username: res.data.username,
          role: res.data.role,
        });
        localStorage.setItem(
          'auth',
          JSON.stringify({
            token: res.data.token,
            username: res.data.username,
            role: res.data.role,
          })
        );
        setMessage('Login successful!');
      } else {
        setMessage('Invalid credentials');
      }
    } catch (err) {
      setMessage('Login failed: ' + (err.response?.data?.error || err.message));
    }
  };

  // --------------------------
  // File Upload Handlers
  // --------------------------
  const handleFileChange = (e) => {
    const selected = Array.from(e.target.files);
    if (selected.length > 10) {
      alert('‚ö†Ô∏è You can upload up to 10 files only.');
      return;
    }
    setFiles(selected);
    setAllMetadata([]);
    setAgingTables([]); // NEW: reset tables list
    setDownloadReady(false);
  };

  const handleUploadAll = async () => {
    if (!files.length) return;
    setLoading(true);

    try {
      // Only Admin attempts to reset server-side accumulator
      if (auth?.role?.toLowerCase() === 'admin') {
        try {
          await api.post('/api/reset');
        } catch (resetErr) {
          console.warn('Reset skipped:', resetErr?.response?.status || resetErr?.message);
        }
      }

      const all = [];
      const tables = [];

      for (const [idx, file] of files.entries()) {
        const formData = new FormData();
        formData.append('file', file);

        const res = await api.post('/api/ocr', formData, {
          headers: {
            'Content-Type': 'multipart/form-data',
            Authorization: `Bearer ${auth?.token}`, // ‚úÖ preserve token
          },
        });

        const meta = res.data.metadata || {};
        meta['File Name'] = meta['File Name'] || file.name;
        meta['Patient Signature'] = res.data.patient_signature;
        meta['Physician Signature'] = res.data.physician_signature;

        // NEW: capture parsed report for Excel-like table
        const report = res.data.report || res.data.metadata?.report || null;
        tables[idx] = (report && report.columns && report.rows)
          ? report
          : { columns: [], rows: [], sections: [] };

        all.push(meta);

        // Save metadata on server for Excel export
        await api.post(
          '/api/export-excel',
          { metadata: meta },
          {
            headers: {
              Authorization: `Bearer ${auth?.token}`,
            },
          }
        );
      }

      setAllMetadata(all);
      setAgingTables(tables); // NEW: set per-file tables
      setDownloadReady(true);
    } catch (err) {
      console.error('Upload failed:', err);
      alert('‚ö†Ô∏è Upload failed. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleDownloadExcel = async () => {
    try {
      const res = await api.get('/api/export-excel', {
        responseType: 'blob',
        headers: {
          Authorization: `Bearer ${auth?.token}`, // ‚úÖ preserve token
        },
      });
      const url = URL.createObjectURL(new Blob([res.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', 'metadata_output.xlsx');
      document.body.appendChild(link);
      link.click();
      link.remove();
    } catch (err) {
      console.error('Excel download failed:', err);
      alert('‚ö†Ô∏è Download failed. Please try again.');
    }
  };

  // --------------------------
  // Excel-like table component
  // --------------------------
  const ExcelGrid = ({ title, columns, rows }) => {
    // Optional CSV export
    const csv = useMemo(() => {
      if (!Array.isArray(columns) || !columns.length || !Array.isArray(rows)) return '';
      const escape = (v) => {
        const s = String(v ?? '');
        const needs = /[",\n]/.test(s);
        const esc = s.replace(/"/g, '""');
        return needs ? `"${esc}"` : esc;
      };
      const header = columns.map(escape).join(',');
      const body = rows.map(r => columns.map(c => escape(r?.[c])).join(',')).join('\n');
      return `${header}\n${body}`;
    }, [columns, rows]);

    const downloadCSV = () => {
      if (!csv) return;
      const blob = new Blob([csv], { type: 'text/csv;charset=utf-8;' });
      const url = URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.setAttribute('download', `${title || 'aging_report'}.csv`);
      document.body.appendChild(a);
      a.click();
      a.remove();
    };

    const hasData = Array.isArray(columns) && columns.length > 0 && Array.isArray(rows);

    return (
      <div className="excel-grid-wrapper">
        <div className="excel-grid-header">
          <h4 className="file-title">{title}</h4>
          <div className="excel-grid-actions">
            <button className="download-btn" onClick={downloadCSV} disabled={!csv}>
              ‚¨áÔ∏è Download CSV
            </button>
          </div>
        </div>

        {!hasData ? (
          <div className="no-data">No table rows parsed</div>
        ) : (
          <div className="excel-grid compact">
            <div className="excel-grid-scroll">
              <table className="excel-table">
                <thead>
                  <tr>
                    {columns.map((col) => (
                      <th key={col} className="excel-th">{col}</th>
                    ))}
                  </tr>
                </thead>
                <tbody>
                  {rows.length === 0 ? (
                    <tr>
                      <td className="excel-td" colSpan={columns.length} style={{ textAlign: 'center' }}>
                        No rows
                      </td>
                    </tr>
                  ) : (
                    rows.map((r, i) => (
                      <tr key={r?.row_id ?? i}>
                        {columns.map((col) => (
                          <td key={col} className="excel-td">{r?.[col] ?? ''}</td>
                        ))}
                      </tr>
                    ))
                  )}
                </tbody>
              </table>
            </div>
          </div>
        )}
      </div>
    );
  };

  // --------------------------
  // Render
  // --------------------------
  if (!auth?.token && !isAdmin) {
    return (
      <div className="container">
        <h2>{isSignup ? 'Signup' : 'Login'}</h2>
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          className="auth-input"
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          className="auth-input"
        />

        {isSignup ? (
          <button className="auth-btn" onClick={handleSignup}>Signup</button>
        ) : (
          <button className="auth-btn" onClick={handleLogin}>Login</button>
        )}

        <button
          className="toggle-btn"
          onClick={() => {
            setIsSignup(!isSignup);
            setMessage('');
          }}
        >
          {isSignup ? 'Already have an account? Login' : 'No account? Signup'}
        </button>

        {message && <p className="auth-message">{message}</p>}
      </div>
    );
  }

  return (
    <div className="container">
      <header>
        <h2 className="main-heading">
          {isAdmin ? 'üìÇ Admin OCR Upload Tool' : 'üìÑ File OCR Extraction Tool'}
        </h2>
      </header>

      <section className="upload-section">
        <label htmlFor="file-upload" className="upload-label">
          Upload up to 10 files:
        </label>
        <input
          type="file"
          id="file-upload"
          accept=".pdf,.jpg,.jpeg,.png,.doc,.docx"
          multiple
          onChange={handleFileChange}
        />
        <div className="button-row">
          <button className="upload-btn" onClick={handleUploadAll}>
            Upload All
          </button>
          {downloadReady && (
            <button className="download-btn" onClick={handleDownloadExcel}>
              ‚¨áÔ∏è Download Excel
            </button>
          )}
        </div>
      </section>

      {files.length > 0 && (
        <section className="file-preview">
          <h3 className="section-heading">üìÅ Selected Files</h3>
          <ul className="file-list">
            {files.map((file, i) => (
              <li key={i}>üìé {file.name}</li>
            ))}
          </ul>
        </section>
      )}

      {loading && <p className="loading-text">‚è≥ Extracting metadata...</p>}

      {allMetadata.length > 0 && (
        <>
          {/* Existing key/value metadata table */}
          <section className="results-section">
            <h3 className="section-heading">üìã Extracted Metadata</h3>
            {allMetadata.map((meta, fileIndex) => (
              <div key={fileIndex} className="table-container">
                <h4 className="file-title">{meta['File Name']}</h4>
                <table className="metadata-table">
                  <thead>
                    <tr>
                      <th>Field</th>
                      <th>Value</th>
                    </tr>
                  </thead>
                  <tbody>
                    {Object.entries(meta).map(([key, value], i) => (
                      <tr key={i}>
                        <td className="field-cell">{key}</td>
                        <td className="value-cell">
                          {(key === 'Patient Signature' || key === 'Physician Signature') &&
                          typeof value === 'string' &&
                          value.includes('/signatures/') ? (
                            <img
                              src={`${API_BASE}${value}`}
                              alt={key}
                              className="signature-img"
                            />
                          ) : (
                            String(value ?? '')
                          )}
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            ))}
          </section>

          {/* NEW: Excel-like tables for parsed Aging Report rows */}
          <section className="results-section">
            <h3 className="section-heading">üìä Aging Report (Excel-style)</h3>
            {files.map((file, idx) => {
              const table = agingTables[idx] || { columns: [], rows: [], sections: [] };
              return (
                <ExcelGrid
                  key={`grid-${idx}`}
                  title={file.name}
                  columns={table.columns || []}
                  rows={table.rows || []}
                />
              );
            })}
          </section>
        </>
      )}
    </div>
  );
};

export default OCRUpload;
