// OCRUpload.jsx — tables-aware Vision OCR UI
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../App.css';

const OFFICE_COLUMNS = ["Section","Code","Description","Fee"];
const ICD10_COLUMNS  = ["Type","Code","Diagnosis"];
const NURSING_COLUMNS= ["MRN","Visit","Patient","Age/DOB","Location","Authored (date/time)","Note summary"];

const OCRUpload = ({ auth, setAuth, isAdmin = false }) => {
  const navigate = useNavigate();
  const [files, setFiles] = useState([]);
  const [results, setResults] = useState([]); // [{ filename, default, office, icd10, nursing, signatures, legacyMeta }]
  const [loading, setLoading] = useState(false);

  const [isSignup, setIsSignup] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage]   = useState('');

  const API_BASE = process.env.REACT_APP_API_BASE || 'http://localhost:5000';

  const api = axios.create({ baseURL: API_BASE });

  api.interceptors.request.use((config) => {
    if (auth?.token) config.headers.Authorization = `Bearer ${auth.token}`;
    return config;
  });
  api.interceptors.response.use(
    (response) => response,
    (error) => {
      if ([401, 403].includes(error.response?.status)) {
        localStorage.clear();
        setAuth(null);
        alert('⚠️ Session expired or access denied. Please log in again.');
      }
      return Promise.reject(error);
    }
  );

  useEffect(() => {
    if (auth?.token && !isAdmin) {
      navigate('/', { replace: true });
    }
  }, [auth, isAdmin, navigate]);

  const handleSignup = async () => {
    try {
      const res = await api.post('/auth/signup', { username, password });
      setMessage(res.data.message || 'Signup successful!');
      const loginRes = await api.post('/auth/login', { username, password });
      const { token, username: un, role } = loginRes.data;
      setAuth({ token, username: un, role });
      localStorage.setItem('auth', JSON.stringify({ token, username: un, role }));
    } catch (err) {
      setMessage('Signup failed: ' + (err.response?.data?.error || err.message));
    }
  };

  const handleLogin = async () => {
    try {
      const res = await api.post('/auth/login', { username, password });
      if (res.data.token) {
        const { token, username: un, role } = res.data;
        setAuth({ token, username: un, role });
        localStorage.setItem('auth', JSON.stringify({ token, username: un, role }));
        setMessage('Login successful!');
      } else {
        setMessage('Invalid credentials');
      }
    } catch (err) {
      setMessage('Login failed: ' + (err.response?.data?.error || err.message));
    }
  };

  const handleFileChange = (e) => {
    const selected = Array.from(e.target.files || []);
    if (selected.length > 10) {
      alert('⚠️ You can upload up to 10 files only.');
      return;
    }
    setFiles(selected);
    setResults([]);
  };

  function normalizeRowsMaybe(objOrArray) {
    // API may send either {columns, rows} or raw array of rows
    if (Array.isArray(objOrArray)) return objOrArray;
    if (objOrArray && Array.isArray(objOrArray.rows)) return objOrArray.rows;
    return [];
  }

  const handleUploadAll = async () => {
    if (!files.length) return;
    setLoading(true);
    try {
      const out = [];

      for (const file of files) {
        const formData = new FormData();
        formData.append('file', file);

        const res = await api.post('/api/ocr', formData, {
          headers: { 'Content-Type': 'multipart/form-data' },
        });

        // New Vision OCR tables shape
        if (res.data?.office_charges || res.data?.icd10 || res.data?.nursing_notes || res.data?.default) {
          const office  = normalizeRowsMaybe(res.data.office_charges);
          const icd10   = normalizeRowsMaybe(res.data.icd10);
          const nursing = normalizeRowsMaybe(res.data.nursing_notes);
          out.push({
            filename: res.data.filename || file.name,
            default: res.data.default || (nursing.length ? 'nursing' : (office.length ? 'office' : 'icd10')),
            office,
            icd10,
            nursing,
            signatures: {
              patient: res.data.patient_signature || null,
              physician: res.data.physician_signature || null,
            },
            legacyMeta: null,
          });
        } else {
          // Legacy metadata dict shape
          const meta = res.data?.metadata || {};
          if (!meta['File Name']) meta['File Name'] = file.name;
          if (res.data.patient_signature) meta['Patient Signature'] = res.data.patient_signature;
          if (res.data.physician_signature) meta['Physician Signature'] = res.data.physician_signature;

          out.push({
            filename: meta['File Name'],
            default: 'legacy',
            office: [],
            icd10: [],
            nursing: [],
            signatures: {
              patient: meta['Patient Signature'] || null,
              physician: meta['Physician Signature'] || null,
            },
            legacyMeta: meta,
          });
        }
      }

      setResults(out);
    } catch (err) {
      console.error('Upload failed:', err);
      alert('⚠️ Upload failed. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  function Grid({ title, columns, rows }) {
    return (
      <div className="excel-grid-wrapper">
        <div className="excel-grid-header"><h4 className="file-title">{title}</h4></div>
        <div className="excel-grid compact">
          <div className="excel-grid-scroll">
            <table className="excel-table">
              <thead>
                <tr>{columns.map(col => <th key={col} className="excel-th">{col}</th>)}</tr>
              </thead>
              <tbody>
                {rows && rows.length > 0 ? rows.map((r, i) => (
                  <tr key={i}>
                    {columns.map(col => <td key={col} className="excel-td">{r?.[col] ?? ''}</td>)}
                  </tr>
                )) : (
                  <tr><td colSpan={columns.length} style={{ textAlign: 'center' }}>No rows</td></tr>
                )}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    );
  }

  function LegacyMetaTable({ meta }) {
    const entries = Object.entries(meta || {});
    return (
      <div className="table-container">
        <table className="metadata-table">
          <thead><tr><th>Field</th><th>Value</th></tr></thead>
          <tbody>
            {entries.map(([key, value], idx) => (
              <tr key={idx}>
                <td className="field-cell">{key}</td>
                <td className="value-cell">
                  {(key === 'Patient Signature' || key === 'Physician Signature') &&
                  typeof value === 'string' && value.includes('/signatures/') ? (
                    <img src={`${API_BASE}${value}`} alt={key} className="signature-img" />
                  ) : (
                    String(value ?? '')
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  }

  function ResultBlock({ item }) {
    // Tabs: office | icd10 | nursing | legacy
    const hasOffice  = item.office.length > 0;
    const hasICD10   = item.icd10.length > 0;
    const hasNursing = item.nursing.length > 0;
    const initialTab = item.default === 'legacy' ? 'legacy'
                      : item.default === 'nursing_notes' ? 'nursing'
                      : item.default === 'office_charges' ? 'office'
                      : item.default;
    const [tab, setTab] = useState(initialTab);

    useEffect(() => { setTab(initialTab); }, [initialTab]);

    return (
      <div className="table-container">
        <div className="excel-grid-header">
          <h4 className="file-title">{item.filename}</h4>
          <div className="tab-row">
            {hasOffice && <button className={`tab-btn ${tab==='office' ? 'active' : ''}`} onClick={() => setTab('office')}>Office</button>}
            {hasICD10 && <button className={`tab-btn ${tab==='icd10' ? 'active' : ''}`} onClick={() => setTab('icd10')}>ICD-10</button>}
            {hasNursing && <button className={`tab-btn ${tab==='nursing' ? 'active' : ''}`} onClick={() => setTab('nursing')}>Nursing</button>}
            {item.legacyMeta && <button className={`tab-btn ${tab==='legacy' ? 'active' : ''}`} onClick={() => setTab('legacy')}>Metadata</button>}
          </div>
        </div>

        {tab === 'office' && <Grid title="Office Charges" columns={OFFICE_COLUMNS} rows={item.office} />}
        {tab === 'icd10'  && <Grid title="ICD-10" columns={ICD10_COLUMNS} rows={item.icd10} />}
        {tab === 'nursing'&& <Grid title="Nursing Notes" columns={NURSING_COLUMNS} rows={item.nursing} />}
        {tab === 'legacy' && <LegacyMetaTable meta={item.legacyMeta} />}

        {(item.signatures?.patient || item.signatures?.physician) && (
          <div className="signatures-row" style={{ marginTop: 8 }}>
            {item.signatures.patient && (
              <div className="sig-box">
                <div className="sig-label">Patient Signature</div>
                <img src={`${API_BASE}${item.signatures.patient}`} alt="Patient Signature" className="signature-img" />
              </div>
            )}
            {item.signatures.physician && (
              <div className="sig-box">
                <div className="sig-label">Physician Signature</div>
                <img src={`${API_BASE}${item.signatures.physician}`} alt="Physician Signature" className="signature-img" />
              </div>
            )}
          </div>
        )}
      </div>
    );
  }

  if (!auth?.token && !isAdmin) {
    return (
      <div className="container">
        <h2>{isSignup ? 'Signup' : 'Login'}</h2>
        <input type="text" placeholder="Username" value={username} onChange={(e) => setUsername(e.target.value)} className="auth-input" />
        <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} className="auth-input" />
        {isSignup ? (<button className="auth-btn" onClick={handleSignup}>Signup</button>) : (<button className="auth-btn" onClick={handleLogin}>Login</button>)}
        <button className="toggle-btn" onClick={() => { setIsSignup(!isSignup); setMessage(''); }}>
          {isSignup ? 'Already have an account? Login' : 'No account? Signup'}
        </button>
        {message && <p className="auth-message">{message}</p>}
      </div>
    );
  }

  return (
    <div className="container">
      <header>
        <h2 className="main-heading">{isAdmin ? '📂 Admin OCR Upload Tool' : '📄 File OCR Extraction Tool'}</h2>
      </header>

      <section className="upload-section">
        <label htmlFor="file-upload" className="upload-label">Upload up to 10 files:</label>
        <input type="file" id="file-upload" accept=".pdf,.jpg,.jpeg,.png,.doc,.docx" multiple onChange={handleFileChange} />
        <div className="button-row">
          <button className="upload-btn" onClick={handleUploadAll}>Upload All</button>
        </div>
      </section>

      {files.length > 0 && (
        <section className="file-preview">
          <h3 className="section-heading">📁 Selected Files</h3>
          <ul className="file-list">{files.map((file, i) => (<li key={i}>📎 {file.name}</li>))}</ul>
        </section>
      )}

      {loading && <p className="loading-text">⏳ Extracting tables...</p>}

      {results.length > 0 && (
        <section className="results-section">
          <h3 className="section-heading">📊 Parsed Tables</h3>
          {results.map((item, idx) => (<ResultBlock key={idx} item={item} />))}
        </section>
      )}
    </div>
  );
};

export default OCRUpload;
