import { Link, Outlet, useLocation } from "react-router-dom";
import { LayoutDashboard, Users, List, Settings } from "lucide-react";

export default function AdminDashboard() {
  const location = useLocation();

  const navItems = [
    { path: "/admin/overview", label: "Overview", icon: <LayoutDashboard size={18} /> },
    { path: "/admin/users", label: "Users", icon: <Users size={18} /> },
    { path: "/admin/logs", label: "Logs", icon: <List size={18} /> },
    { path: "/admin/settings", label: "Settings", icon: <Settings size={18} /> },
  ];

  return (
    <div className="flex h-screen bg-gradient-to-br from-gray-900 via-gray-800 to-black">
      {/* Sidebar */}
      <aside
        className="
          w-64 flex flex-col
          bg-white/10 
          backdrop-blur-2xl 
          border-r border-white/20 
          shadow-[0_8px_32px_0_rgba(31,38,135,0.37)]
          p-6
        "
      >
        {/* Logo / Title */}
        <div className="text-2xl font-bold text-white mb-8 tracking-wide drop-shadow-lg text-center">
          Admin Panel
        </div>

        {/* Navigation */}
        <nav className="flex flex-col gap-4">
          {navItems.map((item) => (
            <Link
              key={item.path}
              to={item.path}
              className={`flex items-center gap-3 px-4 py-3 rounded-lg font-medium transition-all duration-300 w-full
                ${
                  location.pathname === item.path
                    ? "bg-white/30 text-white shadow-lg scale-[1.03]"
                    : "text-gray-300 hover:bg-white/20 hover:text-white hover:scale-[1.02]"
                }
              `}
            >
              {item.icon}
              <span>{item.label}</span>
            </Link>
          ))}
        </nav>
      </aside>

      {/* Main content */}
      <main className="flex-1 p-8 overflow-y-auto">
        <div className="backdrop-blur-xl bg-white/5 rounded-2xl border border-white/20 shadow-2xl p-6 h-full">
          <Outlet />
        </div>
      </main>
    </div>
  );
}
