// src/components/OCRUpload.jsx
import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "../App.css";

/* ---------------------
   Small UI helpers
   --------------------- */

/**
 * Render a value that might be:
 * - string / number
 * - array of strings/numbers
 * - array of objects (render nested table)
 * - object (render key/value table)
 */
function RenderMetaValue({ value }) {
  if (value == null) return <span className="no-data">NIL</span>;

  if (typeof value === "string" && value.startsWith("/signatures/")) {
    return (
      <a href={value} target="_blank" rel="noreferrer">
        <img src={value} alt="signature" className="signature-img" />
      </a>
    );
  }

  if (typeof value === "string" || typeof value === "number" || typeof value === "boolean") {
    return <span>{String(value)}</span>;
  }

  if (Array.isArray(value)) {
    if (value.length === 0) return <span className="no-data">No rows</span>;

    if (value.every((v) => typeof v === "string" || typeof v === "number" || typeof v === "boolean")) {
      return <span>{value.map((v) => String(v)).join(", ")}</span>;
    }

    if (value.every((v) => typeof v === "object" && v !== null)) {
      const keys = Array.from(new Set(value.flatMap((v) => (v ? Object.keys(v) : []))));
      return (
        <div className="excel-grid-wrapper" style={{ marginTop: 6 }}>
          <div className="excel-grid">
            <table className="excel-table">
              <thead>
                <tr>
                  {keys.map((k) => (
                    <th key={k} className="excel-th">{k}</th>
                  ))}
                </tr>
              </thead>
              <tbody>
                {value.map((row, i) => (
                  <tr key={i}>
                    {keys.map((k) => (
                      <td key={k} className="excel-td">{String((row && (row[k] ?? row[k.toLowerCase()])) ?? "")}</td>
                    ))}
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      );
    }

    return <span>{value.map((v) => JSON.stringify(v)).join(", ")}</span>;
  }

  if (typeof value === "object") {
    const entries = Object.entries(value);
    return (
      <table className="metadata-vertical" style={{ marginTop: 6 }}>
        <tbody>
          {entries.map(([k, v]) => (
            <tr key={k}>
              <th style={{ width: 160 }}>{k}</th>
              <td>{typeof v === "string" && v.startsWith("/signatures/") ? (
                <a href={v} target="_blank" rel="noreferrer">
                  <img src={v} alt={k} style={{ maxHeight: 48 }} />
                </a>
              ) : (
                String(v ?? "")
              )}</td>
            </tr>
          ))}
        </tbody>
      </table>
    );
  }

  return <span>{String(value)}</span>;
}

function MetadataVertical({ meta }) {
  if (!meta || Object.keys(meta).length === 0) return <div className="empty">No metadata</div>;
  const hideKeys = new Set([
    "Procedure / Billing (All Tables)",
    "Diagnosis (All Tables)",
    "Procedure / Billing",
    "Diagnosis",
  ]);

  return (
    <table className="metadata-vertical">
      <tbody>
        {Object.keys(meta).map((k) => (
          <tr key={k}>
            <th className="meta-key">{k}</th>
            <td className="meta-val">
              {hideKeys.has(k) ? (
                Array.isArray(meta[k]) ? (
                  <span>{meta[k].length} row(s) â€” shown in tables below</span>
                ) : (
                  <span>{String(meta[k] ?? "")}</span>
                )
              ) : (
                <RenderMetaValue value={meta[k]} />
              )}
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
}

/* Procedure / Diagnosis table helpers */

function normalizeProcRow(r) {
  if (!r) return ["", "", ""];
  if (Array.isArray(r)) return [r[0] ?? "", r[1] ?? "", r[2] ?? ""];
  const code = r.Code ?? r.code ?? r.Code?.toString?.() ?? r.code?.toString?.() ?? "";
  const desc = r.Description ?? r.description ?? r.Desc ?? r.desc ?? "";
  const fee = r.Fee ?? r.fee ?? r.Amount ?? r.amount ?? "";
  return [String(code).trim(), String(desc).trim(), String(fee).trim()];
}

function ProcedureTables({ tables }) {
  if (!tables || Object.keys(tables).length === 0) {
    return <div className="empty">No procedure / billing tables found.</div>;
  }
  return (
    <div className="procedures-root">
      {Object.entries(tables).map(([section, rows]) => (
        <div className="table-block" key={section} style={{ marginBottom: 12 }}>
          <h4 className="table-section-title">{section}</h4>
          <div className="excel-grid">
            <div className="excel-grid-scroll">
              <table className="excel-table">
                <thead>
                  <tr>
                    <th className="excel-th">Code</th>
                    <th className="excel-th">Description</th>
                    <th className="excel-th">Fee</th>
                  </tr>
                </thead>
                <tbody>
                  {rows && rows.length > 0 ? (
                    rows.map((r, i) => {
                      const [code, desc, fee] = normalizeProcRow(r);
                      return (
                        <tr key={i}>
                          <td className="excel-td">{code}</td>
                          <td className="excel-td">{desc}</td>
                          <td className="excel-td">{fee}</td>
                        </tr>
                      );
                    })
                  ) : (
                    <tr>
                      <td colSpan={3} style={{ textAlign: "center" }} className="excel-td">
                        No rows
                      </td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
}

function normalizeDiagRow(r) {
  if (!r) return ["ICD-10", "", ""];
  if (Array.isArray(r)) return [r[0] ?? "ICD-10", r[1] ?? "", r[2] ?? ""];
  const type = r.Type ?? r.type ?? "ICD-10";
  const code = r.Code ?? r.code ?? "";
  const desc = r.Diagnosis ?? r.diagnosis ?? r.Description ?? r.description ?? "";
  return [String(type).trim(), String(code).trim(), String(desc).trim()];
}

function DiagnosisTables({ tables, highlightCodes = new Set() }) {
  if (!tables || Object.keys(tables).length === 0) {
    return <div className="empty">No diagnosis tables found.</div>;
  }
  return (
    <div className="diagnosis-root">
      {Object.entries(tables).map(([section, rows]) => (
        <div className="table-block" key={section} style={{ marginBottom: 12 }}>
          <h4 className="table-section-title">{section}</h4>
          <div className="excel-grid">
            <div className="excel-grid-scroll">
              <table className="excel-table">
                <thead>
                  <tr>
                    <th className="excel-th">Type</th>
                    <th className="excel-th">Code</th>
                    <th className="excel-th">Diagnosis</th>
                  </tr>
                </thead>
                <tbody>
                  {rows && rows.length > 0 ? (
                    rows.map((r, i) => {
                      const [type, code, desc] = normalizeDiagRow(r);
                      const isMatch = highlightCodes.has(String(code).trim());
                      return (
                        <tr key={i} className={isMatch ? "highlight-row" : ""}>
                          <td className="excel-td">{type}</td>
                          <td className="excel-td">{code}</td>
                          <td className="excel-td">{desc}</td>
                        </tr>
                      );
                    })
                  ) : (
                    <tr>
                      <td colSpan={3} style={{ textAlign: "center" }} className="excel-td">
                        No rows
                      </td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
}

/* ---------------------
   PatientBlock + ResultBlock
   --------------------- */

function PatientBlock({ patient = {}, topProcedureTables = {}, topDiagnosisTables = {} }) {
  const icdRegex = /\b[A-Z][0-9]{2,3}(?:\.[0-9A-Z]+)?\b/g;
  const highlightCodes = new Set();
  Object.values(patient || {}).forEach((v) => {
    try {
      const str = typeof v === "string" ? v : JSON.stringify(v);
      const matches = (str || "").match(icdRegex);
      if (matches) matches.forEach((c) => highlightCodes.add(c.trim()));
    } catch (e) {}
  });

  const patientProcRows = patient["Procedure / Billing (All Tables)"] || patient["Procedure / Billing"] || [];
  const patientDiagRows = patient["Diagnosis (All Tables)"] || patient["Diagnosis"] || [];

  const combinedProcTables = {};
  if (patientProcRows && patientProcRows.length) {
    combinedProcTables["Patient Procedures"] = patientProcRows;
  }
  Object.entries(topProcedureTables || {}).forEach(([sec, rows]) => {
    combinedProcTables[sec] = (combinedProcTables[sec] || []).concat(rows || []);
  });

  const combinedDiagTables = {};
  if (patientDiagRows && patientDiagRows.length) {
    combinedDiagTables["Patient Diagnosis"] = patientDiagRows;
  }
  Object.entries(topDiagnosisTables || {}).forEach(([sec, rows]) => {
    combinedDiagTables[sec] = (combinedDiagTables[sec] || []).concat(rows || []);
  });

  return (
    <div className="patient-block">
      <div className="patient-header">
        <h4>Patient: {patient["Patient Name"] ?? patient["Patient"] ?? "Unknown"}</h4>
        <div style={{ color: "rgba(230,238,248,0.66)", fontSize: 12 }}>
          {patient["Date of Birth"] && patient["Date of Birth"] !== "NIL" ? patient["Date of Birth"] : ""}
        </div>
      </div>

      <div className="patient-meta">
        <MetadataVertical meta={patient} />
      </div>

      <div className="patient-tables">
        <h5 className="section-title">Procedure / Billing (All Tables)</h5>
        <ProcedureTables tables={Object.keys(combinedProcTables).length ? combinedProcTables : topProcedureTables} />

        <h5 className="section-title" style={{ marginTop: 12 }}>
          Diagnosis (All Tables)
        </h5>
        <DiagnosisTables tables={Object.keys(combinedDiagTables).length ? combinedDiagTables : topDiagnosisTables} highlightCodes={highlightCodes} />

        {highlightCodes.size > 0 && (
          <div className="matched-note" style={{ marginTop: 8 }}>
            Highlighted diagnosis codes found in patient fields: {Array.from(highlightCodes).join(", ")}
          </div>
        )}
      </div>
    </div>
  );
}

function ResultBlock({ item }) {
  const patients = item.patients || item.patients_list || [];
  const procedureTables =
    item.procedure_tables || item.procedureTables || item.medical_billing_codes_grouped || {} ;
  const diagnosisTables = item.diagnosis_tables || item.diagnosisTables || item.diagnosis_codes_grouped || {} ;

  const procTables = (Object.keys(procedureTables || {}).length === 0 && Array.isArray(item.medical_billing_codes))
    ? { Procedures: item.medical_billing_codes }
    : procedureTables;

  const diagTables = (Object.keys(diagnosisTables || {}).length === 0 && Array.isArray(item.diagnosis_codes))
    ? { Diagnosis: item.diagnosis_codes }
    : diagnosisTables;

  const procCount = Object.values(procTables).reduce((s, a) => s + ((a && a.length) || 0), 0);
  const diagCount = Object.values(diagTables).reduce((s, a) => s + ((a && a.length) || 0), 0);

  return (
    <div className="result-block">
      <h2 className="file-title">{item.file_name || item.filename || "Uploaded file"}</h2>
      <div className="summary">
        {patients.length} patient(s) â€¢ {procCount} procedure rows â€¢ {diagCount} diagnosis rows
      </div>

      <section className="patients-list">
        {patients.length === 0 ? (
          <div className="empty">No patients found</div>
        ) : (
          patients.map((p, idx) => (
            <PatientBlock
              key={idx}
              patient={p}
              topProcedureTables={procTables}
              topDiagnosisTables={diagTables}
            />
          ))
        )}
      </section>

      <details style={{ marginTop: 12 }}>
        <summary>Raw response (debug)</summary>
        <pre style={{ maxHeight: 260, overflow: "auto", background: "#0b1220", color: "#cbd5e1", padding: 8 }}>{JSON.stringify(item, null, 2)}</pre>
      </details>
    </div>
  );
}

const OCRUpload = ({ auth, setAuth, isAdmin = false }) => {
  const navigate = useNavigate();
  const [files, setFiles] = useState([]);
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);

  const [isSignup, setIsSignup] = useState(false);
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState("");

  const API_BASE = process.env.REACT_APP_API_BASE || "http://localhost:5000";
  const api = axios.create({ baseURL: API_BASE });

  api.interceptors.request.use((config) => {
    if (auth?.token) config.headers.Authorization = `Bearer ${auth.token}`;
    return config;
  });
  api.interceptors.response.use(
    (response) => response,
    (error) => {
      if ([401, 403].includes(error.response?.status)) {
        localStorage.clear();
        setAuth(null);
        alert("âš ï¸ Session expired or access denied. Please log in again.");
      }
      return Promise.reject(error);
    }
  );

  useEffect(() => {
    if (auth?.token && !isAdmin) {
      navigate("/", { replace: true });
    }
  }, [auth, isAdmin, navigate]);

  const handleSignup = async () => {
    try {
      const res = await api.post("/auth/signup", { username, password });
      setMessage(res.data.message || "Signup successful!");
      const loginRes = await api.post("/auth/login", { username, password });
      const { token, username: un, role } = loginRes.data;
      setAuth({ token, username: un, role });
      localStorage.setItem("auth", JSON.stringify({ token, username: un, role }));
    } catch (err) {
      setMessage("Signup failed: " + (err.response?.data?.error || err.message));
    }
  };

  const handleLogin = async () => {
    try {
      const res = await api.post("/auth/login", { username, password });
      if (res.data.token) {
        const { token, username: un, role } = res.data;
        setAuth({ token, username: un, role });
        localStorage.setItem("auth", JSON.stringify({ token, username: un, role }));
        setMessage("Login successful!");
      } else {
        setMessage("Invalid credentials");
      }
    } catch (err) {
      setMessage("Login failed: " + (err.response?.data?.error || err.message));
    }
  };

  const handleFileChange = (e) => {
    const selected = Array.from(e.target.files || []);
    if (selected.length > 10) {
      alert("âš ï¸ You can upload up to 10 files only.");
      return;
    }
    setFiles(selected);
    setResults([]);
  };

  const buildItemFromResponse = (resData, file) => {
    if (!resData) {
      return {
        file_name: file.name,
        patients: [],
        procedure_tables: {},
        diagnosis_tables: {},
        metadata: {},
      };
    }

    if (resData.patients || resData.procedure_tables || resData.diagnosis_tables) {
      return {
        file_name: resData.file_name || resData.filename || file.name,
        patients: Array.isArray(resData.patients) ? resData.patients : [],
        procedure_tables: resData.procedure_tables || resData.procedureTables || {},
        diagnosis_tables: resData.diagnosis_tables || resData.diagnosisTables || {},
        metadata: resData.metadata || {},
        medical_billing_codes: resData.medical_billing_codes || resData.medicalBillingCodes || [],
        diagnosis_codes: resData.diagnosis_codes || resData.diagnosisCodes || [],
      };
    }

    if (resData.medical_billing_codes || resData.diagnosis_codes) {
      const procedures = {
        Procedures: Array.isArray(resData.medical_billing_codes)
          ? resData.medical_billing_codes.map((r) => (Array.isArray(r) ? r : [r.Code ?? r.code ?? "", r.Description ?? r.description ?? "", r.Fee ?? r.fee ?? ""]))
          : [],
      };
      const diags = {
        Diagnosis: Array.isArray(resData.diagnosis_codes)
          ? resData.diagnosis_codes.map((d) => (Array.isArray(d) ? d : [d.Type ?? d.type ?? "ICD-10", d.Code ?? d.code ?? "", d.Diagnosis ?? d.diagnosis ?? d.Description ?? d.description ?? ""]))
          : [],
      };
      return {
        file_name: resData.file_name || resData.filename || file.name,
        patients: Array.isArray(resData.patients) ? resData.patients : [],
        procedure_tables: procedures,
        diagnosis_tables: diags,
        metadata: resData.metadata || {},
        medical_billing_codes: resData.medical_billing_codes || [],
        diagnosis_codes: resData.diagnosis_codes || [],
      };
    }

    const meta = resData.metadata || resData;
    if (!meta["File Name"]) meta["File Name"] = file.name;
    return {
      file_name: meta["File Name"],
      patients: [],
      procedure_tables: {},
      diagnosis_tables: {},
      metadata: meta,
      medical_billing_codes: [],
      diagnosis_codes: [],
    };
  };

  const handleUploadAll = async () => {
    if (!files.length) return;
    setLoading(true);
    try {
      const out = [];
      for (const file of files) {
        const formData = new FormData();
        formData.append("file", file);
        const res = await api.post("/api/ocr", formData, {
          headers: { "Content-Type": "multipart/form-data" },
        });
        const item = buildItemFromResponse(res.data || {}, file);
        out.push(item);
      }
      setResults(out);
    } catch (err) {
      console.error("Upload failed:", err);
      alert("âš ï¸ Upload failed. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  if (!auth?.token && !isAdmin) {
    return (
      <div className="container">
        <h2>{isSignup ? "Signup" : "Login"}</h2>
        <input type="text" placeholder="Username" value={username} onChange={(e) => setUsername(e.target.value)} className="auth-input" />
        <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} className="auth-input" />
        {isSignup ? <button className="auth-btn" onClick={handleSignup}>Signup</button> : <button className="auth-btn" onClick={handleLogin}>Login</button>}
        <button className="toggle-btn" onClick={() => { setIsSignup(!isSignup); setMessage(""); }}>{isSignup ? "Already have an account? Login" : "No account? Signup"}</button>
        {message && <p className="auth-message">{message}</p>}
      </div>
    );
  }

  return (
    <div className="container">
      <header>
        <h2 className="main-heading">{isAdmin ? "ðŸ“‚ Admin OCR Upload Tool" : "ðŸ“„ File OCR Extraction Tool"}</h2>
      </header>

      <section className="upload-section">
        <label htmlFor="file-upload" className="upload-label">Upload up to 10 files:</label>
        <input type="file" id="file-upload" accept=".pdf,.jpg,.jpeg,.png,.doc,.docx" multiple onChange={handleFileChange} />
        <div className="button-row">
          <button className="upload-btn" onClick={handleUploadAll} disabled={loading}>{loading ? "Processing..." : "Upload All"}</button>
        </div>
      </section>

      {files.length > 0 && (
        <section className="file-preview">
          <h3 className="section-heading">ðŸ“ Selected Files</h3>
          <ul className="file-list">{files.map((file, i) => <li key={i}>ðŸ“Ž {file.name}</li>)}</ul>
        </section>
      )}

      {loading && <p className="loading-text">â³ Extracting tables...</p>}

      {results.length > 0 && (
        <section className="results-section">
          <h3 className="section-heading">ðŸ“Š Parsed Tables</h3>
          {results.map((item, idx) => <ResultBlock key={idx} item={item} />)}
        </section>
      )}
    </div>
  );
};

export default OCRUpload;
