// OCRUpload.jsx (strict parser version)
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import * as XLSX from 'xlsx';
import { saveAs } from 'file-saver';
import '../App.css';

/* ---------------------------------- schema --------------------------------- */
const FIXED_HEADERS = [
  "Patient Name","Pat Ocr","Responsible Party Name","Rep #","Doc",
  "B-Type","Seen","Service","Type","Office","Code","Description",
  "Amount","Balance","Claim","Loc","Entered","Who"
];

/* ------------------------------ parse helpers ------------------------------ */
const joinParts = (x) => (Array.isArray(x) ? x.join(" ") : String(x ?? "")).trim();

const looksLikeName   = (s) => /[A-Za-z]+,[A-Za-z]+/.test(s || "");
const looksLikeRep    = (s) => /\b\d{6,}-\d\b/.test(s || "");
// CPT/HCPCS-ish: 00402, 19318-50, J0585, C9290, 13100-XU etc.
const looksLikeCPT    = (s) => /\b(?:\d{4,5}(?:-[A-Z0-9]{2})?|[A-Z]\d{4})\b/.test(s || "");

// normalize "7.800.00" -> "7,800.00", "32.407.00" -> "32,407.00", "20.000.00" -> "20,000.00"
function normalizeAmountToken(tok) {
  tok = String(tok || "").replace(/,/g, "").trim();
  if (/^\d+\.\d{2}$/.test(tok)) {
    return Number(tok).toLocaleString(undefined, { minimumFractionDigits: 2, maximumFractionDigits: 2 });
  }
  const m = /^(\d+)\.(\d{3})\.?(\d{2})$/.exec(tok);
  if (m) {
    const val = `${m[1]}${m[2]}.${m[3]}`;
    return Number(val).toLocaleString(undefined, { minimumFractionDigits: 2, maximumFractionDigits: 2 });
  }
  const m2 = /^(\d{1,3}(?:\.\d{3})+)\.0?(\d{2})$/.exec(tok);
  if (m2) {
    const noDots = m2[1].replace(/\./g, "");
    const val = `${noDots}.${m2[2]}`;
    return Number(val).toLocaleString(undefined, { minimumFractionDigits: 2, maximumFractionDigits: 2 });
  }
  return tok;
}

function moneyTokens(line) {
  const raw = (line || "").match(/\d[\d.,]*\.\d{2}/g) || [];
  return raw.map(normalizeAmountToken);
}

// page chrome & headings to ignore
const BAD_LINE = /^(?:Date:|Time\b|FFCBilling|BeginDate:|Page\b|Page[A-Za-z]|===|=|AmountBalanceClaim|LocEnteredWho)\b/i;

function prefilterLines(rawTables) {
  return (rawTables || [])
    .map(joinParts)
    .map(s => s.replace(/\u00A0/g, " ").trim())
    .filter(s => s && !BAD_LINE.test(s));
}

// OFFICE is the last 3â€“4 digit number BEFORE the first code in a line
function extractOfficeFromLine(line) {
  if (!line) return "";
  const codeIdx = line.search(/\b(?:\d{4,5}(?:-[A-Z0-9]{2})?|[A-Z]\d{4})\b/);
  if (codeIdx < 0) return "";
  const left = line.slice(0, codeIdx);
  const nums = left.match(/\b\d{3,4}\b/g);
  return nums ? nums[nums.length - 1] : "";
}

// CLAIM/LOC/ENTERED/WHO tail: "18827 50 01-08-25 DF"
function extractTailFields(line) {
  if (!line) return {};
  const m = /\b(\d{4,6})\b\s+(\d{2})\s+(\d{2}[-/]\d{2}[-/]\d{2})\s+([A-Z]{2})\b/.exec(line);
  if (!m) return {};
  return { Claim: m[1], Loc: m[2], Entered: m[3], Who: m[4] };
}

/** Strict auto-parse: replicate the report row logic precisely. */
function autoParseRows(rawTables) {
  const rows = [];
  const lines = prefilterLines(rawTables);
  if (!lines.length) return rows;

  // rolling patient context (name/rep#)
  let ctx = { patient: "", rep: "" };

  for (let i = 0; i < lines.length; i++) {
    const L = lines[i];

    // carry patient context
    if (looksLikeName(L)) {
      ctx.patient = L;
      for (let k = 1; k <= 3 && i + k < lines.length; k++) {
        const cand = lines[i + k];
        if (looksLikeRep(cand)) {
          ctx.rep = cand.match(/\b\d{6,}-\d\b/)[0];
          break;
        }
      }
      continue;
    }
    if (looksLikeRep(L)) {
      ctx.rep = L.match(/\b\d{6,}-\d\b/)[0];
      continue;
    }

    // a data row starts where we see a Code AND there is an Amount within next 1â€“2 lines
    if (!looksLikeCPT(L)) continue;

    // capture codes from this line (can be multiple)
    const codes = (L.match(/\b(?:\d{4,5}(?:-[A-Z0-9]{2})?|[A-Z]\d{4})\b/g) || []);
    if (!codes.length) continue;

    // office number: last 3â€“4 digit number BEFORE first code token
    const office = extractOfficeFromLine(L);

    // description: next non-code, non-amount line
    let desc = "";
    let p = i + 1;
    while (p < Math.min(i + 4, lines.length)) {
      const cand = lines[p];
      if (!looksLikeCPT(cand) && moneyTokens(cand).length === 0) {
        desc = cand.replace(/\*/g, " ").replace(/\s+/g, " ").trim();
        p++;
        break;
      }
      p++;
    }

    // amounts: within next 1â€“2 lines after desc (or after code if no desc)
    let amount = "", balance = "";
    let qStart = desc ? p : (i + 1);
    let foundAmt = false;
    for (let q = qStart; q < Math.min(qStart + 3, lines.length); q++) {
      const mt = moneyTokens(lines[q]);
      if (mt.length) {
        amount = mt[0];
        if (mt.length > 1) balance = mt[1];
        foundAmt = true;
        // tail details could be on same line or the next line
        // look on this line first, else check next one
        let tail = extractTailFields(lines[q]);
        if (!tail && q + 1 < lines.length) tail = extractTailFields(lines[q + 1]);

        const row = Object.fromEntries(FIXED_HEADERS.map(h => [h, ""]));
        row["Patient Name"] = ctx.patient;
        row["Rep #"]        = ctx.rep;
        row["Office"]       = office;
        row["Code"]         = codes.join(" ");
        row["Description"]  = desc;
        row["Amount"]       = amount;
        row["Balance"]      = balance;
        if (tail) {
          row["Claim"]   = tail.Claim || "";
          row["Loc"]     = tail.Loc || "";
          row["Entered"] = tail.Entered || "";
          row["Who"]     = tail.Who || "";
        }

        rows.push(row);
        // advance i just past the amount/tail we consumed
        i = Math.max(i, q);
        break;
      }
    }

    // if we never found an amount close by, skip (prevents false positives)
    if (!foundAmt) {
      continue;
    }
  }

  // de-dupe identical patient+rep+code+amount
  const seen = new Set();
  return rows.filter(r => {
    const key = [r["Patient Name"], r["Rep #"], r["Code"], r["Amount"]].join("|");
    if (seen.has(key)) return false;
    seen.add(key);
    return true;
  });
}

/* ---------------------------------- component ------------------------------- */
const OCRUpload = ({ auth, setAuth, isAdmin = false }) => {
  const navigate = useNavigate();
  const [files, setFiles] = useState([]);
  const [tables, setTables] = useState([]);       // [{ columns, rows, raw }]
  const [summaries, setSummaries] = useState([]); // [{ Charges, Receipts, ... , NOTE }]
  const [loading, setLoading] = useState(false);
  const [downloadReady, setDownloadReady] = useState(false);

  // auth form
  const [isSignup, setIsSignup] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage]   = useState('');

  const API_BASE = process.env.REACT_APP_API_BASE || 'http://localhost:5000';
  const api = axios.create({ baseURL: API_BASE });

  api.interceptors.request.use((config) => {
    if (auth?.token) config.headers.Authorization = `Bearer ${auth.token}`;
    return config;
  });

  api.interceptors.response.use(
    (response) => response,
    (error) => {
      if ([401, 403].includes(error.response?.status)) {
        localStorage.clear();
        setAuth(null);
        alert('âš ï¸ Session expired or access denied. Please log in again.');
      }
      return Promise.reject(error);
    }
  );

  useEffect(() => {
    if (auth?.token && !isAdmin) navigate('/', { replace: true });
  }, [auth, isAdmin, navigate]);

  /* ------------------------------ auth handlers ----------------------------- */
  const handleSignup = async () => {
    try {
      const res = await api.post('/auth/signup', { username, password });
      setMessage(res.data.message || 'Signup successful!');
      const loginRes = await api.post('/auth/login', { username, password });
      const { token, username: un, role } = loginRes.data;
      setAuth({ token, username: un, role });
      localStorage.setItem('auth', JSON.stringify({ token, username: un, role }));
    } catch (err) {
      setMessage('Signup failed: ' + (err.response?.data?.error || err.message));
    }
  };

  const handleLogin = async () => {
    try {
      const res = await api.post('/auth/login', { username, password });
      if (res.data.token) {
        const { token, username: un, role } = res.data;
        setAuth({ token, username: un, role });
        localStorage.setItem('auth', JSON.stringify({ token, username: un, role }));
        setMessage('Login successful!');
      } else {
        setMessage('Invalid credentials');
      }
    } catch (err) {
      setMessage('Login failed: ' + (err.response?.data?.error || err.message));
    }
  };

  /* ------------------------------ file selection ---------------------------- */
  const handleFileChange = (e) => {
    const selected = Array.from(e.target.files || []);
    if (selected.length > 10) {
      alert('âš ï¸ You can upload up to 10 files only.');
      return;
    }
    setFiles(selected);
    setTables([]);
    setSummaries([]);
    setDownloadReady(false);
  };

  /* ------------------------------- upload parse ----------------------------- */
  const handleUploadAll = async () => {
    if (!files.length) return;
    setLoading(true);

    try {
      const outTables = [];
      const outSummaries = [];

      for (const [idx, file] of files.entries()) {
        const formData = new FormData();
        formData.append('file', file);

        const res = await api.post('/api/ocr', formData, {
          headers: { 'Content-Type': 'multipart/form-data' },
        });

        console.log('OCR response for', file.name, res.data);

        // backend shape from your logs
        const md        = res.data?.metadata || {};
        const report    = md.report || {};
        const rawTables = Array.isArray(md.tables) ? md.tables : [];
        const fieldArr  = Array.isArray(md.fields) ? md.fields : [];

        // rows: prefer backend; else strict auto-parse
        let rows = Array.isArray(report.rows) ? report.rows : [];
        if (!rows || rows.length === 0) {
          rows = autoParseRows(rawTables);
        }
        const columns = report.columns?.length ? report.columns : FIXED_HEADERS;

        // summary from fields; fallback parse from raw
        const wanted = [
          "Charges","Receipts","Commissions","Total Due Doctor","Credits","Debits",
          "Bal Fwd","Fin Chg","Direct Paymnt","Assess","Acct. Rec.","Total Entries","NOTE"
        ];
        const summaryObj = {};
        fieldArr.forEach(({ Field, Value }) => {
          const hit = wanted.find(w => w.toLowerCase() === String(Field || "").toLowerCase());
          if (hit) summaryObj[hit] = Value ?? "";
        });
        if (rawTables.length) {
          const joined = rawTables.map(joinParts).join(" ");
          const grab = (label) => {
            const m = new RegExp(`${label.replace(/[.*+?^${}()|[\\]\\\\]/g, '\\$&')}\\s*:?\\s*([0-9,.-]+)`, "i").exec(joined);
            return m ? m[1] : "";
          };
          wanted.forEach(k => {
            if (!summaryObj[k] || summaryObj[k] === "") {
              if (k === "NOTE") {
                const m = /NOTE\s*:?(.+?)(?:\s{2,}|$)/i.exec(joined);
                if (m) summaryObj["NOTE"] = m[1].trim();
              } else {
                summaryObj[k] = grab(k) || "";
              }
            }
          });
        }

        outTables[idx] = { columns, rows, raw: rawTables };
        outSummaries[idx] = summaryObj;
      }

      setTables(outTables);
      setSummaries(outSummaries);
      setDownloadReady(true);
    } catch (err) {
      console.error('Upload failed:', err);
      alert('âš ï¸ Upload failed. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  /* ----------------------------- Excel download ----------------------------- */
  const handleDownloadExcel = () => {
    const wb = XLSX.utils.book_new();

    // 1) Report rows per file
    tables.forEach((t, i) => {
      const data = (t.rows || []).map(r => {
        const o = {};
        (t.columns || FIXED_HEADERS).forEach(c => { o[c] = r?.[c] ?? ""; });
        return o;
      });
      const ws = XLSX.utils.json_to_sheet(data.length ? data : [{ Note: "No rows" }]);
      XLSX.utils.book_append_sheet(wb, ws, `ReportRows_${i + 1}`);
    });

    // 2) Fields sheet (one row per file)
    const fieldsRows = summaries.map((s, i) => ({ File: files[i]?.name || `File_${i + 1}`, ...s }));
    XLSX.utils.book_append_sheet(
      wb,
      XLSX.utils.json_to_sheet(fieldsRows.length ? fieldsRows : [{ Note: "No summaries" }]),
      "Fields"
    );

    // 3) Raw text per file
    tables.forEach((t, i) => {
      const raw = (t.raw || []).map((line, idx) => ({
        Line: idx + 1,
        Text: joinParts(line)
      }));
      const ws = XLSX.utils.json_to_sheet(raw.length ? raw : [{ Note: "No raw text" }]);
      XLSX.utils.book_append_sheet(wb, ws, `RawText_${i + 1}`);
    });

    const wbout = XLSX.write(wb, { type: 'array', bookType: 'xlsx' });
    saveAs(new Blob([wbout], { type: 'application/octet-stream' }), 'ocr_export.xlsx');
  };

  /* --------------------------------- UI bits -------------------------------- */
  const ExcelGrid = ({ title, columns, rows, summary }) => (
    <div className="excel-grid-wrapper">
      <div className="excel-grid-header">
        <h4 className="file-title">{title}</h4>
      </div>
      <div className="excel-grid compact">
        <div className="excel-grid-scroll">
          <table className="excel-table">
            <thead>
              <tr>
                {columns.map((col) => (
                  <th key={col} className="excel-th">{col}</th>
                ))}
              </tr>
            </thead>
            <tbody>
              {rows && rows.length > 0 ? (
                rows.map((r, i) => (
                  <tr key={i}>
                    {columns.map((col) => (
                      <td key={col} className="excel-td">{r?.[col] ?? ''}</td>
                    ))}
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan={columns.length} style={{ textAlign: 'center' }}>
                    No rows (showing Summary and Raw OCR below)
                  </td>
                </tr>
              )}
              {summary && Object.keys(summary).length > 0 && (
                <tr className="summary-row">
                  <td colSpan={columns.length} style={{ fontWeight: 'bold' }}>
                    Charges: {summary["Charges"] || 0} | Receipts: {summary["Receipts"] || 0} | 
                    Credits: {summary["Credits"] || 0} | Debits: {summary["Debits"] || 0} | 
                    Balance Fwd: {summary["Bal Fwd"] || 0} | Acct. Rec.: {summary["Acct. Rec."] || 0} | 
                    Total Entries: {summary["Total Entries"] || 0}
                    {summary["NOTE"] ? ` | Note: ${summary["NOTE"]}` : ""}
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );

  const SummaryCards = ({ summary }) => {
    const keys = [
      "Charges","Receipts","Commissions","Total Due Doctor","Credits","Debits",
      "Bal Fwd","Fin Chg","Direct Paymnt","Assess","Acct. Rec.","Total Entries","NOTE"
    ];
    return (
      <div className="grid grid-cols-2 md:grid-cols-3 gap-2 my-3">
        {keys.map(k => (
          <div key={k} className="border border-white/15 rounded-lg p-2">
            <div className="text-xs opacity-70">{k}</div>
            <div className="font-semibold">{summary?.[k] ? String(summary[k]) : "â€”"}</div>
          </div>
        ))}
      </div>
    );
  };

  const RawPanel = ({ lines }) => (
    <div className="h-64 overflow-auto border border-white/15 rounded-xl p-3 text-xs leading-6 my-2">
      {(lines?.length ? lines : ["(no raw text)"]).map((t, i) => (
        <div key={i} className="whitespace-pre-wrap">
          {i + 1}. {joinParts(t)}
        </div>
      ))}
    </div>
  );

  /* --------------------------------- gates/UI -------------------------------- */
  if (!auth?.token && !isAdmin) {
    return (
      <div className="container">
        <h2>{isSignup ? 'Signup' : 'Login'}</h2>
        <input type="text" placeholder="Username" value={username} onChange={(e) => setUsername(e.target.value)} className="auth-input" />
        <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} className="auth-input" />
        {isSignup ? (
          <button className="auth-btn" onClick={handleSignup}>Signup</button>
        ) : (
          <button className="auth-btn" onClick={handleLogin}>Login</button>
        )}
        <button className="toggle-btn" onClick={() => { setIsSignup(!isSignup); setMessage(''); }}>
          {isSignup ? 'Already have an account? Login' : 'No account? Signup'}
        </button>
        {message && <p className="auth-message">{message}</p>}
      </div>
    );
  }

  return (
    <div className="container">
      <header>
        <h2 className="main-heading">
