import React, { useState, useEffect } from "react";
import axios from "axios";
import { Users, FileText, BarChart3, Settings } from "lucide-react";

// ðŸ“Š Charts
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ResponsiveContainer,
  BarChart,
  Bar,
} from "recharts";

export default function AdminDashboard() {
  const [stats, setStats] = useState({
    totalUsers: 0,
    totalUploads: 0,
    totalLogs: 0,
  });

  const [activeTab, setActiveTab] = useState("overview");

  useEffect(() => {
    // ðŸ”— Fetch stats from backend
    axios.get("/api/admin/stats").then((res) => {
      setStats(res.data);
    });
  }, []);

  // Example chart data
  const userGrowthData = [
    { month: "Jan", users: 40 },
    { month: "Feb", users: 80 },
    { month: "Mar", users: 65 },
  ];

  const uploadData = [
    { day: "Mon", uploads: 10 },
    { day: "Tue", uploads: 30 },
    { day: "Wed", uploads: 15 },
  ];

  return (
    <div className="p-6 bg-gray-50 min-h-screen">
      <h1 className="text-2xl font-bold mb-6">Admin Dashboard</h1>

      {/* Tabs */}
      <div className="flex space-x-2 border-b mb-6">
        {[
          { key: "overview", label: "Overview", icon: BarChart3 },
          { key: "users", label: "Users", icon: Users },
          { key: "logs", label: "Logs", icon: FileText },
          { key: "settings", label: "Settings", icon: Settings },
        ].map((tab) => {
          const Icon = tab.icon;
          return (
            <button
              key={tab.key}
              onClick={() => setActiveTab(tab.key)}
              className={`flex items-center px-4 py-2 text-sm font-medium rounded-t-lg ${
                activeTab === tab.key
                  ? "bg-white border border-b-0 border-gray-200 text-blue-600"
                  : "text-gray-500 hover:text-blue-600"
              }`}
            >
              <Icon className="w-4 h-4 mr-1" />
              {tab.label}
            </button>
          );
        })}
      </div>

      {/* Tab Content */}
      <div className="bg-white p-6 rounded-lg shadow-md border border-gray-200">
        {activeTab === "overview" && (
          <div>
            {/* Stats */}
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              <div className="p-4 rounded-lg shadow bg-gray-50">
                <h2 className="text-lg font-semibold">Total Users</h2>
                <p className="text-2xl font-bold">{stats.totalUsers}</p>
              </div>
              <div className="p-4 rounded-lg shadow bg-gray-50">
                <h2 className="text-lg font-semibold">Total Uploads</h2>
                <p className="text-2xl font-bold">{stats.totalUploads}</p>
              </div>
              <div className="p-4 rounded-lg shadow bg-gray-50">
                <h2 className="text-lg font-semibold">System Logs</h2>
                <p className="text-2xl font-bold">{stats.totalLogs}</p>
              </div>
            </div>

            {/* Charts */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mt-6">
              <div className="p-4 rounded-lg shadow bg-gray-50">
                <h2 className="text-lg font-semibold mb-2">User Growth</h2>
                <ResponsiveContainer width="100%" height={250}>
                  <LineChart data={userGrowthData}>
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis dataKey="month" />
                    <YAxis />
                    <Tooltip />
                    <Line type="monotone" dataKey="users" stroke="#3b82f6" />
                  </LineChart>
                </ResponsiveContainer>
              </div>

              <div className="p-4 rounded-lg shadow bg-gray-50">
                <h2 className="text-lg font-semibold mb-2">Daily Uploads</h2>
                <ResponsiveContainer width="100%" height={250}>
                  <BarChart data={uploadData}>
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis dataKey="day" />
                    <YAxis />
                    <Tooltip />
                    <Bar dataKey="uploads" fill="#10b981" />
                  </BarChart>
                </ResponsiveContainer>
              </div>
            </div>
          </div>
        )}

        {activeTab === "users" && (
          <div>
            <h2 className="text-lg font-semibold mb-4">User Management</h2>
            <p className="text-gray-600">User table will go here.</p>
          </div>
        )}

        {activeTab === "logs" && (
          <div>
            <h2 className="text-lg font-semibold mb-4">System Logs</h2>
            <p className="text-gray-600">Logs table will go here.</p>
          </div>
        )}

        {activeTab === "settings" && (
          <div>
            <h2 className="text-lg font-semibold mb-4">Settings</h2>
            <button className="px-4 py-2 bg-blue-600 text-white rounded-lg shadow hover:bg-blue-700">
              Update Settings
            </button>
          </div>
        )}
      </div>
    </div>
  );
}
