import React, { useState } from "react";
import axios from "axios";

const API_BASE = process.env.REACT_APP_API_BASE || "http://localhost:5000";

export default function Signup({ onSignupSuccess, switchToLogin }) {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  const handleSignup = async (e) => {
    e.preventDefault();
    setError("");
    try {
      await axios.post(`${API_BASE}/auth/signup`, { username, password });
      onSignupSuccess();
    } catch (err) {
      console.error("Signup error:", err);
      const msg = err.response?.data?.error || "Server error. Try again later.";
      setError(msg);
    }
  };

  return (
    <div style={{ padding: 20, maxWidth: 420, margin: "auto" }}>
      <h2>Sign Up</h2>
      {error && <div style={{ color: "red", marginBottom: 10 }}>{error}</div>}
      <form onSubmit={handleSignup}>
        <input
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          placeholder="Username"
          required
          style={{ width: "100%", padding: 8, marginBottom: 8 }}
        />
        <input
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          type="password"
          placeholder="Password"
          required
          style={{ width: "100%", padding: 8, marginBottom: 8 }}
        />
        <button type="submit" style={{ width: "100%", padding: 10 }}>Sign Up</button>
      </form>
      <p style={{ marginTop: 10 }}>
        Already have an account?{" "}
        <button
          type="button"
          onClick={switchToLogin}
          style={{ background: "none", border: "none", color: "blue", cursor: "pointer" }}
        >
          Login
        </button>
      </p>
    </div>
  );
}
