import React, { useState, useEffect } from "react";

// Mock data for users and logs
const mockUsers = [
  { id: 1, name: "Alice Johnson", role: "Admin", status: "Active" },
  { id: 2, name: "Bob Smith", role: "User", status: "Suspended" },
  { id: 3, name: "Charlie Brown", role: "User", status: "Active" },
];

const mockLogs = [
  { id: 1, action: "File Uploaded", user: "Alice Johnson", time: "2m ago" },
  { id: 2, action: "Login Success", user: "Bob Smith", time: "10m ago" },
  { id: 3, action: "Error: Invalid PDF", user: "Charlie Brown", time: "1h ago" },
];

export default function AdminDashboard() {
  const [activeTab, setActiveTab] = useState("overview");
  const [users, setUsers] = useState([]);
  const [logs, setLogs] = useState([]);
  const [search, setSearch] = useState("");
  const [filterRole, setFilterRole] = useState("All");
  const [filterStatus, setFilterStatus] = useState("All");
  const [loadingUsers, setLoadingUsers] = useState(true);
  const [loadingLogs, setLoadingLogs] = useState(true);

  useEffect(() => {
    setTimeout(() => {
      setUsers(mockUsers);
      setLoadingUsers(false);
    }, 1000);

    setTimeout(() => {
      setLogs(mockLogs);
      setLoadingLogs(false);
    }, 1200);
  }, []);

  // Filtered users
  const filteredUsers = users.filter((u) => {
    const matchesSearch = u.name.toLowerCase().includes(search.toLowerCase());
    const matchesRole = filterRole === "All" || u.role === filterRole;
    const matchesStatus = filterStatus === "All" || u.status === filterStatus;
    return matchesSearch && matchesRole && matchesStatus;
  });

  return (
    <div className="min-h-screen bg-gray-50 p-6">
      <h1 className="text-3xl font-bold mb-6">Admin Dashboard</h1>

      {/* Navigation Tabs (Now styled buttons) */}
      <div className="flex space-x-4 mb-8">
        <button
          onClick={() => setActiveTab("overview")}
          className={`px-6 py-2 rounded-xl font-semibold transition ${
            activeTab === "overview"
              ? "bg-blue-600 text-white shadow-md"
              : "bg-gray-200 text-gray-700 hover:bg-gray-300"
          }`}
        >
          Overview
        </button>
        <button
          onClick={() => setActiveTab("users")}
          className={`px-6 py-2 rounded-xl font-semibold transition ${
            activeTab === "users"
              ? "bg-blue-600 text-white shadow-md"
              : "bg-gray-200 text-gray-700 hover:bg-gray-300"
          }`}
        >
          Users
        </button>
        <button
          onClick={() => setActiveTab("logs")}
          className={`px-6 py-2 rounded-xl font-semibold transition ${
            activeTab === "logs"
              ? "bg-blue-600 text-white shadow-md"
              : "bg-gray-200 text-gray-700 hover:bg-gray-300"
          }`}
        >
          Logs
        </button>
        <button
          onClick={() => setActiveTab("settings")}
          className={`px-6 py-2 rounded-xl font-semibold transition ${
            activeTab === "settings"
              ? "bg-blue-600 text-white shadow-md"
              : "bg-gray-200 text-gray-700 hover:bg-gray-300"
          }`}
        >
          Settings
        </button>
      </div>

      {/* Tab Content */}
      <div className="bg-white p-6 rounded-xl shadow-md">
        {/* Overview */}
        {activeTab === "overview" && (
          <div>
            <h2 className="text-xl font-bold mb-4">Overview</h2>
            <div className="grid grid-cols-2 gap-6">
              <div className="p-4 bg-blue-100 rounded-lg shadow-sm">
                <h3 className="text-lg font-semibold">Total Users</h3>
                <p className="text-2xl">{users.length || "-"}</p>
              </div>
              <div className="p-4 bg-green-100 rounded-lg shadow-sm">
                <h3 className="text-lg font-semibold">Total Logs</h3>
                <p className="text-2xl">{logs.length || "-"}</p>
              </div>
            </div>
          </div>
        )}

        {/* Users */}
        {activeTab === "users" && (
          <div>
            <h2 className="text-xl font-bold mb-4">Manage Users</h2>

            {/* Search & Filters */}
            <div className="flex space-x-4 mb-4">
              <input
                type="text"
                placeholder="Search users..."
                className="px-3 py-2 border rounded-lg w-1/3"
                value={search}
                onChange={(e) => setSearch(e.target.value)}
              />
              <select
                className="px-3 py-2 border rounded-lg"
                value={filterRole}
                onChange={(e) => setFilterRole(e.target.value)}
              >
                <option>All</option>
                <option>Admin</option>
                <option>User</option>
              </select>
              <select
                className="px-3 py-2 border rounded-lg"
                value={filterStatus}
                onChange={(e) => setFilterStatus(e.target.value)}
              >
                <option>All</option>
                <option>Active</option>
                <option>Suspended</option>
              </select>
            </div>

            {loadingUsers ? (
              <p className="text-gray-500">Loading users...</p>
            ) : filteredUsers.length === 0 ? (
              <p className="text-gray-500">No users found.</p>
            ) : (
              <table className="w-full border rounded-lg">
                <thead>
                  <tr className="bg-gray-200">
                    <th className="px-4 py-2 text-left">Name</th>
                    <th className="px-4 py-2 text-left">Role</th>
                    <th className="px-4 py-2 text-left">Status</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredUsers.map((user) => (
                    <tr key={user.id} className="border-t">
                      <td className="px-4 py-2">{user.name}</td>
                      <td className="px-4 py-2">{user.role}</td>
                      <td className="px-4 py-2">{user.status}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            )}
          </div>
        )}

        {/* Logs */}
        {activeTab === "logs" && (
          <div>
            <h2 className="text-xl font-bold mb-4">System Logs</h2>
            {loadingLogs ? (
              <p className="text-gray-500">Loading logs...</p>
            ) : logs.length === 0 ? (
              <p className="text-gray-500">No logs found.</p>
            ) : (
              <ul className="space-y-2">
                {logs.map((log) => (
                  <li
                    key={log.id}
                    className="p-3 bg-gray-100 rounded-lg border"
                  >
                    <p className="font-semibold">{log.action}</p>
                    <p className="text-sm text-gray-600">
                      {log.user} â€” {log.time}
                    </p>
                  </li>
                ))}
              </ul>
            )}
          </div>
        )}

        {/* Settings */}
        {activeTab === "settings" && (
          <div>
            <h2 className="text-xl font-bold mb-4">Settings</h2>
            <p className="text-gray-600">Admin controls go here.</p>
          </div>
        )}
      </div>
    </div>
  );
}
