// src/components/OCRUpload.jsx
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../App.css';

const OFFICE_COLUMNS = ["Section", "Code", "Description", "Fee"];
const ICD10_COLUMNS = ["Type", "Code", "Diagnosis"];
const NURSING_COLUMNS = ["MRN", "Visit", "Patient", "Age/DOB", "Location", "Authored (date/time)", "Note summary"];
const PATIENT_COLUMNS = ["Patient Name", "Date of Birth", "Subscriber ID", "Primary Insurance", "Date of Service"];

function normalizeRows(section) {
  if (!section) return [];
  if (Array.isArray(section)) return section;
  if (Array.isArray(section.rows)) return section.rows;
  return [];
}

function Grid({ title, columns, rows }) {
  return (
    <div className="excel-grid-wrapper">
      <div className="excel-grid-header"><h4 className="file-title">{title}</h4></div>
      <div className="excel-grid compact">
        <div className="excel-grid-scroll">
          <table className="excel-table">
            <thead>
              <tr>{columns.map(col => <th key={col} className="excel-th">{col ?? ''}</th>)}</tr>
            </thead>
            <tbody>
              {rows && rows.length > 0 ? rows.map((r, i) => (
                <tr key={i}>
                  {columns.map(col => {
                    // try a few name variants for compatibility
                    const val = r?.[col] ?? r?.[col.toLowerCase()] ?? r?.[col.replace(/\s/g, "_")] ?? '';
                    return <td key={col} className="excel-td">{val}</td>;
                  })}
                </tr>
              )) : (
                <tr><td colSpan={columns.length} style={{ textAlign: 'center' }}>No rows</td></tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

function isScalar(v) {
  return v == null || ['string', 'number', 'boolean'].includes(typeof v);
}

function summarize(v) {
  if (Array.isArray(v)) return `${v.length} rows`;
  if (typeof v === 'object') return '(object)';
  return String(v ?? '');
}

// New component for horizontal metadata table
function HorizontalMetaTable({ meta }) {
  const keys = Object.keys(meta || {});
  const values = keys.map(k => meta[k] ?? '');
  return (
    <div className="table-container">
      <table className="metadata-table">
        <thead>
          <tr>
            {keys.map(key => <th key={key}>{key}</th>)}
          </tr>
        </thead>
        <tbody>
          <tr>
            {values.map((value, idx) => <td key={idx}>{value}</td>)}
          </tr>
        </tbody>
      </table>
    </div>
  );
}

function ResultBlock({ item }) {
  // item shape after upload:
  // { filename, office:[], icd10:[], nursing:[], patients:[], legacyMeta: {...}, default }
  const hasOffice = (item.office && item.office.length > 0);
  const hasICD10 = (item.icd10 && item.icd10.length > 0);
  const hasNursing = (item.nursing && item.nursing.length > 0);
  const hasPatients = (item.patients && item.patients.length > 0);
  const hasLegacy = !!item.legacyMeta && Object.keys(item.legacyMeta).length > 0;

  const initialTab = item.default === 'legacy' ? 'legacy'
    : item.default === 'nursing' ? 'nursing'
    : item.default === 'office' ? 'office'
    : item.default === 'patients' ? 'patients'
    : item.default || (hasPatients ? 'patients' : (hasNursing ? 'nursing' : (hasOffice ? 'office' : (hasICD10 ? 'icd10' : 'legacy'))));

  const [tab, setTab] = useState(initialTab);
  useEffect(() => { setTab(initialTab); }, [initialTab]);

  const counts = [
    hasPatients ? `patients: ${item.patients.length} rows` : null,
    hasOffice ? `office_charges: ${item.office.length} rows` : null,
    hasICD10 ? `icd10: ${item.icd10.length} rows` : null,
    hasNursing ? `nursing_notes: ${item.nursing.length} rows` : null,
  ].filter(Boolean).join(' • ');

  return (
    <div className="table-container">
      <div className="excel-grid-header">
        <div>
          <h4 className="file-title">{item.filename}</h4>
          {counts && <div className="text-xs opacity-70" style={{ marginTop: 6 }}>{counts}</div>}
        </div>
        <div className="tab-row" style={{ display: 'flex', gap: 8 }}>
          {hasPatients && <button className={`tab-btn ${tab === 'patients' ? 'active' : ''}`} onClick={() => setTab('patients')}>Patients</button>}
          {hasOffice && <button className={`tab-btn ${tab === 'office' ? 'active' : ''}`} onClick={() => setTab('office')}>Office</button>}
          {hasICD10 && <button className={`tab-btn ${tab === 'icd10' ? 'active' : ''}`} onClick={() => setTab('icd10')}>ICD-10</button>}
          {hasNursing && <button className={`tab-btn ${tab === 'nursing' ? 'active' : ''}`} onClick={() => setTab('nursing')}>Nursing</button>}
          {hasLegacy && <button className={`tab-btn ${tab === 'legacy' ? 'active' : ''}`} onClick={() => setTab('legacy')}>Metadata</button>}
        </div>
      </div>

      {tab === 'patients' && <Grid title="Patients" columns={PATIENT_COLUMNS} rows={item.patients} />}
      {tab === 'office' && <Grid title="Office Charges" columns={OFFICE_COLUMNS} rows={item.office} />}
      {tab === 'icd10' && <Grid title="ICD-10" columns={ICD10_COLUMNS} rows={item.icd10} />}
      {tab === 'nursing' && <Grid title="Nursing Notes" columns={NURSING_COLUMNS} rows={item.nursing} />}
      {tab === 'legacy' && <HorizontalMetaTable meta={item.legacyMeta} />}
    </div>
  );
}

const OCRUpload = ({ auth, setAuth, isAdmin = false }) => {
  const navigate = useNavigate();
  const [files, setFiles] = useState([]);
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);

  const [isSignup, setIsSignup] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');

  const API_BASE = process.env.REACT_APP_API_BASE || 'http://localhost:5000';
  const api = axios.create({ baseURL: API_BASE });

  api.interceptors.request.use((config) => {
    if (auth?.token) config.headers.Authorization = `Bearer ${auth.token}`;
    return config;
  });
  api.interceptors.response.use(
    (response) => response,
    (error) => {
      if ([401, 403].includes(error.response?.status)) {
        localStorage.clear();
        setAuth(null);
        alert('⚠️ Session expired or access denied. Please log in again.');
      }
      return Promise.reject(error);
    }
  );

  useEffect(() => {
    if (auth?.token && !isAdmin) {
      navigate('/', { replace: true });
    }
  }, [auth, isAdmin, navigate]);

  const handleSignup = async () => {
    try {
      const res = await api.post('/auth/signup', { username, password });
      setMessage(res.data.message || 'Signup successful!');
      const loginRes = await api.post('/auth/login', { username, password });
      const { token, username: un, role } = loginRes.data;
      setAuth({ token, username: un, role });
      localStorage.setItem('auth', JSON.stringify({ token, username: un, role }));
    } catch (err) {
      setMessage('Signup failed: ' + (err.response?.data?.error || err.message));
    }
  };

  const handleLogin = async () => {
    try {
      const res = await api.post('/auth/login', { username, password });
      if (res.data.token) {
        const { token, username: un, role } = res.data;
        setAuth({ token, username: un, role });
        localStorage.setItem('auth', JSON.stringify({ token, username: un, role }));
        setMessage('Login successful!');
      } else {
        setMessage('Invalid credentials');
      }
    } catch (err) {
      setMessage('Login failed: ' + (err.response?.data?.error || err.message));
    }
  };

  const handleFileChange = (e) => {
    const selected = Array.from(e.target.files || []);
    if (selected.length > 10) {
      alert('⚠️ You can upload up to 10 files only.');
      return;
    }
    setFiles(selected);
    setResults([]);
  };

  // Helper to convert backend response into UI item
  function buildItemFromResponse(resData, file) {
    // If backend returns legacy shape: office_charges, icd10, nursing_notes, metadata
    if (resData.office_charges || resData.icd10 || resData.nursing_notes || resData.default) {
      const office = normalizeRows(resData.office_charges);
      const icd10 = normalizeRows(resData.icd10);
      const nursing = normalizeRows(resData.nursing_notes);
      const patients = resData.patients || [];
      return {
        filename: resData.filename || file.name,
        default: resData.default || (patients.length ? 'patients' : (nursing.length ? 'nursing' : (office.length ? 'office' : (icd10.length ? 'icd10' : 'legacy')))),
        office, icd10, nursing, patients,
        legacyMeta: resData.metadata || null
      };
    }

    // New backend shape: patients, medical_billing_codes, diagnosis_codes
    if (resData.patients || resData.medical_billing_codes || resData.diagnosis_codes) {
      const patients = Array.isArray(resData.patients) ? resData.patients : [];
      // Map medical_billing_codes to office rows with fields Code/Description/Fee.
      const office = (Array.isArray(resData.medical_billing_codes) ? resData.medical_billing_codes : []).map(r => {
        // Keep Section blank (your OFFICE_COLUMNS expect a Section column); frontend can show Code, Description, Fee
        return {
          Section: '',
          Code: r.Code ?? r.code ?? '',
          Description: r.Description ?? r.description ?? '',
          Fee: r.Fee ?? r.fee ?? ''
        };
      });
      // Map diagnosis_codes to icd10 rows (Type, Code, Diagnosis)
      const icd10 = (Array.isArray(resData.diagnosis_codes) ? resData.diagnosis_codes : []).map(r => ({
        Type: r.Type ?? r.type ?? 'ICD-10',
        Code: r.Code ?? r.code ?? '',
        Diagnosis: r.Diagnosis ?? r.diagnosis ?? r.Description ?? r.description ?? ''
      }));
      // No nursing notes in this path
      const nursing = [];

      // Create a small legacy meta from first patient (useful if frontend wants horizontal view)
      const legacyMeta = patients.length > 0 ? {
        "File Name": resData.file_name || file.name,
        "Patient Count": patients.length,
        "First Patient": patients[0]["Patient Name"] ?? patients[0].Patient ?? ''
      } : { "File Name": resData.file_name || file.name };

      return {
        filename: resData.file_name || file.name,
        default: patients.length ? 'patients' : (office.length ? 'office' : (icd10.length ? 'icd10' : 'legacy')),
        office, icd10, nursing, patients,
        legacyMeta
      };
    }

    // Fallback: treat res.data.metadata as legacy
    const meta = resData.metadata || {};
    if (!meta['File Name']) meta['File Name'] = file.name;
    delete meta['Patient Signature'];
    delete meta['Physician Signature'];
    return {
      filename: meta['File Name'],
      default: 'legacy',
      office: [], icd10: [], nursing: [], patients: [],
      legacyMeta: meta
    };
  }

  const handleUploadAll = async () => {
    if (!files.length) return;
    setLoading(true);
    try {
      const out = [];
      for (const file of files) {
        const formData = new FormData();
        formData.append('file', file);
        const res = await api.post('/api/ocr', formData, {
          headers: { 'Content-Type': 'multipart/form-data' },
        });

        // Build UI item from available response shapes
        const item = buildItemFromResponse(res.data || {}, file);
        out.push(item);
      }
      setResults(out);
    } catch (err) {
      console.error('Upload failed:', err);
      alert('⚠️ Upload failed. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  if (!auth?.token && !isAdmin) {
    return (
      <div className="container">
        <h2>{isSignup ? 'Signup' : 'Login'}</h2>
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          className="auth-input"
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          className="auth-input"
        />
        {isSignup ? (
          <button className="auth-btn" onClick={handleSignup}>
            Signup
          </button>
        ) : (
          <button className="auth-btn" onClick={handleLogin}>
            Login
          </button>
        )}
        <button
          className="toggle-btn"
          onClick={() => {
            setIsSignup(!isSignup);
            setMessage('');
          }}
        >
          {isSignup ? 'Already have an account? Login' : 'No account? Signup'}
        </button>
        {message && <p className="auth-message">{message}</p>}
      </div>
    );
  }

  return (
    <div className="container">
      <header>
        <h2 className="main-heading">
          {isAdmin ? '📂 Admin OCR Upload Tool' : '📄 File OCR Extraction Tool'}
        </h2>
      </header>

      <section className="upload-section">
        <label htmlFor="file-upload" className="upload-label">
          Upload up to 10 files:
        </label>
        <input
          type="file"
          id="file-upload"
          accept=".pdf,.jpg,.jpeg,.png,.doc,.docx"
          multiple
          onChange={handleFileChange}
        />
        <div className="button-row">
          <button className="upload-btn" onClick={handleUploadAll} disabled={loading}>
            {loading ? 'Processing...' : 'Upload All'}
          </button>
        </div>
      </section>

      {files.length > 0 && (
        <section className="file-preview">
          <h3 className="section-heading">📁 Selected Files</h3>
          <ul className="file-list">{files.map((file, i) => <li key={i}>📎 {file.name}</li>)}</ul>
        </section>
      )}

      {loading && <p className="loading-text">⏳ Extracting tables...</p>}

      {results.length > 0 && (
        <section className="results-section">
          <h3 className="section-heading">📊 Parsed Tables</h3>
          {results.map((item, idx) => (
            <ResultBlock key={idx} item={item} />
          ))}
        </section>
      )}
    </div>
  );
};

export default OCRUpload;
