import React, { useState } from 'react';
import axios from 'axios';
import MetadataTable from './MetadataTable';

const OCRUpload = () => {
  const [file, setFile] = useState(null);
  const [metadata, setMetadata] = useState([]);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleUpload = async () => {
    if (!file) return alert("Please select a file.");
    const formData = new FormData();
    formData.append("file", file);

    try {
      const res = await axios.post("http://localhost:5000/api/ocr", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      const structured = res.data.structured_data;
      setMetadata(structured["Metadata"] || []);

    } catch (error) {
      console.error("OCR upload error:", error);
    }
  };

  return (
    <div className="max-w-3xl mx-auto p-6">
      <h1 className="text-2xl font-bold mb-4">PDF OCR Metadata Extractor</h1>

      <input
        type="file"
        onChange={handleFileChange}
        accept="application/pdf"
        className="mb-4"
      />

      <button
        onClick={handleUpload}
        className="bg-blue-600 hover:bg-blue-700 text-white px-6 py-2 rounded shadow"
      >
        Upload & Extract Metadata
      </button>

      {/* Display only the metadata table */}
      <MetadataTable metadataList={metadata} />
    </div>
  );
};

export default OCRUpload;
