import React, { useState } from 'react';
import axios from 'axios';

const OCRUpload = () => {
  const [file, setFile] = useState(null);
  const [metadata, setMetadata] = useState({});
  const [loading, setLoading] = useState(false);
  const [downloadReady, setDownloadReady] = useState(false);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
    setMetadata({});
    setDownloadReady(false);
  };

  const handleUpload = async () => {
    if (!file) return;
    const formData = new FormData();
    formData.append('file', file);

    setLoading(true);
    try {
      const res = await axios.post("http://localhost:5000/api/ocr", formData);
      setMetadata(res.data.metadata || {});
      setDownloadReady(true);
    } catch (err) {
      console.error("OCR failed", err);
    } finally {
      setLoading(false);
    }
  };

  const handleDownloadPDF = async () => {
    const res = await axios.post(
      "http://localhost:5000/api/export-pdf",
      { metadata },
      { responseType: "blob" }
    );
    const url = URL.createObjectURL(new Blob([res.data]));
    const link = document.createElement("a");
    link.href = url;
    link.setAttribute("download", "metadata_output.pdf");
    document.body.appendChild(link);
    link.click();
  };

  return (
    <div style={{ padding: "2rem", fontFamily: "Arial, sans-serif" }}>
      <h2>üìÑ Upload PDF to Extract Patient Metadata</h2>
      <input type="file" onChange={handleFileChange} />
      <button onClick={handleUpload} style={{ marginLeft: "1rem" }}>
        Upload
      </button>

      {loading && <p>‚è≥ Extracting...</p>}

      {!loading && Object.keys(metadata).length > 0 && (
        <div style={{ marginTop: "2rem" }}>
          <h3>üìã Extracted Metadata</h3>
          <table border="1" cellPadding="10" style={{ borderCollapse: "collapse", width: "100%" }}>
            <thead>
              <tr>
                <th>Field</th>
                <th>Value</th>
              </tr>
            </thead>
            <tbody>
              {Object.entries(metadata).map(([key, value], i) => (
                <tr key={i}>
                  <td><strong>{key}</strong></td>
                  <td>{value}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {downloadReady && (
        <button onClick={handleDownloadPDF} style={{ marginTop: "1.5rem" }}>
          ‚¨áÔ∏è Download PDF
        </button>
      )}
    </div>
  );
};

export default OCRUpload;
