// src/components/OCRUpload.jsx
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../App.css';

// Columns - kept for reference / compatibility but not strictly needed here
const PATIENT_COLUMNS = ["Patient Name", "Date of Birth", "Subscriber ID", "Primary Insurance", "Date of Service"];

function HorizontalMetaTable({ meta }) {
  if (!meta) return null;
  const keys = Object.keys(meta || {});
  if (!keys.length) return null;
  return (
    <div className="metadata-horizontal">
      <table className="metadata-table">
        <thead>
          <tr>{keys.map(k => <th key={k}>{k}</th>)}</tr>
        </thead>
        <tbody>
          <tr>{keys.map(k => <td key={k}>{String(meta[k] ?? '')}</td>)}</tr>
        </tbody>
      </table>
    </div>
  );
}

function BillingTable({ rows }) {
  if (!rows || rows.length === 0) {
    return <div className="empty">No billing codes</div>;
  }
  return (
    <div className="table-block">
      <h5 className="table-title">Medical Billing Codes</h5>
      <table className="excel-table">
        <thead>
          <tr>
            <th>Code</th>
            <th>Description</th>
            <th>Fee</th>
          </tr>
        </thead>
        <tbody>
          {rows.map((r, i) => (
            <tr key={i}>
              <td>{r.Code ?? r.code ?? ''}</td>
              <td>{r.Description ?? r.description ?? ''}</td>
              <td>{r.Fee ?? r.fee ?? ''}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

function DiagnosisTable({ rows }) {
  if (!rows || rows.length === 0) {
    return <div className="empty">No diagnosis codes</div>;
  }
  return (
    <div className="table-block">
      <h5 className="table-title">Diagnosis Codes</h5>
      <table className="excel-table">
        <thead>
          <tr>
            <th>Type</th>
            <th>Code</th>
            <th>Diagnosis</th>
          </tr>
        </thead>
        <tbody>
          {rows.map((r, i) => (
            <tr key={i}>
              <td>{r.Type ?? r.type ?? 'ICD-10'}</td>
              <td>{r.Code ?? r.code ?? ''}</td>
              <td>{r.Diagnosis ?? r.diagnosis ?? r.Description ?? r.description ?? ''}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

function PatientBlock({ patient, billing, diagnosis }) {
  // patient is a dict of keys -> values
  const keys = Object.keys(patient || {});
  return (
    <div className="patient-block">
      <div className="patient-header">
        <h4>Patient: {patient["Patient Name"] ?? patient["Patient"] ?? "Unknown"}</h4>
        <div className="patient-sub">Preview of patient fields</div>
      </div>

      <div className="patient-meta-grid">
        <table className="metadata-vertical">
          <tbody>
            {keys.map(k => (
              <tr key={k}>
                <th className="meta-key">{k}</th>
                <td className="meta-val">{String(patient[k] ?? '')}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <BillingTable rows={billing} />
      <DiagnosisTable rows={diagnosis} />
    </div>
  );
}

function ResultBlock({ item }) {
  // item shape expected:
  // { filename, patients:[], medical_billing_codes:[], diagnosis_codes:[], legacyMeta:? }
  const patients = item.patients || [];
  const billing = item.medical_billing_codes || item.office || [];
  const diagnosis = item.diagnosis_codes || item.icd10 || [];

  // If there are no explicit patients but legacyMeta exists, show legacy block
  if (!patients.length && item.legacyMeta) {
    return (
      <div className="result-block">
        <h3 className="file-title">{item.filename}</h3>
        <HorizontalMetaTable meta={item.legacyMeta} />
      </div>
    );
  }

  return (
    <div className="result-block">
      <h3 className="file-title">{item.filename}</h3>
      <div className="counts">{(patients.length)} patient(s) • {diagnosis.length} diagnosis rows • {billing.length} billing rows</div>

      {patients.map((p, i) => (
        <PatientBlock
          key={i}
          patient={p}
          billing={billing}
          diagnosis={diagnosis}
        />
      ))}

      {/* Raw JSON debug (collapsed) */}
      <details style={{ marginTop: 12 }}>
        <summary>Raw response (debug)</summary>
        <pre style={{ maxHeight: 240, overflow: 'auto', background: '#0b1220', color: '#cbd5e1', padding: 8 }}>
          {JSON.stringify(item, null, 2)}
        </pre>
      </details>
    </div>
  );
}

const OCRUpload = ({ auth, setAuth, isAdmin = false }) => {
  const navigate = useNavigate();
  const [files, setFiles] = useState([]);
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);

  const [isSignup, setIsSignup] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');

  const API_BASE = process.env.REACT_APP_API_BASE || 'http://localhost:5000';
  const api = axios.create({ baseURL: API_BASE });

  api.interceptors.request.use((config) => {
    if (auth?.token) config.headers.Authorization = `Bearer ${auth.token}`;
    return config;
  });
  api.interceptors.response.use(
    (response) => response,
    (error) => {
      if ([401, 403].includes(error.response?.status)) {
        localStorage.clear();
        setAuth(null);
        alert('⚠️ Session expired or access denied. Please log in again.');
      }
      return Promise.reject(error);
    }
  );

  useEffect(() => {
    if (auth?.token && !isAdmin) {
      navigate('/', { replace: true });
    }
  }, [auth, isAdmin, navigate]);

  const handleSignup = async () => {
    try {
      const res = await api.post('/auth/signup', { username, password });
      setMessage(res.data.message || 'Signup successful!');
      const loginRes = await api.post('/auth/login', { username, password });
      const { token, username: un, role } = loginRes.data;
      setAuth({ token, username: un, role });
      localStorage.setItem('auth', JSON.stringify({ token, username: un, role }));
    } catch (err) {
      setMessage('Signup failed: ' + (err.response?.data?.error || err.message));
    }
  };

  const handleLogin = async () => {
    try {
      const res = await api.post('/auth/login', { username, password });
      if (res.data.token) {
        const { token, username: un, role } = res.data;
        setAuth({ token, username: un, role });
        localStorage.setItem('auth', JSON.stringify({ token, username: un, role }));
        setMessage('Login successful!');
      } else {
        setMessage('Invalid credentials');
      }
    } catch (err) {
      setMessage('Login failed: ' + (err.response?.data?.error || err.message));
    }
  };

  const handleFileChange = (e) => {
    const selected = Array.from(e.target.files || []);
    if (selected.length > 10) {
      alert('⚠️ You can upload up to 10 files only.');
      return;
    }
    setFiles(selected);
    setResults([]);
  };

  // Build UI item from backend response
  function buildItemFromResponse(resData, file) {
    if (!resData) return {
      filename: file.name,
      patients: [],
      medical_billing_codes: [],
      diagnosis_codes: [],
      legacyMeta: { "File Name": file.name }
    };

    // New shape (preferred)
    if (resData.patients || resData.medical_billing_codes || resData.diagnosis_codes) {
      const patients = Array.isArray(resData.patients) ? resData.patients : [];
      const billing = Array.isArray(resData.medical_billing_codes) ? resData.medical_billing_codes : [];
      const diagnosis = Array.isArray(resData.diagnosis_codes) ? resData.diagnosis_codes : [];

      return {
        filename: resData.file_name || resData.filename || file.name,
        patients,
        medical_billing_codes: billing,
        diagnosis_codes: diagnosis,
        legacyMeta: resData.metadata || null
      };
    }

    // Legacy shape (older API)
    if (resData.office_charges || resData.icd10 || resData.nursing_notes || resData.metadata) {
      // try normalizing to new keys
      const patients = resData.patients || [];
      const office = resData.office_charges || resData.office || [];
      const icd10 = resData.icd10 || resData.diagnosis_codes || [];
      const nursing = resData.nursing_notes || [];
      const meta = resData.metadata || resData;
      return {
        filename: resData.filename || meta['File Name'] || file.name,
        patients,
        medical_billing_codes: office,
        diagnosis_codes: icd10,
        legacyMeta: meta
      };
    }

    // Fallback: treat entire response as metadata
    const meta = resData.metadata || resData;
    if (!meta['File Name']) meta['File Name'] = file.name;
    return {
      filename: meta['File Name'],
      patients: [],
      medical_billing_codes: [],
      diagnosis_codes: [],
      legacyMeta: meta
    };
  }

  const handleUploadAll = async () => {
    if (!files.length) return;
    setLoading(true);
    try {
      const out = [];
      for (const file of files) {
        const formData = new FormData();
        formData.append('file', file);
        const res = await api.post('/api/ocr', formData, {
          headers: { 'Content-Type': 'multipart/form-data' },
        });

        const item = buildItemFromResponse(res.data || {}, file);
        out.push(item);
      }
      setResults(out);
    } catch (err) {
      console.error('Upload failed:', err);
      alert('⚠️ Upload failed. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  if (!auth?.token && !isAdmin) {
    return (
      <div className="container">
        <h2>{isSignup ? 'Signup' : 'Login'}</h2>
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          className="auth-input"
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          className="auth-input"
        />
        {isSignup ? (
          <button className="auth-btn" onClick={handleSignup}>
            Signup
          </button>
        ) : (
          <button className="auth-btn" onClick={handleLogin}>
            Login
          </button>
        )}
        <button
          className="toggle-btn"
          onClick={() => {
            setIsSignup(!isSignup);
            setMessage('');
          }}
        >
          {isSignup ? 'Already have an account? Login' : 'No account? Signup'}
        </button>
        {message && <p className="auth-message">{message}</p>}
      </div>
    );
  }

  return (
    <div className="container">
      <header>
        <h2 className="main-heading">
          {isAdmin ? '📂 Admin OCR Upload Tool' : '📄 File OCR Extraction Tool'}
        </h2>
      </header>

      <section className="upload-section">
        <label htmlFor="file-upload" className="upload-label">
          Upload up to 10 files:
        </label>
        <input
          type="file"
          id="file-upload"
          accept=".pdf,.jpg,.jpeg,.png,.doc,.docx"
          multiple
          onChange={handleFileChange}
        />
        <div className="button-row">
          <button className="upload-btn" onClick={handleUploadAll} disabled={loading}>
            {loading ? 'Processing...' : 'Upload All'}
          </button>
        </div>
      </section>

      {files.length > 0 && (
        <section className="file-preview">
          <h3 className="section-heading">📁 Selected Files</h3>
          <ul className="file-list">{files.map((file, i) => <li key={i}>📎 {file.name}</li>)}</ul>
        </section>
      )}

      {loading && <p className="loading-text">⏳ Extracting tables...</p>}

      {results.length > 0 && (
        <section className="results-section">
          <h3 className="section-heading">📊 Parsed Tables</h3>
          {results.map((item, idx) => (
            <ResultBlock key={idx} item={item} />
          ))}
        </section>
      )}
    </div>
  );
};

export default OCRUpload;
