import React, { useState } from 'react';
import axios from 'axios';

const OCRUpload = () => {
  const [files, setFiles] = useState([]);
  const [allMetadata, setAllMetadata] = useState([]);
  const [loading, setLoading] = useState(false);

  const handleFileChange = (e) => {
    setFiles(Array.from(e.target.files));
    setAllMetadata([]);
  };

  const handleUpload = async () => {
    if (!files.length) return;

    setLoading(true);
    const results = [];

    for (const file of files) {
      const formData = new FormData();
      formData.append("file", file);

      try {
        const res = await axios.post("http://localhost:5000/api/ocr", formData);
        const metadata = res.data.metadata || {};
        results.push(metadata);

        // Send metadata to Excel backend
        await axios.post("http://localhost:5000/api/export-excel", { metadata });
      } catch (err) {
        console.error(`Error processing ${file.name}`, err);
      }
    }

    setAllMetadata(results);
    setLoading(false);
  };

  const handleDownloadExcel = async () => {
    try {
      const res = await axios.post(
        "http://localhost:5000/api/export-excel",
        {},
        { responseType: "blob" }
      );
      const url = URL.createObjectURL(new Blob([res.data]));
      const link = document.createElement("a");
      link.href = url;
      link.setAttribute("download", "metadata_output.xlsx");
      document.body.appendChild(link);
      link.click();
    } catch (err) {
      console.error("Excel download failed", err.message);
    }
  };

  return (
    <div style={{ padding: "2rem", fontFamily: "Arial, sans-serif" }}>
      <h2>üìÑ Upload Multiple PDFs to Extract Metadata</h2>
      <input type="file" multiple accept=".pdf" onChange={handleFileChange} />
      <button onClick={handleUpload} style={{ marginLeft: "1rem" }}>
        Upload All
      </button>

      {loading && <p>‚è≥ Extracting...</p>}

      {!loading && allMetadata.length > 0 && (
        <div style={{ marginTop: "2rem" }}>
          <h3>üßæ Extracted Metadata</h3>
          {allMetadata.map((metadata, index) => (
            <div key={index} style={{ marginBottom: "2rem" }}>
              <h4>üìå File {index + 1}</h4>
              <table border="1" cellPadding="10" style={{ borderCollapse: "collapse", width: "100%" }}>
                <thead>
                  <tr>
                    <th>Field</th>
                    <th>Value</th>
                  </tr>
                </thead>
                <tbody>
                  {Object.entries(metadata).map(([key, value], i) => (
                    <tr key={i}>
                      <td><strong>{key}</strong></td>
                      <td>{value}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ))}
          <button onClick={handleDownloadExcel} style={{ marginTop: "1rem" }}>
            ‚¨áÔ∏è Download All as Excel
          </button>
        </div>
      )}
    </div>
  );
};

export default OCRUpload;
