import React, { useState, useEffect, useMemo } from "react";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ResponsiveContainer,
  BarChart,
  Bar,
  PieChart,
  Pie,
  Cell,
  Legend,
} from "recharts";
// NOTE: socket.io-client removed; we're using EventSource (SSE)
import "../../App.css";

// Example data (kept intact)
const userGrowthData = [
  { month: "Jan", users: 40 },
  { month: "Feb", users: 80 },
  { month: "Mar", users: 65 },
  { month: "Apr", users: 120 },
  { month: "May", users: 150 },
];

const activityData = [
  { name: "Uploads", value: 120 },
  { name: "Downloads", value: 95 },
  { name: "Logins", value: 200 },
  { name: "Errors", value: 15 },
];

// Sample users (fallback only; we won't remove it)
const sampleUsers = [
  { id: 1, name: "John Doe", email: "john@example.com", role: "User" },
  { id: 2, name: "Jane Smith", email: "jane@example.com", role: "Admin" },
  { id: 3, name: "Alice Johnson", email: "alice@example.com", role: "User" },
  { id: 4, name: "Bob Brown", email: "bob@example.com", role: "Suspended" },
  { id: 5, name: "Charlie White", email: "charlie@example.com", role: "User" },
  { id: 6, name: "Daisy Blue", email: "daisy@example.com", role: "User" },
];

// Initial logs (fallback only; kept intact)
const logsData = [
  { id: 1, time: "12:01", text: "User John uploaded a file." },
  { id: 2, time: "12:05", text: "Jane logged in." },
  { id: 3, time: "12:10", text: "Error: Failed upload attempt." },
  { id: 4, time: "12:15", text: "System backup completed." },
  { id: 5, time: "12:20", text: "Alice updated her profile." },
  { id: 6, time: "12:25", text: "New user registered." },
];

// Small Glass Card component (unchanged)
const GlassCard = ({ title, children }) => {
  return (
    <div className="unique-glass-card small-card">
      <h2>{title}</h2>
      {children}
    </div>
  );
};

export default function AdminDashboard() {
  const [activeTab, setActiveTab] = useState("overview");

  // Users Tab State
  const [search, setSearch] = useState("");
  const [filterRole, setFilterRole] = useState("All");
  const [currentPage, setCurrentPage] = useState(1);
  const [selectedUsers, setSelectedUsers] = useState([]); // bulk actions
  const usersPerPage = 3;

  // Logs Tab State
  const [logPage, setLogPage] = useState(1);
  const logsPerPage = 3;

  // Loading state (kept)
  const [loading, setLoading] = useState(true);

  // --- server state ---
  const [serverUsers, setServerUsers] = useState([]); // from /api/admin/users
  const [serverLogs, setServerLogs] = useState([]);   // from /api/admin/logs
  const [analytics, setAnalytics] = useState({
    total_users: 0,
    role_counts: {},
    total_metadata_items: 0,
    total_invites: 0,
  });

  // --- helpers for auth header ---
  const token =
    typeof window !== "undefined" ? localStorage.getItem("token") : null;
  const authHeaders = token ? { Authorization: `Bearer ${token}` } : {};

  // --- base URL ---
  const apiBase =
    process.env.REACT_APP_API_BASE ||
    window.location.origin.replace(/\/$/, "");

  // --- initial data fetch (users, logs, analytics) ---
  useEffect(() => {
    let canceled = false;
    async function bootstrap() {
      try {
        const [uRes, lRes, aRes] = await Promise.all([
          fetch(`${apiBase}/api/admin/users`, { headers: { ...authHeaders } }),
          fetch(`${apiBase}/api/admin/logs`, { headers: { ...authHeaders } }),
          fetch(`${apiBase}/api/admin/analytics`, { headers: { ...authHeaders } }),
        ]);
        if (!canceled) {
          if (uRes.ok) {
            const uJson = await uRes.json();
            setServerUsers(Array.isArray(uJson.users) ? uJson.users : []);
          }
          if (lRes.ok) {
            const lJson = await lRes.json();
            setServerLogs(Array.isArray(lJson.logs) ? lJson.logs : []);
          }
          if (aRes.ok) {
            const aJson = await aRes.json();
            setAnalytics({
              total_users: aJson.total_users ?? 0,
              role_counts: aJson.role_counts ?? {},
              total_metadata_items: aJson.total_metadata_items ?? 0,
              total_invites: aJson.total_invites ?? 0,
            });
          }
        }
      } catch (e) {
        // ignore; fall back to static data
      } finally {
        if (!canceled) setLoading(false);
      }
    }
    bootstrap();
    return () => {
      canceled = true;
    };
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  // --- realtime via SSE ---
  useEffect(() => {
    if (!token) return;
    // Pass JWT via query param because EventSource cannot set headers
    const streamUrl = `${apiBase}/api/admin/stream?token=${encodeURIComponent(
      token
    )}`;
    const es = new EventSource(streamUrl);

    const onMessage = async (event) => {
      if (!event || !event.data) return;
      try {
        const payload = JSON.parse(event.data);
        if (payload.type === "log") {
          setServerLogs((prev) => [{ time: payload.time, text: payload.text }, ...prev].slice(0, 200));
        } else if (payload.type === "analytics_update") {
          try {
            const [aRes, uRes] = await Promise.all([
              fetch(`${apiBase}/api/admin/analytics`, { headers: { ...authHeaders } }),
              fetch(`${apiBase}/api/admin/users`, { headers: { ...authHeaders } }),
            ]);
            if (aRes.ok) {
              const aJson = await aRes.json();
              setAnalytics({
                total_users: aJson.total_users ?? 0,
                role_counts: aJson.role_counts ?? {},
                total_metadata_items: aJson.total_metadata_items ?? 0,
                total_invites: aJson.total_invites ?? 0,
              });
            }
            if (uRes.ok) {
              const uJson = await uRes.json();
              setServerUsers(Array.isArray(uJson.users) ? uJson.users : []);
            }
          } catch {}
        } else {
          // ignore hello/other
        }
      } catch {
        // ignore parse errors
      }
    };

    es.onmessage = onMessage;
    es.onerror = () => {
      // Let browser auto-retry; no-op here
    };

    return () => es.close();
  }, [apiBase, token]); // eslint-disable-line react-hooks/exhaustive-deps

  // Keep your loading simulation duration but finish after initial fetch
  useEffect(() => {
    const timer = setTimeout(() => setLoading(false), 800);
    return () => clearTimeout(timer);
  }, []);

  // --- normalize server users to fit current table shape (name/email/role) ---
  const normalizedServerUsers = useMemo(() => {
    return serverUsers.map((u, idx) => ({
      id: idx + 1,
      name: u.username,
      email: `${u.username}@local`,
      role: u.role || "User",
    }));
  }, [serverUsers]);

  // Use server data if present; else fallback to sample
  const allUsers = normalizedServerUsers.length ? normalizedServerUsers : sampleUsers;

  // Filter + Search Users
  const filteredUsers = allUsers.filter((user) => {
    const matchesSearch =
      user.name.toLowerCase().includes(search.toLowerCase()) ||
      user.email.toLowerCase().includes(search.toLowerCase());
    const matchesRole = filterRole === "All" || user.role === filterRole;
    return matchesSearch && matchesRole;
  });

  // Pagination logic (Users)
  const usersPerPageCount = Math.max(usersPerPage, 1);
  const indexOfLastUser = currentPage * usersPerPageCount;
  const indexOfFirstUser = indexOfLastUser - usersPerPageCount;
  const currentUsers = filteredUsers.slice(indexOfFirstUser, indexOfLastUser);

  // Logs source: serverLogs if available; else fallback to logsData
  const logsSource = serverLogs.length
    ? serverLogs.map((l, i) => ({ id: i + 1, time: l.time, text: l.text }))
    : logsData;

  // Pagination logic (Logs)
  const logsPerPageCount = Math.max(logsPerPage, 1);
  const indexOfLastLog = logPage * logsPerPageCount;
  const indexOfFirstLog = indexOfLastLog - logsPerPageCount;
  const currentLogs = logsSource.slice(indexOfFirstLog, indexOfLastLog);

  // Handle bulk selection
  const toggleUserSelection = (id) => {
    if (selectedUsers.includes(id)) {
      setSelectedUsers(selectedUsers.filter((uid) => uid !== id));
    } else {
      setSelectedUsers([...selectedUsers, id]);
    }
  };

  const bulkDelete = () => {
    alert(`Deleting users: ${selectedUsers.join(", ")}`);
    setSelectedUsers([]);
  };

  const bulkPromote = () => {
    alert(`Promoting users: ${selectedUsers.join(", ")}`);
    setSelectedUsers([]);
  };

  // --- role distribution from analytics (fallback to previous static values) ---
  const roleDistData = useMemo(() => {
    const rc = analytics.role_counts || {};
    const total = Object.values(rc).reduce((a, b) => a + (b || 0), 0);
    if (total === 0) {
      return [
        { name: "Users", value: 4 },
        { name: "Admins", value: 1 },
        { name: "Suspended", value: 1 },
      ];
    }
    return [
      { name: "Users", value: rc["User"] || 0 },
      { name: "Admins", value: rc["Admin"] || 0 },
      { name: "Suspended", value: rc["Suspended"] || 0 },
    ];
  }, [analytics.role_counts]);

  return (
    <div className="dashboard-container">
      {/* Header */}
      <h1 className="dashboard-title">Admin Dashboard</h1>

      {/* Tabs */}
      <div className="flex space-x-4 mb-6">
        {["overview", "users", "logs", "settings"].map((tab) => (
          <button
            key={tab}
            onClick={() => setActiveTab(tab)}
            className={`px-5 py-2 rounded-lg font-medium capitalize transition-all duration-200 ${
              activeTab === tab
                ? "bg-blue-600 text-white shadow-lg"
                : "bg-gray-200 text-gray-700 hover:bg-gray-300"
            }`}
          >
            {tab}
          </button>
        ))}
      </div>

      {/* Tab Content */}
      {activeTab === "overview" && (
        <div>
          {/* Stats Cards Side by Side */}
          <div className="stats-grid">
            <GlassCard title="Total Users">
              <p className="stat-value">{analytics.total_users || 120}</p>
            </GlassCard>
            <GlassCard title="Activity Logs">
              <p className="stat-value">{serverLogs.length || 523}</p>
            </GlassCard>
            <GlassCard title="Total Uploads">
              <p className="stat-value">{analytics.total_metadata_items || 78}</p>
            </GlassCard>
          </div>

          {/* Charts Section */}
          <div className="charts-grid">
            <GlassCard title="User Growth">
              <ResponsiveContainer width="100%" height={220}>
                <LineChart data={userGrowthData}>
                  <CartesianGrid strokeDasharray="3 3" stroke="rgba(255,255,255,0.2)" />
                  <XAxis dataKey="month" stroke="#fff" />
                  <YAxis stroke="#fff" />
                  <Tooltip
                    contentStyle={{
                      backgroundColor: "rgba(0,0,0,0.8)",
                      borderRadius: "8px",
                      border: "none",
                      color: "#fff",
                    }}
                  />
                  <Line type="monotone" dataKey="users" stroke="#00ff88" strokeWidth={2} />
                </LineChart>
              </ResponsiveContainer>
            </GlassCard>

            <GlassCard title="Activity Breakdown">
              <ResponsiveContainer width="100%" height={220}>
                <BarChart data={activityData}>
                  <CartesianGrid strokeDasharray="3 3" stroke="rgba(255,255,255,0.2)" />
                  <XAxis dataKey="name" stroke="#fff" />
                  <YAxis stroke="#fff" />
                  <Tooltip
                    contentStyle={{
                      backgroundColor: "rgba(0,0,0,0.8)",
                      borderRadius: "8px",
                      border: "none",
                      color: "#fff",
                    }}
                  />
                  <Bar dataKey="value" fill="#8884d8" radius={[6, 6, 0, 0]} />
                </BarChart>
              </ResponsiveContainer>
            </GlassCard>

            <GlassCard title="Role Distribution">
              <ResponsiveContainer width="100%" height={220}>
                <PieChart>
                  <Pie
                    data={roleDistData}
                    cx="50%"
                    cy="50%"
                    outerRadius={80}
                    fill="#8884d8"
                    dataKey="value"
                    label
                  >
                    <Cell fill="#00C49F" />
                    <Cell fill="#FF8042" />
                    <Cell fill="#FF0000" />
                  </Pie>
                  <Legend />
                </PieChart>
              </ResponsiveContainer>
            </GlassCard>
          </div>
        </div>
      )}

      {activeTab === "users" && (
        <div>
          <h2 className="text-xl font-semibold mb-4">Manage Users</h2>

          {/* Search + Filter */}
          <div className="flex mb-4 space-x-4">
            <input
              type="text"
              placeholder="Search by name or email..."
              className="border border-gray-300 rounded p-2 flex-grow"
              value={search}
              onChange={(e) => setSearch(e.target.value)}
            />
            <select
              className="border border-gray-300 rounded p-2"
              value={filterRole}
              onChange={(e) => setFilterRole(e.target.value)}
            >
              <option>All</option>
              <option>User</option>
              <option>Admin</option>
              <option>Suspended</option>
            </select>
          </div>

          {/* Bulk Action Buttons */}
          {selectedUsers.length > 0 && (
            <div className="flex mb-4 space-x-2">
              <button
                onClick={bulkPromote}
                className="px-3 py-1 bg-blue-500 text-white rounded hover:bg-blue-600"
              >
                Promote Selected
              </button>
              <button
                onClick={bulkDelete}
                className="px-3 py-1 bg-red-500 text-white rounded hover:bg-red-600"
              >
                Delete Selected
              </button>
            </div>
          )}

          {/* User Table */}
          {loading ? (
            <p className="text-gray-500">Loading users...</p>
          ) : currentUsers.length === 0 ? (
            <p className="text-gray-500">No users found.</p>
          ) : (
            <>
              <table className="w-full border-collapse border border-gray-300">
                <thead>
                  <tr className="bg-gray-200">
                    <th className="p-2 border border-gray-300">Select</th>
                    <th className="p-2 border border-gray-300">Name</th>
                    <th className="p-2 border border-gray-300">Email</th>
                    <th className="p-2 border border-gray-300">Role</th>
                    <th className="p-2 border border-gray-300">Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {currentUsers.map((user) => (
                    <tr key={user.id}>
                      <td className="p-2 border border-gray-300 text-center">
                        <input
                          type="checkbox"
                          checked={selectedUsers.includes(user.id)}
                          onChange={() => toggleUserSelection(user.id)}
                        />
                      </td>
                      <td className="p-2 border border-gray-300">{user.name}</td>
                      <td className="p-2 border border-gray-300">{user.email}</td>
                      <td className="p-2 border border-gray-300">{user.role}</td>
                      <td className="p-2 border border-gray-300 space-x-2">
                        <button className="px-2 py-1 bg-blue-500 text-white rounded hover:bg-blue-600">
                          Promote
                        </button>
                        <button className="px-2 py-1 bg-red-500 text-white rounded hover:bg-red-600">
                          Delete
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>

              {/* Pagination Controls */}
              <div className="flex justify-center mt-4 space-x-2">
                {Array.from(
                  {
                    length: Math.ceil(filteredUsers.length / usersPerPageCount),
                  },
                  (_, i) => (
                    <button
                      key={i + 1}
                      onClick={() => setCurrentPage(i + 1)}
                      className={`px-3 py-1 rounded ${
                        currentPage === i + 1
                          ? "bg-blue-500 text-white"
                          : "bg-gray-200"
                      }`}
                    >
                      {i + 1}
                    </button>
                  )
                )}
              </div>
            </>
          )}
        </div>
      )}

      {activeTab === "logs" && (
        <div>
          <h2 className="text-xl font-semibold mb-4">System Logs</h2>
          {loading ? (
            <p className="text-gray-500">Loading logs...</p>
          ) : currentLogs.length === 0 ? (
            <p className="text-gray-500">No logs available.</p>
          ) : (
            <>
              <ul className="space-y-2">
                {currentLogs.map((log) => (
                  <li
                    key={log.id}
                    className="p-2 bg-gray-100 rounded shadow-sm"
                  >
                    [{log.time}] {log.text}
                  </li>
                ))}
              </ul>

              {/* Pagination Controls */}
              <div className="flex justify-center mt-4 space-x-2">
                {Array.from(
                  { length: Math.ceil(logsSource.length / logsPerPageCount) },
                  (_, i) => (
                    <button
                      key={i + 1}
                      onClick={() => setLogPage(i + 1)}
                      className={`px-3 py-1 rounded ${
                        logPage === i + 1
                          ? "bg-blue-500 text-white"
                          : "bg-gray-200"
                      }`}
                    >
                      {i + 1}
                    </button>
                  )
                )}
              </div>
            </>
          )}
        </div>
      )}

      {activeTab === "settings" && (
        <div>
          <h2 className="text-xl font-semibold mb-4">Admin Settings</h2>
          <form className="space-y-4">
            <div>
              <label className="block font-medium">System Name</label>
              <input
                type="text"
                className="w-full border border-gray-300 rounded p-2"
                defaultValue="PDF-OCR System"
              />
            </div>
            <div>
              <label className="block font-medium">Maintenance Mode</label>
              <select className="w-full border border-gray-300 rounded p-2">
                <option>Off</option>
                <option>On</option>
              </select>
            </div>
            <button
              type="submit"
              className="px-4 py-2 bg-green-500 text-white rounded hover:bg-green-600"
            >
              Save Changes
            </button>
          </form>
        </div>
      )}
    </div>
  );
}
