import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../App.css';

const OCRUpload = ({ auth, setAuth, isAdmin = false }) => {
  const navigate = useNavigate();
  const [files, setFiles] = useState([]);
  const [tables, setTables] = useState([]);     // [{columns, rows, sections, note?, head?}]
  const [rawTexts, setRawTexts] = useState([]); // plain text per file for fallback parse
  const [loading, setLoading] = useState(false);
  const [downloadReady, setDownloadReady] = useState(false);

  const [isSignup, setIsSignup] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');

  const API_BASE = process.env.REACT_APP_API_BASE || 'http://localhost:5000';
  const api = axios.create({ baseURL: API_BASE });

  // Attach token
  api.interceptors.request.use((config) => {
    if (auth?.token) config.headers.Authorization = `Bearer ${auth.token}`;
    return config;
  });

  // Handle 401/403
  api.interceptors.response.use(
    (response) => response,
    (error) => {
      if ([401, 403].includes(error.response?.status)) {
        localStorage.clear();
        setAuth(null);
        alert('‚ö†Ô∏è Session expired or access denied. Please log in again.');
      }
      return Promise.reject(error);
    }
  );

  useEffect(() => {
    if (auth?.token && !isAdmin) {
      navigate('/', { replace: true });
    }
  }, [auth, isAdmin, navigate]);

  // Auth flows (unchanged)
  const handleSignup = async () => {
    try {
      const res = await api.post('/auth/signup', { username, password });
      setMessage(res.data.message || 'Signup successful!');
      const loginRes = await api.post('/auth/login', { username, password });
      setAuth({
        token: loginRes.data.token,
        username: loginRes.data.username,
        role: loginRes.data.role,
      });
      localStorage.setItem(
        'auth',
        JSON.stringify({
          token: loginRes.data.token,
          username: loginRes.data.username,
          role: loginRes.data.role,
        })
      );
    } catch (err) {
      setMessage('Signup failed: ' + (err.response?.data?.error || err.message));
    }
  };

  const handleLogin = async () => {
    try {
      const res = await api.post('/auth/login', { username, password });
      if (res.data.token) {
        setAuth({
          token: res.data.token,
          username: res.data.username,
          role: res.data.role,
        });
        localStorage.setItem(
          'auth',
          JSON.stringify({
            token: res.data.token,
            username: res.data.username,
            role: res.data.role,
          })
        );
        setMessage('Login successful!');
      } else {
        setMessage('Invalid credentials');
      }
    } catch (err) {
      setMessage('Login failed: ' + (err.response?.data?.error || err.message));
    }
  };

  // File selection
  const handleFileChange = (e) => {
    const selected = Array.from(e.target.files || []);
    if (selected.length > 10) {
      alert('‚ö†Ô∏è You can upload up to 10 files only.');
      return;
    }
    setFiles(selected);
    setTables([]);
    setRawTexts([]);
    setDownloadReady(false);
  };

  // Utilities for client-side fallback parsing
  function stripEmptyEdges(parts) {
    const arr = parts.slice();
    while (arr.length && arr === '') arr.shift();           // fixed: check arr [11]
    while (arr.length && arr[arr.length - 1] === '') arr.pop();
    return arr;
  }
  function setOf(s) {
    return new Set((s || '').split(''));
  }
  function inferColumnsFromText(text) {
    if (!text) return [];
    const lines = (text || '').split(/\r?\n/).map(l => l.trimEnd()); // use trimEnd alias [4][9]
    // Try markdown-like header with pipes for this report schema
    const mdIdx = lines.findIndex(ln => ln.includes('|') && /Patient/i.test(ln) && /Amount/i.test(ln));
    if (mdIdx !== -1) {
      let parts = lines[mdIdx].split('|').map(s => s.trim()).filter(Boolean);
      return parts;
    }
    // Default to the CRNA/Procedure report schema
    return [
      "Patient Name","Pat Ocr","Responsible Party Name","Rep #","Doc","B-Type","Seen",
      "Service Type","Office","Code","Description","Amount","Balance","Claim","Loc","Entered","Who"
    ];
  }
  function parsePlainTextToRows(text, expectedColumns) {
    if (!text || !expectedColumns?.length) return [];
    const lines = (text || '').split(/\r?\n/).map(l => l.trimEnd());

    // Try markdown-like rows first
    const headerIdx = lines.findIndex(
      ln => ln.includes('|') &&
        expectedColumns.filter(Boolean).slice(0, 5).every(c =>
          ln.toLowerCase().includes((c || '').toLowerCase().split(' '))
        )
    );
    if (headerIdx !== -1) {
      let start = headerIdx + 1;
      // Skip separator like ---|---
      if (lines[start] && setOf(lines[start].replace(/\|/g, '').trim()).isSubsetOf
        ? setOf(lines[start].replace(/\|/g, '').trim()).isSubsetOf(setOf('-: '))
        : /^[\-\:\|\s]+$/.test(lines[start])) start++;
      const out = [];
      for (let i = start; i < lines.length; i++) {
        const ln = lines[i].trim();
        if (!ln) continue;
        if (/^Billing Type Totals:/i.test(ln) || /<GRAND TOTALS>/i.test(ln)) break;
        if (!ln.includes('|')) break;
        let parts = ln.split('|').map(s => s.trim());
        parts = stripEmptyEdges(parts);
        if (parts.length > expectedColumns.length) parts = parts.slice(0, expectedColumns.length);
        if (parts.length < expectedColumns.length) parts = parts.concat(Array(expectedColumns.length - parts.length).fill(''));
        const row = {};
        expectedColumns.forEach((c, idx) => row[c] = parts[idx] ?? '');
        if (Object.values(row).some(v => v)) out.push(row);
      }
      return out;
    }

    // Monospace-like fallback
    const out = [];
    for (const raw of lines) {
      const ln = (raw || '').trim();
      if (!ln || /^Billing Type Totals:/i.test(ln) || /<GRAND TOTALS>/i.test(ln)) continue;
      if (/^[-:| ]+$/.test(ln)) continue;
      let parts = ln.includes('|') ? ln.split('|').map(s => s.trim()) : ln.split(/\s{2,}/).map(s => s.trim());
      parts = parts.filter(Boolean);
      if (parts.length < 3) continue;
      if (parts.length > expectedColumns.length) parts = parts.slice(0, expectedColumns.length);
      if (parts.length < expectedColumns.length) parts = parts.concat(Array(expectedColumns.length - parts.length).fill(''));
      const row = {};
      expectedColumns.forEach((c, idx) => row[c] = parts[idx] ?? '');
      if (row['Patient Name'] || row['Code'] || row['Amount'] || row['Balance']) {
        out.push(row);
      }
    }
    return out;
  }

  // Upload and parse with logs
  const handleUploadAll = async () => {
    if (!files.length) return;
    setLoading(true);

    try {
      if (auth?.role?.toLowerCase() === 'admin') {
        try { await api.post('/api/reset'); } catch {}
      }

      const outTables = [];
      const texts = [];

      for (const [idx, file] of files.entries()) {
        const formData = new FormData();
        formData.append('file', file);

        const res = await api.post('/api/ocr', formData, {
          headers: {
            'Content-Type': 'multipart/form-data',
            Authorization: `Bearer ${auth?.token}`,
          },
        });

        // Debug OCR payload
        console.log('OCR response for', file.name, {
          report: res.data.report,
          report_text_head: (res.data.report_text_head || res.data.report_text || '').slice(0, 400)
        });

        const report = res.data.report || res.data.metadata?.report || null;
        const fallbackText =
          res.data.report_text ||
          res.data.report_text_head ||   // quick head if full text missing
          res.data.text ||
          res.data.metadata?.report_text ||
          res.data.metadata?.full_text ||
          '';

        outTables[idx] = (report && Array.isArray(report.columns) && Array.isArray(report.rows))
          ? report
          : { columns: [], rows: [], sections: [], note: res.data.message || '', head: res.data.report_text_head || '' };

        texts[idx] = typeof fallbackText === 'string' ? fallbackText : '';
      }

      console.log('Built table shell for', files.map(f => f.name), outTables);
      console.log('Raw text heads', texts.map(t => (t || '').slice(0, 200)));

      setTables(outTables);
      setRawTexts(texts);
      setDownloadReady(true);
    } catch (err) {
      console.error('Upload failed:', err);
      alert('‚ö†Ô∏è Upload failed. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  // Excel-like grid renderer
  const ExcelGrid = ({ title, columns, rows, head, note }) => {
    const hasHeaders = Array.isArray(columns) && columns.length > 0;
    return (
      <div className="excel-grid-wrapper">
        <div className="excel-grid-header">
          <h4 className="file-title">{title}</h4>
        </div>
        {!hasHeaders ? (
          <div className="no-data">
            <div>No headers detected.</div>
            {head ? <pre className="ocr-head">{head}</pre> : null}
            {note ? <div className="note">{note}</div> : null}
            <div className="hint">Tip: If this is a sideways report, rotate the PDF 90¬∞ and retry.</div>
          </div>
        ) : (
          <div className="excel-grid compact">
            <div className="excel-grid-scroll">
              <table className="excel-table">
                <thead>
                  <tr>
                    {columns.map((col) => (
                      <th key={col} className="excel-th">{col}</th>
                    ))}
                  </tr>
                </thead>
                <tbody>
                  {!rows || rows.length === 0 ? (
                    <tr>
                      <td className="excel-td" colSpan={columns.length} style={{ textAlign: 'center' }}>
                        No rows
                      </td>
                    </tr>
                  ) : (
                    rows.map((r, i) => (
                      <tr key={r?.row_id ?? i}>
                        {columns.map((col) => (
                          <td key={col} className="excel-td">{r?.[col] ?? ''}</td>
                        ))}
                      </tr>
                    ))
                  )}
                </tbody>
              </table>
            </div>
          </div>
        )}
      </div>
    );
  };

  // Login gate
  if (!auth?.token && !isAdmin) {
    return (
      <div className="container">
        <h2>{isSignup ? 'Signup' : 'Login'}</h2>
        <input type="text" placeholder="Username" value={username} onChange={(e) => setUsername(e.target.value)} className="auth-input" />
        <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} className="auth-input" />
        {isSignup ? (
          <button className="auth-btn" onClick={handleSignup}>Signup</button>
        ) : (
          <button className="auth-btn" onClick={handleLogin}>Login</button>
        )}
        <button className="toggle-btn" onClick={() => { setIsSignup(!isSignup); setMessage(''); }}>
          {isSignup ? 'Already have an account? Login' : 'No account? Signup'}
        </button>
        {message && <p className="auth-message">{message}</p>}
      </div>
    );
  }

  return (
    <div className="container">
      <header>
        <h2 className="main-heading">
          {isAdmin ? 'üìÇ Admin OCR Upload Tool' : 'üìÑ File OCR Extraction Tool'}
        </h2>
      </header>

      <section className="upload-section">
        <label htmlFor="file-upload" className="upload-label">Upload up to 10 files:</label>
        <input type="file" id="file-upload" accept=".pdf,.jpg,.jpeg,.png,.doc,.docx" multiple onChange={handleFileChange} />
        <div className="button-row">
          <button className="upload-btn" onClick={handleUploadAll}>Upload All</button>
          {downloadReady && (
            <button className="download-btn" onClick={() => alert('Excel export handled on server.')}>
              ‚¨áÔ∏è Download Excel
            </button>
          )}
        </div>
      </section>

      {files.length > 0 && (
        <section className="file-preview">
          <h3 className="section-heading">üìÅ Selected Files</h3>
          <ul className="file-list">
            {files.map((file, i) => (<li key={i}>üìé {file.name}</li>))}
          </ul>
        </section>
      )}

      {loading && <p className="loading-text">‚è≥ Extracting table...</p>}

      {/* Only the table view (metadata removed) */}
      {tables.length > 0 && (
        <section className="results-section">
          <h3 className="section-heading">üìä Procedure Report</h3>
          {files.map((file, idx) => {
            // Preferred: backend-provided headers/rows
            let columns = tables[idx]?.columns || [];
            let rows = tables[idx]?.rows || [];
            let head = tables[idx]?.head || '';
            let note = tables[idx]?.note || '';

            // Fallback: infer headers from text if missing
            if (!columns || columns.length === 0) {
              columns = inferColumnsFromText(rawTexts[idx] || '');
            }

            // Fallback: parse rows from raw text if missing
            if ((!rows || rows.length === 0) && columns && columns.length) {
              const parsed = parsePlainTextToRows(rawTexts[idx] || '', columns);
              // Debug parsing result
              const firstLines = (rawTexts[idx] || '').split(/\r?\n/).slice(0, 10);
              console.log('First raw lines for', file.name, firstLines);
              console.log('Final inferred columns for', file.name, columns);
              console.log('Parsed rows sample for', file.name, parsed.slice(0, 3));
              rows = parsed;
            } else {
              console.log('Using backend rows for', file.name, {
                columns,
                rows_count: rows?.length || 0,
                sample: (rows || []).slice(0, 3)
              });
            }

            return (
              <ExcelGrid
                key={`grid-${idx}`}
                title={file.name}
                columns={columns}
                rows={rows}
                head={head || (rawTexts[idx] || '').split(/\r?\n/).slice(0, 3).join('\n')}
                note={note}
              />
            );
          })}
        </section>
      )}
    </div>
  );
};

export default OCRUpload;
