import { Link, Outlet, useLocation } from "react-router-dom";
import { LayoutDashboard, Users, List, Settings } from "lucide-react";

export default function AdminDashboard() {
  const location = useLocation();

  const navItems = [
    { path: "/admin/overview", label: "Overview", icon: <LayoutDashboard size={18} /> },
    { path: "/admin/users", label: "Users", icon: <Users size={18} /> },
    { path: "/admin/logs", label: "Logs", icon: <List size={18} /> },
    { path: "/admin/settings", label: "Settings", icon: <Settings size={18} /> },
  ];

  return (
    <div className="flex h-screen">
      {/* Sidebar */}
      <aside className="w-64 p-6 flex flex-col bg-white/10 backdrop-blur-xl border-r border-white/20">
        <div className="text-2xl font-bold text-white mb-10 tracking-wide">
          Admin Panel
        </div>
        <nav className="flex-1 space-y-4">
          {navItems.map((item) => (
            <Link
              key={item.path}
              to={item.path}
              className={`flex items-center gap-3 px-4 py-3 rounded-xl font-medium transition-all duration-300 ${
                location.pathname === item.path
                  ? "bg-white/20 text-white shadow-lg"
                  : "text-gray-300 hover:bg-white/10 hover:text-white"
              }`}
            >
              {item.icon}
              <span>{item.label}</span>
            </Link>
          ))}
        </nav>
      </aside>

      {/* Main content */}
      <main className="flex-1 overflow-y-auto p-8">
        <div className="dashboard-container unique-glass-card">
          <Outlet />
        </div>
      </main>
    </div>
  );
}
